import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";
import { Link } from "react-router-dom";

function NewTicketForm() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    sujet: "",
    departement: "",
    priorite: "",
    description: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    const newTicket = {
      ...formData,
      id: Date.now(),
      statut: "Ouvert",
      date: new Date().toLocaleDateString('fr-FR', {
        day: '2-digit',
        month: '2-digit',
        year: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      })
    };

    const existingTickets = JSON.parse(localStorage.getItem('tickets')) || [];
    localStorage.setItem('tickets', JSON.stringify([...existingTickets, newTicket]));
    
    navigate("/MyTickets");
  };

  return (
    <>
      <IndexNavbar />

      <div style={container}>
        <div style={card}>
          <div style={leftSide}>
            <h2 style={title}>🎫 Créer un ticket d’assistance</h2>
            <p style={subtitle}>Merci de remplir les informations nécessaires pour traiter votre demande.</p>

            <form style={form} onSubmit={handleSubmit}>
              <div style={inputGroup}>
                <label htmlFor="sujet" style={label}>Sujet</label>
                <input
                  type="text"
                  id="sujet"
                  name="sujet"
                  value={formData.sujet}
                  onChange={handleChange}
                  placeholder="Ex : Virement non effectué"
                  style={input}
                  required
                />
              </div>

              <div style={inputGroup}>
                <label htmlFor="departement" style={label}>Département</label>
                <select
                  id="departement"
                  name="departement"
                  value={formData.departement}
                  onChange={handleChange}
                  style={input}
                  required
                >
                  <option value="">-- Sélectionner --</option>
                  <option value="IT">IT - Informatique</option>
                  <option value="RH">RH - Ressources Humaines</option>
                  <option value="Comptabilité">Comptabilité</option>
                </select>
              </div>

              <div style={inputGroup}>
                <label htmlFor="priorite" style={label}>Priorité</label>
                <select
                  id="priorite"
                  name="priorite"
                  value={formData.priorite}
                  onChange={handleChange}
                  style={input}
                  required
                >
                  <option value="">-- Choisir --</option>
                  <option value="Urgent">Urgent</option>
                  <option value="Normal">Normal</option>
                  <option value="Faible">Faible priorité</option>
                </select>
              </div>

              <div style={inputGroup}>
                <label htmlFor="description" style={label}>Description</label>
                <textarea
                  id="description"
                  name="description"
                  rows="4"
                  value={formData.description}
                  onChange={handleChange}
                  placeholder="Décrivez le problème rencontré..."
                  style={textarea}
                  required
                ></textarea>
              </div>

              <div style={buttonGroup}>
                <Link to="/MyTickets" style={backLink}>← Retour</Link>
                <button type="submit" style={submitButton}>Soumettre</button>
              </div>
            </form>
          </div>

          <div style={rightSide}>
            <img
              src="https://images.unsplash.com/photo-1605902711622-cfb43c4437d4"
              alt="Assistance bancaire"
              style={imageStyle}
            />
          </div>
        </div>
      </div>

      <Footerr />
    </>
  );
}

// Styles (identique à votre version originale)
// ... (coller ici tous les styles de votre version originale)

export default NewTicketForm;
