import React from 'react';

const Dashboard = () => {
  const styles = {
    container: {
      width: '100%',
      height: '100vh', // La div garde 100% de la hauteur de la fenêtre
      display: 'flex',
      flexDirection: 'column',
      background: 'linear-gradient(to bottom right, #f0f4f8, #dbeafe)',
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      justifyContent: 'center', // Centrage vertical
      alignItems: 'center', // Centrage horizontal
      paddingTop: '10px',
    },
    dashboardContainer: {
      width: '90%', // Taille flexible mais large
      maxWidth: '1200px', // Limite la largeur maximum pour éviter qu'elle devienne trop large
      padding: '10px 20px',
      display: 'flex',
      flexDirection: 'column', // Organiser le titre et l'iframe en colonne
      alignItems: 'center', // Centrer horizontalement
    },
    iframe: {
      width: '100%',
      height: '80vh', // Garder une taille proportionnelle mais assez grande
      border: 'none',
      borderRadius: '12px',
      boxShadow: '0 8px 24px rgba(0, 0, 0, 0.15)',
      marginTop: '20px', // Espace entre le titre et l'iframe
    },
    title: {
      fontSize: '36px',
      fontWeight: 'bold',
      color: '#2d3748',
      marginBottom: '20px', // Espacement entre le titre et l'iframe
    }
  };

  const powerBiUrl = "https://app.powerbi.com/reportEmbed?reportId=f7a7df31-5087-43e4-aa18-2db86a75abde&autoAuth=true&ctid=dbd6664d-4eb9-46eb-99d8-5c43ba153c61";

  return (
    <div style={styles.container}>
      <div style={styles.dashboardContainer}>
        <h2 style={styles.title}>Dashboard</h2> {/* Sous-titre ajouté ici */}
        <iframe 
          title="Dashboard STB Tickets" 
          style={styles.iframe}
          src={powerBiUrl}
          allowFullScreen
        />
      </div>
    </div>
  );
};

export default Dashboard;
