import React, { useState, useEffect } from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";

function MyTickets() {
  const [tickets, setTickets] = useState([]);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [searchId, setSearchId] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const formatTicketId = (id) => {
    if (!id && id !== 0) return 'ID_INVALIDE';
    const strId = String(id);
    
    if (strId.startsWith('ERR_')) {
      return strId.replace('ERR_O_ID', 'ERREUR_ID').toUpperCase();
    }
    
    try {
      return strId.length > 8 
        ? `${strId.slice(0, 4)}${strId.slice(-4)}`
        : strId;
    } catch (e) {
      console.error("Erreur de formatage ID:", e);
      return strId;
    }
  };

  useEffect(() => {
    const controller = new AbortController();
    const { signal } = controller;

    document.body.style.margin = "0";
    document.body.style.minHeight = "100vh";
    document.body.style.display = "flex";
    document.body.style.flexDirection = "column";

    const fetchTickets = async () => {
      try {
        const response = await fetch('/api/tickets', { signal });
        
        if (!response.ok) {
          throw new Error(`Erreur ${response.status}: ${response.statusText}`);
        }
        
        const data = await response.json();
        
        const validatedTickets = data
          .map((ticket, index) => ({
            ...ticket,
            id: ticket.id?.toString() || `ERR_NO_ID_${index}_${Date.now()}`,
            date: ticket.date || new Date().toISOString(),
            statut: ticket.statut || "Inconnu"
          }))
          .filter(ticket => ticket.sujet);

        setTickets(validatedTickets);
        setError(null);

      } catch (err) {
        if (err.name !== 'AbortError') {
          setError(err.message);
          setTickets([]);
        }
      } finally {
        setIsLoading(false);
      }
    };

    fetchTickets();
    return () => controller.abort();
  }, []);

  const statusStyles = (statut) => ({
    padding: "0.3rem 0.8rem",
    borderRadius: "15px",
    fontSize: "0.85rem",
    backgroundColor: {
      "Ouvert": "#ffebee",
      "En cours": "#fff3e0",
      "Résolu": "#e8f5e9",
      "Inconnu": "#f5f5f5"
    }[statut],
    color: {
      "Ouvert": "#c62828",
      "En cours": "#ef6c00",
      "Résolu": "#2e7d32",
      "Inconnu": "#616161"
    }[statut],
    fontWeight: "600",
    minWidth: "85px",
    textAlign: "center"
  });

  const filteredTickets = tickets.filter(ticket =>
    String(ticket.id).toLowerCase().includes(searchId.toLowerCase())
  );

  return (
    <>
      <IndexNavbar />
      <div style={container}>
        <div style={contentWrapper}>
          <img 
            src="/images/support-banner.jpg" 
            alt="Bannière Support"
            style={bannerImage}
          />
          
          <h1 style={title}>Espace Support Technique</h1>
          <p style={subtitle}>Gestion des demandes clients</p>

          <div style={searchContainer}>
            <input
              type="text"
              placeholder="🔎 Rechercher par référence..."
              value={searchId}
              onChange={(e) => setSearchId(e.target.value)}
              style={searchInput}
              disabled={isLoading}
            />
          </div>

          {error && (
            <div style={errorStyle}>
              <div style={{ fontSize: "1.5rem" }}>⚠️</div>
              <div style={{ marginLeft: "1rem" }}>
                <h3 style={{ margin: "0 0 0.5rem 0" }}>Erreur de connexion</h3>
                <p style={{ margin: 0 }}>{error}</p>
              </div>
            </div>
          )}

          {isLoading ? (
            <div style={loadingStyle}>
              <div style={loaderStyle}></div>
              <p style={{ marginTop: "1rem" }}>Chargement en cours...</p>
            </div>
          ) : (
            <div style={contentArea}>
              <div style={ticketList}>
                {filteredTickets.map((ticket) => (
                  <div 
                    key={ticket.id}
                    style={ticketItem(ticket.id)}
                    onClick={() => setSelectedTicket(ticket)}
                  >
                    <div style={ticketHeader}>
                      <div style={idContainer}>
                        <span style={ticketIdStyle(ticket.id)}>
                          {ticket.id.startsWith('ERR_') ? (
                            <span style={{ display: "flex", alignItems: "center" }}>
                              <span style={{ marginRight: "0.5rem" }}>⚠️</span>
                              {ticket.id}
                            </span>
                          ) : (
                            `#${formatTicketId(ticket.id)}`
                          )}
                        </span>
                        <span style={statusStyles(ticket.statut)}>
                          {ticket.statut}
                        </span>
                      </div>
                      <h3 style={ticketTitle}>{ticket.sujet}</h3>
                      <p style={ticketDate}>
                        {new Date(ticket.date).toLocaleDateString('fr-FR', {
                          year: 'numeric',
                          month: 'long',
                          day: 'numeric'
                        })}
                      </p>
                    </div>
                  </div>
                ))}
                
                {!isLoading && filteredTickets.length === 0 && (
                  <div style={noResultsStyle}>
                    <p style={{ margin: 0 }}>
                      {searchId ? (
                        <>Aucun résultat pour "<strong>{searchId}</strong>"</>
                      ) : (
                        "Aucun ticket disponible"
                      )}
                    </p>
                  </div>
                )}
              </div>

              {selectedTicket && (
                <div style={detailsPanel}>
                  <div style={detailsHeader}>
                    <h2 style={detailsTitle}>
                      {selectedTicket.id.startsWith('ERR_') ? (
                        <span style={{ color: "#c62828", display: "flex", alignItems: "center" }}>
                          <span style={{ marginRight: "0.5rem" }}>⚠️</span>
                          Ticket non valide
                        </span>
                      ) : (
                        `Ticket #${formatTicketId(selectedTicket.id)}`
                      )}
                    </h2>
                    <button 
                      style={closeButton} 
                      onClick={() => setSelectedTicket(null)}
                      aria-label="Fermer"
                    >
                      &times;
                    </button>
                  </div>
                  
                  <div style={detailsContent}>
                    <div style={detailRow}>
                      <span style={detailLabel}>Référence :</span>
                      <span style={ticketIdStyle(selectedTicket.id)}>
                        {selectedTicket.id}
                      </span>
                    </div>
                    
                    <div style={detailRow}>
                      <span style={detailLabel}>Statut :</span>
                      <span style={statusStyles(selectedTicket.statut)}>
                        {selectedTicket.statut}
                      </span>
                    </div>
                    
                    <div style={detailRow}>
                      <span style={detailLabel}>Date :</span>
                      <span>
                        {new Date(selectedTicket.date).toLocaleDateString('fr-FR', {
                          weekday: 'long',
                          year: 'numeric',
                          month: 'long',
                          day: 'numeric',
                          hour: '2-digit',
                          minute: '2-digit'
                        })}
                      </span>
                    </div>
                    
                    <div style={detailDescription}>
                      <h4 style={{ margin: "0 0 1rem 0", color: "#1a237e" }}>Description</h4>
                      <p style={descriptionText}>
                        {selectedTicket.description || "Aucune description fournie"}
                      </p>
                    </div>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
      <Footerr />
    </>
  );
}

// Styles complets
const container = {
  flex: 1,
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  padding: "2rem",
  backgroundColor: "#f8f9fa",
  minHeight: "calc(100vh - 120px)"
};

const contentWrapper = {
  width: "100%",
  maxWidth: "1200px",
  margin: "0 auto"
};

const bannerImage = {
  width: "100%",
  height: "250px",
  borderRadius: "15px",
  objectFit: "cover",
  marginBottom: "2rem",
  boxShadow: "0 4px 15px rgba(0,0,0,0.1)"
};

const title = {
  color: "#1a237e",
  fontSize: "2.5rem",
  textAlign: "center",
  margin: "0 0 0.5rem 0",
  fontWeight: "600",
  letterSpacing: "-0.5px"
};

const subtitle = {
  textAlign: "center",
  color: "#616161",
  fontSize: "1.1rem",
  marginBottom: "2rem"
};

const searchContainer = {
  marginBottom: "2rem",
  textAlign: "center",
  position: "relative"
};

const searchInput = {
  padding: "0.8rem 1.5rem",
  width: "100%",
  maxWidth: "500px",
  borderRadius: "30px",
  border: "2px solid #e0e0e0",
  fontSize: "1rem",
  transition: "all 0.3s",
  outline: "none",
  ":focus": {
    borderColor: "#1a237e",
    boxShadow: "0 0 8px rgba(26, 35, 126, 0.2)"
  }
};

const errorStyle = {
  backgroundColor: "#fff",
  border: "2px solid #ffcdd2",
  color: "#c62828",
  padding: "1.5rem",
  borderRadius: "10px",
  margin: "2rem 0",
  display: "flex",
  alignItems: "center",
  boxShadow: "0 2px 8px rgba(198, 40, 40, 0.1)"
};

const loadingStyle = {
  textAlign: "center",
  padding: "3rem",
  color: "#1a237e"
};

const loaderStyle = {
  border: "4px solid #f3f3f3",
  borderTop: "4px solid #1a237e",
  borderRadius: "50%",
  width: "50px",
  height: "50px",
  animation: "spin 1s linear infinite",
  margin: "0 auto"
};

const contentArea = {
  display: "grid",
  gap: "2rem",
  gridTemplateColumns: "1fr",
  "@media (min-width: 992px)": {
    gridTemplateColumns: "1fr 1fr"
  }
};

const ticketList = {
  backgroundColor: "#fff",
  borderRadius: "12px",
  padding: "1rem",
  boxShadow: "0 2px 10px rgba(0,0,0,0.05)"
};

const ticketItem = (id) => ({
  backgroundColor: "#fff",
  borderRadius: "10px",
  padding: "1.5rem",
  marginBottom: "1rem",
  cursor: "pointer",
  border: "2px solid",
  borderColor: id.startsWith('ERR_') ? "#ffebee" : "#eee",
  transition: "all 0.2s",
  ":hover": {
    transform: "translateY(-2px)",
    boxShadow: id.startsWith('ERR_') 
      ? "0 4px 15px rgba(198, 40, 40, 0.1)" 
      : "0 4px 15px rgba(26, 35, 126, 0.1)"
  }
});

const ticketHeader = {
  display: "flex",
  flexDirection: "column",
  gap: "0.5rem"
};

const idContainer = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  flexWrap: "wrap",
  gap: "0.5rem"
};

const ticketIdStyle = (id) => ({
  color: id.startsWith('ERR_') ? "#c62828" : "#1a237e",
  fontSize: "0.9rem",
  fontWeight: "600",
  fontFamily: "'Courier New', monospace"
});

const ticketTitle = {
  fontSize: "1.2rem",
  color: "#212121",
  margin: "0.5rem 0",
  fontWeight: "500"
};

const ticketDate = {
  color: "#757575",
  fontSize: "0.85rem",
  margin: 0
};

const noResultsStyle = {
  textAlign: "center",
  color: "#616161",
  padding: "2rem",
  backgroundColor: "#fff",
  borderRadius: "10px",
  marginTop: "1rem"
};

const detailsPanel = {
  backgroundColor: "#fff",
  borderRadius: "12px",
  padding: "2rem",
  boxShadow: "0 4px 20px rgba(0,0,0,0.1)",
  position: "sticky",
  top: "1rem",
  maxHeight: "90vh",
  overflowY: "auto"
};

const detailsHeader = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  marginBottom: "2rem",
  borderBottom: "2px solid #eee",
  paddingBottom: "1rem"
};

const detailsTitle = {
  fontSize: "1.8rem",
  color: "#1a237e",
  margin: 0,
  fontWeight: "600"
};

const closeButton = {
  background: "none",
  border: "none",
  fontSize: "2rem",
  color: "#616161",
  cursor: "pointer",
  padding: "0 0.5rem",
  lineHeight: 1,
  ":hover": {
    color: "#1a237e"
  }
};

const detailsContent = {
  lineHeight: "1.6"
};

const detailRow = {
  display: "flex",
  justifyContent: "space-between",
  marginBottom: "1.5rem",
  paddingBottom: "1.5rem",
  borderBottom: "1px solid #eee"
};

const detailLabel = {
  color: "#616161",
  fontWeight: "500",
  minWidth: "120px"
};

const detailDescription = {
  marginTop: "2rem"
};

const descriptionText = {
  whiteSpace: "pre-wrap",
  lineHeight: "1.8",
  backgroundColor: "#f8f9fa",
  padding: "1.5rem",
  borderRadius: "8px",
  fontSize: "0.95rem"
};

export default MyTickets;

