import React from 'react';

// Composant du tableau des tickets
const TicketTable = ({ tickets }) => {
  return (
    <div className="ticket-table-container">
      <h2 className="table-title">Tableau des Tickets</h2>
      <table className="ticket-table">
        <thead>
          <tr>
            <th>ID Ticket</th>
            <th>Client</th>
            <th>Type de Demande</th>
            <th>Statut</th>
            <th>Date de Création</th>
            <th>Date d'Échéance</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {tickets.map(ticket => (
            <tr key={ticket.id}>
              <td>{ticket.id}</td>
              <td>{ticket.client}</td>
              <td>{ticket.type}</td>
              <td>{ticket.status}</td>
              <td>{ticket.creationDate}</td>
              <td>{ticket.dueDate}</td>
              <td>
                <button className="btn-action">Voir</button>
                <button className="btn-action">Modifier</button>
                <button className="btn-action">Fermer</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// Exemple de données des tickets
const tickets = [
  { id: 1, client: 'Client A', type: 'Retrait', status: 'En cours', creationDate: '2025-04-20', dueDate: '2025-04-22' },
  { id: 2, client: 'Client B', type: 'Dépôt', status: 'Terminé', creationDate: '2025-04-21', dueDate: '2025-04-23' },
  { id: 3, client: 'Client C', type: 'Consultation', status: 'En attente', creationDate: '2025-04-22', dueDate: '2025-04-24' },
];

const App = () => {
  return <TicketTable tickets={tickets} />;
};

export default App;

// CSS intégré dans le fichier App.js
const styles = `
  /* Conteneur du tableau */
  .ticket-table-container {
    padding: 20px;
    background-color: #fff;
    border-radius: 8px;
    max-width: 800px;
    margin: 0 auto;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  }

  /* Titre du tableau */
  .table-title {
    text-align: center;
    color: #333;
    font-size: 22px;
    margin-bottom: 20px;
  }

  /* Style du tableau */
  .ticket-table {
    width: 100%;
    border-collapse: collapse;
  }

  /* Style des en-têtes du tableau */
  .ticket-table th {
    background-color: #f4f4f4;
    color: #333;
    padding: 10px;
    text-align: left;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    font-weight: normal;
  }

  /* Style des cellules du tableau */
  .ticket-table td {
    padding: 10px;
    text-align: left;
    border-bottom: 1px solid #ddd;
  }

  /* Style des boutons d'action */
  button {
    padding: 6px 12px;
    font-size: 14px;
    cursor: pointer;
    border: 1px solid #ddd;
    border-radius: 4px;
    background-color: #f1f1f1;
    margin-right: 8px;
  }

  /* Boutons d'action : Simples et sans couleur vive */
  .btn-action {
    background-color: #f1f1f1;
    color: #333;
  }

  .btn-action:hover {
    background-color: #e0e0e0;
  }

  /* Style pour la dernière cellule du tableau contenant les boutons */
  .ticket-table td button {
    margin: 5px;
  }
`;

// Création d'une balise style pour insérer le CSS dans la page
const styleSheet = document.createElement("style");
styleSheet.type = "text/css";
styleSheet.innerText = styles;
document.head.appendChild(styleSheet);
