import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { Search, Edit, Trash, Plus, X, Check, Filter } from "lucide-react";

// CSS encapsul√© avec pr√©fixe "gp-"
const encapsulatedStyles = `
  /* ... Ajoute ici ton CSS encapsul√© gp- ... */
`;

const Toast = ({ message, type, onClose }) => {
  useEffect(() => {
    const timer = setTimeout(onClose, 3000);
    return () => clearTimeout(timer);
  }, [onClose]);

  return (
    <div className={`gp-toast gp-toast-${type}`}>
      {message}
    </div>
  );
};

export default function CardDepartment() {
  const [searchTerm, setSearchTerm] = useState("");
  const [sortOption, setSortOption] = useState("");
  const [departments, setDepartments] = useState([
    {
      _id: "1a2b3c4d",
      nom: "Ressources Humaines",
      exp: 4
    },
    {
      _id: "5e6f7g8h",
      nom: "Informatique",
      exp: 7
    },
    {
      _id: "9i0j1k2l",
      nom: "Marketing",
      exp: 2
    }
  ]);
  const [modalOuvert, setModalOuvert] = useState(false);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [selectedDepartmentId, setSelectedDepartmentId] = useState(null);
  const [selectedDepartmentName, setSelectedDepartmentName] = useState("");
  const [newDepartment, setNewDepartment] = useState({
    nom: "",
    exp: 0
  });
  const [selectedDepartment, setSelectedDepartment] = useState(null);
  const [errors, setErrors] = useState({});
  const [toast, setToast] = useState({ affiche: false, message: "", type: "" });

  const filteredDepartments = departments
    .filter((department) => {
      const searchLower = searchTerm.toLowerCase();
      return (
        department._id.toLowerCase().includes(searchLower) ||
        department.nom.toLowerCase().includes(searchLower) ||
        department.exp.toString().includes(searchTerm)
      );
    })
    .sort((a, b) => {
      if (!sortOption) return 0;
      if (sortOption === "id-asc") return a._id.localeCompare(b._id);
      if (sortOption === "id-desc") return b._id.localeCompare(a._id);
      if (sortOption === "departement") return a.nom.localeCompare(b.nom);
      if (sortOption === "exp-asc") return a.exp - b.exp;
      if (sortOption === "exp-desc") return b.exp - a.exp;
      return 0;
    });

  const afficherToast = (message, type) => {
    setToast({ affiche: true, message, type });
  };

  const fermerToast = () => {
    setToast({ ...toast, affiche: false });
  };

  const handleAction = (action, id, nom) => {
    switch (action) {
      case "modifier":
        const departmentToEdit = departments.find(
          (department) => department._id === id
        );
        setSelectedDepartment(departmentToEdit);
        setIsEditModalOpen(true);
        break;
      case "supprimer":
        setSelectedDepartmentId(id);
        setSelectedDepartmentName(nom);
        setModalOuvert(true);
        break;
      default:
        break;
    }
  };

  const handleConfirmDelete = () => {
    setDepartments(
      departments.filter(
        (department) => department._id !== selectedDepartmentId
      )
    );
    setModalOuvert(false);
    afficherToast("D√©partement supprim√© avec succ√®s", "success");
  };

  const validateForm = (department) => {
    const newErrors = {};
    if (!department.nom) newErrors.nom = "Nom obligatoire";
    if (department.exp < 0 || department.exp > 20) newErrors.exp = "Exp doit √™tre entre 0-20";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (isEditModalOpen && selectedDepartment) {
      setSelectedDepartment({
        ...selectedDepartment,
        [name]: value,
      });
    } else {
      setNewDepartment({
        ...newDepartment,
        [name]: value,
      });
    }
  };

  const handleModalSubmit = () => {
    if (!validateForm(newDepartment)) return;

    const newDept = {
      ...newDepartment,
      _id: Math.random().toString(36).substr(2, 8),
      exp: parseInt(newDepartment.exp)
    };

    setDepartments([...departments, newDept]);
    setModalOuvert(false);
    afficherToast("D√©partement ajout√©", "success");
  };

  const handleEditSubmit = () => {
    if (!validateForm(selectedDepartment)) return;

    const updatedDepartments = departments.map((department) =>
      department._id === selectedDepartment._id
        ? { ...selectedDepartment, exp: parseInt(selectedDepartment.exp) }
        : department
    );

    setDepartments(updatedDepartments);
    setIsEditModalOpen(false);
    afficherToast("D√©partement modifi√©", "success");
  };

  return (
    <>
      <style>{encapsulatedStyles}</style>
      <div className="gp-department-container">
        <table className="gp-table">
          <thead>
            <tr>
              <th className="gp-th">ID</th>
              <th className="gp-th">D√©partement</th>
              <th className="gp-th">Exp (ann√©es)</th>
              <th className="gp-th" style={{ textAlign: "center" }}>
                Actions
              </th>
            </tr>
          </thead>
          <tbody>
            {filteredDepartments.map((department) => (
              <tr className="gp-tr" key={department._id}>
                <td className="gp-td">{department._id}</td>
                <td className="gp-td">
                  <span className="gp-department-badge">{department.nom}</span>
                </td>
                <td className="gp-td">{department.exp}</td>
                <td className="gp-td" style={{ textAlign: "center" }}>
                  <button onClick={() => handleAction("modifier", department._id)}>‚úèÔ∏è</button>
                  <button onClick={() => handleAction("supprimer", department._id, department.nom)}>üóëÔ∏è</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {modalOuvert && (
          <div className="gp-modal-overlay">
            <div className="gp-modal-container">
              <h3>Ajouter D√©partement</h3>
              <div className="gp-form-group">
                <label className="gp-form-label">Nom</label>
                <input
                  type="text"
                  name="nom"
                  value={newDepartment.nom}
                  onChange={handleChange}
                  className="gp-form-input"
                />
                {errors.nom && <p className="error">{errors.nom}</p>}
              </div>
              <div className="gp-form-group">
                <label className="gp-form-label">Exp√©rience (ann√©es)</label>
                <input
                  type="number"
                  name="exp"
                  value={newDepartment.exp}
                  onChange={handleChange}
                  className="gp-form-input"
                  min="0"
                  max="20"
                />
                {errors.exp && <p className="error">{errors.exp}</p>}
              </div>
              <button onClick={handleModalSubmit}>Ajouter</button>
              <button onClick={() => setModalOuvert(false)}>Annuler</button>
            </div>
          </div>
        )}

        {isEditModalOpen && selectedDepartment && (
          <div className="gp-modal-overlay">
            <div className="gp-modal-container">
              <h3>Modifier D√©partement</h3>
              <div className="gp-form-group">
                <label className="gp-form-label">Nom</label>
                <input
                  type="text"
                  name="nom"
                  value={selectedDepartment.nom}
                  onChange={handleChange}
                  className="gp-form-input"
                />
                {errors.nom && <p className="error">{errors.nom}</p>}
              </div>
              <div className="gp-form-group">
                <label className="gp-form-label">Exp√©rience (ann√©es)</label>
                <input
                  type="number"
                  name="exp"
                  value={selectedDepartment.exp}
                  onChange={handleChange}
                  className="gp-form-input"
                  min="0"
                  max="20"
                />
                {errors.exp && <p className="error">{errors.exp}</p>}
              </div>
              <button onClick={handleEditSubmit}>Modifier</button>
              <button onClick={() => setIsEditModalOpen(false)}>Annuler</button>
            </div>
          </div>
        )}

        {toast.affiche && (
          <Toast message={toast.message} type={toast.type} onClose={fermerToast} />
        )}
      </div>
    </>
  );
}

CardDepartment.propTypes = {
  color: PropTypes.string,
};
