import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faPen } from "@fortawesome/free-solid-svg-icons";
import { getAllUsers, updateUser, deleteUser } from "../services/ApiUser"; // Chemin corrigé

export default function CardTable({ color }) {
  const [users, setUsers] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newUser, setNewUser] = useState({
    nom: "",
    prenom: "",
    email: "",
    tel: "",
    password: "",
    role: "user",
  });

  const [isEditing, setIsEditing] = useState(false);
  const [editId, setEditId] = useState(null);

  const getUsers = async () => {
    try {
      const res = await getAllUsers();
      if (res.data) setUsers(res.data);
    } catch (error) {
      console.error("Erreur de chargement:", error);
    }
  };

  useEffect(() => {
    getUsers();
  }, []);

  const handleInputChange = (e) => {
    setNewUser({ ...newUser, [e.target.name]: e.target.value });
  };

  const handleUpdateUser = async (e) => {
    e.preventDefault();
    try {
      await updateUser(editId, newUser);
      getUsers();
      resetForm();
    } catch (error) {
      console.error("Échec de la mise à jour:", error);
    }
  };

  const handleDeleteUser = async (userId) => {
    const confirmDelete = window.confirm("Êtes-vous sûr de vouloir supprimer cet utilisateur ?");
    if (confirmDelete) {
      try {
        await deleteUser(userId);
        getUsers();
      } catch (error) {
        console.error("Échec de la suppression:", error);
      }
    }
  };

  const handleEditUser = (user) => {
    setNewUser({
      nom: user.nom,
      prenom: user.prenom,
      email: user.email,
      tel: user.tel,
      role: user.role,
      password: "",
    });
    setEditId(user._id);
    setIsEditing(true);
    setShowForm(true);
  };

  const resetForm = () => {
    setNewUser({
      nom: "",
      prenom: "",
      email: "",
      tel: "",
      password: "",
      role: "user",
    });
    setEditId(null);
    setIsEditing(false);
    setShowForm(false);
  };

  return (
    <div className={`relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg 
      ${color === "light" ? "bg-white" : "bg-lightBlue-900 text-white"}`}
    >
      <div className="flex justify-between items-center px-6 py-4 border-b border-gray-200">
        <h3 className={`font-bold text-xl ${color === "light" ? "text-blueGray-700" : "text-white"}`}>
          Liste des utilisateurs
        </h3>
      </div>

      <div className="overflow-x-auto p-4">
        <table className="w-full text-sm text-left text-white">
          <thead className="text-xs uppercase bg-lightBlue-800 text-blueGray-100">
            <tr>
              <th className="px-6 py-3">Nom</th>
              <th className="px-6 py-3">Prénom</th>
              <th className="px-6 py-3">Email</th>
              <th className="px-6 py-3">Téléphone</th>
              <th className="px-6 py-3">Rôle</th>
              <th className="px-6 py-3 text-right">Actions</th>
            </tr>
          </thead>
          <tbody className="bg-lightBlue-900 divide-y divide-blueGray-700">
            {users.map((user) => (
              <tr key={user._id} className="hover:bg-lightBlue-800">
                <td className="px-6 py-4">{user.nom}</td>
                <td className="px-6 py-4">{user.prenom}</td>
                <td className="px-6 py-4">{user.email}</td>
                <td className="px-6 py-4">{user.tel}</td>
                <td className="px-6 py-4 capitalize">{user.role}</td>
                <td className="px-6 py-4 text-right">
                  <button
                    onClick={() => handleDeleteUser(user._id)}
                    className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded mr-2 text-xs shadow"
                  >
                    <FontAwesomeIcon icon={faTrash} />
                  </button>
                  <button
                    onClick={() => handleEditUser(user)}
                    className="bg-lightBlue-500 hover:bg-lightBlue-600 text-white px-3 py-1 rounded text-xs shadow"
                  >
                    <FontAwesomeIcon icon={faPen} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {showForm && (
          <form onSubmit={handleUpdateUser} className="mt-6 bg-lightBlue-900 text-white p-6 rounded shadow-md">
            <h4 className="text-lg font-semibold mb-4">
              {isEditing ? "Modifier l'utilisateur" : "Nouvel utilisateur"}
            </h4>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mb-4">
              {/* Champs du formulaire */}
              <div>
                <label className="block text-sm mb-1">Nom</label>
                <input
                  type="text"
                  name="nom"
                  value={newUser.nom}
                  onChange={handleInputChange}
                  className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 rounded"
                  required
                />
              </div>
              
              {/* ... (autres champs similaires) ... */}
              
            </div>

            <div className="flex justify-end gap-2">
              <button
                type="button"
                onClick={resetForm}
                className="bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded"
              >
                Annuler
              </button>
              <button
                type="submit"
                className="bg-emerald-500 hover:bg-emerald-600 text-white px-4 py-2 rounded"
              >
                {isEditing ? "Mettre à jour" : "Créer"}
              </button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
}

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
