import React, { useState } from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";
import { Link } from "react-router-dom";

// DÃ©claration des styles avant le composant
const container = {
  backgroundColor: "#f1f3f6",
  minHeight: "100vh",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  padding: "2rem",
};

const card = {
  display: "flex",
  flexDirection: "row",
  backgroundColor: "#fff",
  borderRadius: "16px",
  boxShadow: "0 8px 24px rgba(0, 0, 0, 0.07)",
  overflow: "hidden",
  maxWidth: "1080px",
  width: "100%",
};

const leftSide = {
  flex: 1,
  padding: "2.5rem",
};

const rightSide = {
  flex: 1,
  backgroundColor: "#e3e9f1",
};

const imageStyle = {
  width: "100%",
  height: "100%",
  objectFit: "cover",
};

const title = {
  fontSize: "1.8rem",
  fontWeight: "700",
  color: "#003366",
  marginBottom: "1rem",
};

const subtitle = {
  fontSize: "1rem",
  color: "#555",
  marginBottom: "2rem",
};

const form = {
  display: "flex",
  flexDirection: "column",
  gap: "1.2rem",
};

const inputGroup = {
  display: "flex",
  flexDirection: "column",
};

const label = {
  fontWeight: "600",
  marginBottom: "0.5rem",
  color: "#003366",
};

const input = {
  padding: "0.8rem",
  fontSize: "1rem",
  borderRadius: "8px",
  border: "1px solid #ccc",
  backgroundColor: "#f9f9f9",
};

const textarea = {
  ...input,
  resize: "vertical",
  minHeight: "100px",
};

const buttonGroup = {
  marginTop: "1.5rem",
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
};

const submitButton = {
  backgroundColor: "#003366",
  color: "#fff",
  border: "none",
  padding: "0.8rem 1.6rem",
  fontSize: "1rem",
  borderRadius: "8px",
  cursor: "pointer",
  fontWeight: "600",
};

const backLink = {
  color: "#003366",
  textDecoration: "none",
  fontWeight: "500",
};

const errorStyle = {
  color: "#dc3545",
  backgroundColor: "#f8d7da",
  padding: "0.75rem 1.25rem",
  borderRadius: "0.25rem",
  marginBottom: "1rem",
  border: "1px solid #f5c6cb",
};

function NewTicketForm() {
  const [formData, setFormData] = useState({
    sujet: "",
    type: "",
    urgence: "",
    description: "",
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError(null);

    try {
      const response = await fetch("http://localhost:3000/api/tickets", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Ã‰chec de la crÃ©ation du ticket");
      }

      window.location.href = "/MyTickets";
    } catch (error) {
      console.error("Erreur:", error);
      setError(error.message);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <>
      <IndexNavbar />
      <div style={container}>
        <div style={card}>
          <div style={leftSide}>
            <h2 style={title}>ðŸŽ« CrÃ©er un ticket d'assistance</h2>
            <p style={subtitle}>
              Merci de remplir les informations nÃ©cessaires pour traiter votre
              demande.
            </p>

            {error && <div style={errorStyle}>{error}</div>}

            <form style={form} onSubmit={handleSubmit}>
              <div style={inputGroup}>
                <label htmlFor="sujet" style={label}>
                  Sujet
                </label>
                <input
                  type="text"
                  id="sujet"
                  name="sujet"
                  value={formData.sujet}
                  onChange={handleChange}
                  placeholder="Ex : Virement non effectuÃ©"
                  style={input}
                  required
                />
              </div>

              {/* Les autres Ã©lÃ©ments du formulaire restent identiques */}
              {/* ... */}

            </form>
          </div>

          <div style={rightSide}>
            <img
              src="https://images.unsplash.com/photo-1605902711622-cfb43c4437d4?auto=format&fit=crop&w=1000&q=80"
              alt="Assistance bancaire"
              style={imageStyle}
            />
          </div>
        </div>
      </div>
      <Footerr />
    </>
  );
}

export default NewTicketForm;
