import React, { useEffect, useState } from "react";
import axios from 'axios';
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faPen } from "@fortawesome/free-solid-svg-icons";

export default function CardTable({ color }) {
  const [users, setUsers] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newUser, setNewUser] = useState({
    nom: "",
    prenom: "",
    email: "",
    _id: "",
    role: "",
    tel: "",
    password: ""
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editIndex, setEditIndex] = useState(null);

  const getUsers = async () => {
    try {
      const res = await axios.get("/allusers");
      if (res.data) {
        setUsers(res.data);
      }
    } catch (error) {
      console.error("Erreur lors de la récupération des utilisateurs :", error);
    }
  };

  useEffect(() => {
    getUsers();
  }, []);

  const handleInputChange = (e) => {
    setNewUser({ ...newUser, [e.target.name]: e.target.value });
  };

  const handleSubmitUser = async (e) => {
    e.preventDefault();

    try {
      if (isEditing) {
        const res = await axios.put(`/updateuser/${newUser._id}`, newUser);
        const updatedUsers = [...users];
        updatedUsers[editIndex] = res.data;
        setUsers(updatedUsers);
      } else {
        const res = await axios.post("/adduser", newUser);
        setUsers([...users, res.data]);
      }
      setShowForm(false);
      setIsEditing(false);
      setNewUser({
        nom: "",
        prenom: "",
        email: "",
        _id: "",
        role: "",
        tel: "",
        password: ""
      });
    } catch (error) {
      console.error("Erreur :", error);
      alert(`Échec de ${isEditing ? "la modification" : "l'ajout"}`);
    }
  };

  const handleDeleteUser = async (index) => {
    if (window.confirm("Êtes-vous sûr ?")) {
      try {
        await axios.delete(`/deleteuser/${users[index]._id}`);
        const updatedUsers = users.filter((_, i) => i !== index);
        setUsers(updatedUsers);
      } catch (error) {
        console.error("Erreur suppression :", error);
      }
    }
  };

  const handleEditUser = (index) => {
    setNewUser(users[index]);
    setIsEditing(true);
    setEditIndex(index);
    setShowForm(true);
  };

  return (
    <div className={`relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg 
      ${color === "light" ? "bg-white" : "bg-lightBlue-900 text-white"}`}>

      <div className="flex justify-between items-center px-6 py-4 border-b border-gray-200">
        <h3 className={`font-bold text-xl ${color === "light" ? "text-blueGray-700" : "text-white"}`}>
          Liste des utilisateurs
        </h3>
        
        {/* Bouton Ajouter - Vert Émeraude */}
        <button
          onClick={() => {
            setShowForm(true);
            setIsEditing(false);
            setNewUser({
              nom: "",
              prenom: "",
              email: "",
              _id: "",
              role: "",
              tel: "",
              password: ""
            });
          }}
          className="bg-emerald-500 hover:bg-emerald-600 text-white font-bold py-2 px-4 rounded-lg transition-colors"
        >
          + Ajouter utilisateur
        </button>
      </div>

      <div className="overflow-x-auto p-4">
        <table className="w-full text-sm text-left">
          <thead className="text-xs uppercase bg-lightBlue-800 text-white">
            <tr>
              <th className="px-6 py-3">Nom</th>
              <th className="px-6 py-3">Prénom</th>
              <th className="px-6 py-3">Email</th>
              <th className="px-6 py-3">Rôle</th>
              <th className="px-6 py-3">Téléphone</th>
              <th className="px-6 py-3 text-right">Actions</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {users.map((user, index) => (
              <tr key={user._id} className="hover:bg-lightBlue-50">
                <td className="px-6 py-4">{user.nom}</td>
                <td className="px-6 py-4">{user.prenom}</td>
                <td className="px-6 py-4">{user.email}</td>
                <td className="px-6 py-4">{user.role}</td>
                <td className="px-6 py-4">{user.tel}</td>
                <td className="px-6 py-4 text-right space-x-2">
                  <button
                    onClick={() => handleEditUser(index)}
                    className="text-blue-500 hover:text-blue-700"
                  >
                    <FontAwesomeIcon icon={faPen} />
                  </button>
                  <button
                    onClick={() => handleDeleteUser(index)}
                    className="text-red-500 hover:text-red-700"
                  >
                    <FontAwesomeIcon icon={faTrash} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Formulaire */}
        {showForm && (
          <div className="mt-8 p-6 bg-gray-100 rounded-lg">
            <h2 className="text-2xl font-bold mb-6">
              {isEditing ? "Modifier utilisateur" : "Nouvel utilisateur"}
            </h2>
            
            <form onSubmit={handleSubmitUser} className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {isEditing && (
                <div className="hidden">
                  <label>ID</label>
                  <input 
                    type="text" 
                    name="_id" 
                    value={newUser._id}
                    readOnly 
                    className="bg-gray-200"
                  />
                </div>
              )}

              <div>
                <label>Nom</label>
                <input
                  type="text"
                  name="nom"
                  value={newUser.nom}
                  onChange={handleInputChange}
                  required
                  className="w-full p-2 border rounded"
                />
              </div>

              <div>
                <label>Prénom</label>
                <input
                  type="text"
                  name="prenom"
                  value={newUser.prenom}
                  onChange={handleInputChange}
                  required
                  className="w-full p-2 border rounded"
                />
              </div>

              <div>
                <label>Email</label>
                <input
                  type="email"
                  name="email"
                  value={newUser.email}
                  onChange={handleInputChange}
                  required
                  className="w-full p-2 border rounded"
                />
              </div>

              <div>
                <label>Téléphone</label>
                <input
                  type="tel"
                  name="tel"
                  value={newUser.tel}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                />
              </div>

              <div>
                <label>Rôle</label>
                <select
                  name="role"
                  value={newUser.role}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                  required
                >
                  <option value="">Sélectionner...</option>
                  <option value="admin">Admin</option>
                  <option value="user">Utilisateur</option>
                </select>
              </div>

              {isEditing && (
                <div>
                  <label>Nouveau mot de passe</label>
                  <input
                    type="password"
                    name="password"
                    value={newUser.password}
                    onChange={handleInputChange}
                    placeholder="Laisser vide pour ne pas modifier"
                    className="w-full p-2 border rounded"
                  />
                </div>
              )}

              <div className="md:col-span-2 flex justify-end space-x-4 mt-4">
                <button
                  type="button"
                  onClick={() => setShowForm(false)}
                  className="bg-gray-500 text-white px-4 py-2 rounded"
                >
                  Annuler
                </button>
                <button
                  type="submit"
                  className="bg-emerald-500 hover:bg-emerald-600 text-white px-6 py-2 rounded"
                >
                  {isEditing ? "Mettre à jour" : "Créer"}
                </button>
              </div>
            </form>
          </div>
        )}
      </div>
    </div>
  );
}

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
