import React, { useState } from "react";
import PropTypes from "prop-types";
import { FaFilter, FaEdit, FaTrash } from "react-icons/fa";
import { X } from "lucide-react";

export default function CardTable({ color }) {
  const [items, setItems] = useState([]);
  const [filterRole, setFilterRole] = useState("");
  const [filterId, setFilterId] = useState("");

  const [modalOuvert, setModalOuvert] = useState(false);
  const [newUser, setNewUser] = useState({
    nom: "",
    prenom: "",
    email: "",
    telephone: "",
    role: "",
    password: "",
  });
  const [errors, setErrors] = useState({});

  const [editModalOuvert, setEditModalOuvert] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [editErrors, setEditErrors] = useState({});

  const [deleteModalOuvert, setDeleteModalOuvert] = useState(false);
  const [userIdToDelete, setUserIdToDelete] = useState(null);

  // Fonctions de gestion des changements
  const handleChange = (e) => {
    setNewUser({ ...newUser, [e.target.name]: e.target.value });
  };

  const handleEditChange = (e) => {
    setEditingUser({ ...editingUser, [e.target.name]: e.target.value });
  };

  // Validation des formulaires
  const validateForm = (user) => {
    const errors = {};
    if (!user.nom.trim()) errors.nom = "Le nom est requis.";
    if (!user.prenom.trim()) errors.prenom = "Le prénom est requis.";
    if (!user.email.trim()) errors.email = "L'email est requis.";
    if (!user.telephone.trim()) errors.telephone = "Le téléphone est requis.";
    if (!user.password.trim()) errors.password = "Le mot de passe est requis.";
    if (!user.role) errors.role = "Le rôle est requis.";
    return errors;
  };

  const validateEditForm = (user) => {
    const errors = {};
    if (!user.nom.trim()) errors.nom = "Le nom est requis.";
    if (!user.prenom.trim()) errors.prenom = "Le prénom est requis.";
    if (!user.email.trim()) errors.email = "L'email est requis.";
    if (!user.telephone.trim()) errors.telephone = "Le téléphone est requis.";
    if (!user.role) errors.role = "Le rôle est requis.";
    return errors;
  };

  // Gestion des soumissions
  const handleCreateSubmit = () => {
    const errors = validateForm(newUser);
    if (Object.keys(errors).length > 0) {
      setErrors(errors);
    } else {
      const newItem = { ...newUser, id: items.length + 1 };
      setItems([...items, newItem]);
      setModalOuvert(false);
      setNewUser({ nom: "", prenom: "", email: "", telephone: "", role: "", password: "" });
    }
  };

  const handleEditSubmit = () => {
    const errors = validateEditForm(editingUser);
    if (Object.keys(errors).length > 0) {
      setEditErrors(errors);
    } else {
      setItems(items.map(item => item.id === editingUser.id ? editingUser : item));
      setEditModalOuvert(false);
    }
  };

  // Style des rôles
  const roleStyle = (role) => ({
    padding: "0.25rem 0.5rem",
    borderRadius: "9999px",
    fontSize: "0.75rem",
    fontWeight: "500",
    backgroundColor:
      role === "admin" ? "#dcfce7" :
      role === "manager" ? "#ffedd5" :
      role === "technicien" ? "#fee2e2" : "#f3f4f6",
    color:
      role === "admin" ? "#16a34a" :
      role === "manager" ? "#ea580c" :
      role === "technicien" ? "#dc2626" : "#374151",
  });

  return (
    <div className={`relative mx-auto max-w-screen-xl flex flex-col min-w-0 rounded-lg shadow-lg mb-10 ${
      color === "light" ? "bg-white" : "bg-slate-800 text-white"
    }`}>
      
      {/* Entête et filtres */}
      <div className="px-6 pt-6 border-b-2 border-gray-300">
        <h1 className={`text-2xl font-bold text-center ${color === "light" ? "text-gray-800" : "text-white"}`}>
          Liste des utilisateurs
        </h1>
      </div>

      <div className="flex justify-between px-6 pt-6 pb-4 items-center gap-4">
        <input
          type="text"
          placeholder="🔍 Rechercher par ID..."
          value={filterId}
          onChange={(e) => setFilterId(e.target.value)}
          className="w-full sm:w-64 px-4 py-2 border rounded-lg shadow-sm text-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500 mr-4"
        />

        <div className="flex gap-4 items-center">
          <FaFilter className="text-gray-700 text-xl mr-2" />
          <select
            value={filterRole}
            onChange={(e) => setFilterRole(e.target.value)}
            className="w-50 sm:w-64 border rounded-lg shadow-sm text-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500 mr-4"
          >
            <option value="">Tous les rôles</option>
            <option value="admin">Admin</option>
            <option value="utilisateur">Utilisateur</option>
            <option value="manager">Manager</option>
            <option value="technicien">Technicien</option>
          </select>

          <button 
            className="gp-add-button" 
            onClick={() => setModalOuvert(true)}
          >
            <span>+</span>
            <span>Ajouter</span>
          </button>
        </div>
      </div>

      {/* Tableau principal */}
      <div className="overflow-x-auto px-6 pt-4 pb-14">
        <table className="w-full border-collapse">
          <thead>
            <tr className={`text-left ${color === "light" ? "bg-gray-50 text-gray-500" : "bg-slate-700 text-slate-200"}`}>
              <th className="px-6 py-4 font-medium">ID</th>
              <th className="px-6 py-4 font-medium">Nom</th>
              <th className="px-6 py-4 font-medium">Prénom</th>
              <th className="px-6 py-4 font-medium">Email</th>
              <th className="px-6 py-4 font-medium">Téléphone</th>
              <th className="px-6 py-4 font-medium">Rôle</th>
              <th className="px-6 py-4 font-medium">Actions</th>
            </tr>
          </thead>
          <tbody>
            {items.filter(item => {
              const matchesId = filterId ? item.id.toString().includes(filterId) : true;
              const matchesRole = filterRole ? item.role === filterRole : true;
              return matchesId && matchesRole;
            }).map((item) => (
              <tr 
                key={item.id} 
                className={`border-t ${color === "light" ? "hover:bg-gray-50" : "hover:bg-slate-700"} transition-colors`}
              >
                <td className="px-6 py-4">{item.id}</td>
                <td className="px-6 py-4">{item.nom}</td>
                <td className="px-6 py-4">{item.prenom}</td>
                <td className="px-6 py-4">{item.email}</td>
                <td className="px-6 py-4">{item.telephone}</td>
                <td className="px-6 py-4">
                  <span style={roleStyle(item.role)}>
                    {item.role}
                  </span>
                </td>
                <td className="px-6 py-4">
                  <div className="flex">
                    <button
                      onClick={() => {
                        setEditingUser(item);
                        setEditModalOuvert(true);
                      }}
                      className="gp-action-icon gp-edit mr-2"
                    >
                      <FaEdit size={16} />
                    </button>
                    <button
                      onClick={() => {
                        setUserIdToDelete(item.id);
                        setDeleteModalOuvert(true);
                      }}
                      className="gp-action-icon gp-delete"
                    >
                      <FaTrash size={16} />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modales */}
      {modalOuvert && (
        <div className="gp-modal-overlay">
          <div className="gp-modal-container">
            <div className="gp-modal-header">
              <h2 className="text-xl font-bold">Créer un utilisateur</h2>
              <button onClick={() => setModalOuvert(false)}><X size={24} /></button>
            </div>
            <div>
              {/* Formulaire simplifié */}
              {['nom', 'prenom', 'email', 'telephone', 'password', 'role'].map((field) => (
                <div key={field} className="gp-form-group">
                  <label className="block font-semibold mb-1">{{
                    nom: 'Nom',
                    prenom: 'Prénom',
                    email: 'Email',
                    telephone: 'Téléphone',
                    password: 'Mot de passe',
                    role: 'Rôle'
                  }[field]}</label>
                  {field === 'role' ? (
                    <select
                      name={field}
                      value={newUser[field]}
                      onChange={handleChange}
                      className="gp-form-input"
                    >
                      <option value="">-- Sélectionner --</option>
                      {['admin', 'utilisateur', 'manager', 'technicien'].map(option => (
                        <option key={option} value={option}>{option}</option>
                      ))}
                    </select>
                  ) : (
                    <input
                      type={field === 'password' ? 'password' : 'text'}
                      name={field}
                      value={newUser[field]}
                      onChange={handleChange}
                      className="gp-form-input"
                    />
                  )}
                  {errors[field] && <p className="text-red-500 text-sm">{errors[field]}</p>}
                </div>
              ))}
              <div className="flex justify-end mt-4 gap-2">
                <button onClick={() => setModalOuvert(false)} className="gp-btn gp-btn-cancel">Annuler</button>
                <button onClick={handleCreateSubmit} className="gp-btn gp-btn-save">Ajouter</button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Modale d'édition simplifiée */}
      {editModalOuvert && editingUser && (
        <div className="gp-modal-overlay">
          <div className="gp-modal-container">
            <div className="gp-modal-header">
              <h2 className="text-xl font-bold">Modifier l'utilisateur</h2>
              <button onClick={() => setEditModalOuvert(false)}><X size={24} /></button>
            </div>
            <div>
              {['nom', 'prenom', 'email', 'telephone', 'role'].map((field) => (
                <div key={field} className="gp-form-group">
                  <label className="block font-semibold mb-1">{{
                    nom: 'Nom',
                    prenom: 'Prénom',
                    email: 'Email',
                    telephone: 'Téléphone',
                    role: 'Rôle'
                  }[field]}</label>
                  {field === 'role' ? (
                    <select
                      name={field}
                      value={editingUser[field]}
                      onChange={handleEditChange}
                      className="gp-form-input"
                    >
                      {['admin', 'utilisateur', 'manager', 'technicien'].map(option => (
                        <option key={option} value={option}>{option}</option>
                      ))}
                    </select>
                  ) : (
                    <input
                      type="text"
                      name={field}
                      value={editingUser[field]}
                      onChange={handleEditChange}
                      className="gp-form-input"
                    />
                  )}
                  {editErrors[field] && <p className="text-red-500 text-sm">{editErrors[field]}</p>}
                </div>
              ))}
              <div className="flex justify-end mt-4 gap-2">
                <button onClick={() => setEditModalOuvert(false)} className="gp-btn gp-btn-cancel">Annuler</button>
                <button onClick={handleEditSubmit} className="gp-btn gp-btn-save">Enregistrer</button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Modale de suppression inchangée */}
      {deleteModalOuvert && (
        <div className="gp-modal-overlay">
          <div className="gp-modal-container">
            {/* ... (contenu identique) ... */}
          </div>
        </div>
      )}
    </div>
  );
}

CardTable.defaultProps = { color: "light" };
CardTable.propTypes = { color: PropTypes.oneOf(["light", "dark"]) };
