import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faTicket, 
  faSignOutAlt,
  faTachometerAlt,
  faTools,
  faTable,
  faCalendarWeek,
  faUserTie,
  faBars,
  faTimes
} from '@fortawesome/free-solid-svg-icons';

export default function Sidebar() {
  const [collapseShow, setCollapseShow] = useState("hidden");
  const history = useHistory(); // Modification ici
  const user = JSON.parse(localStorage.getItem('user'));

  const handleLogout = () => {
    localStorage.removeItem('user');
    localStorage.removeItem('jwt_token');
    history.push('/login'); // Modification ici
  };


  return (
    <nav className="md:left-0 md:block md:fixed md:top-0 md:bottom-0 md:overflow-y-auto md:flex-row md:flex-nowrap md:overflow-hidden shadow-xl bg-white flex flex-wrap items-center justify-between relative md:w-64 z-10 py-4 px-6">
      <div className="md:flex-col md:items-stretch md:min-h-full md:flex-nowrap px-0 flex flex-wrap items-center justify-between w-full mx-auto">
        
        {/* Toggler */}
        <button
          className="cursor-pointer text-black opacity-50 md:hidden px-3 py-1 text-xl leading-none bg-transparent rounded border border-solid border-transparent"
          onClick={() => setCollapseShow("bg-white m-2 py-3 px-6")}
        >
          <FontAwesomeIcon icon={faBars} />
        </button>

        {/* Brand Logo */}
        <Link
          className="md:block text-left md:pb-2 text-blueGray-600 mr-0 inline-block whitespace-nowrap text-sm uppercase font-bold p-4 px-0"
          to="/homepage"
        >
          <img
            src="https://upload.wikimedia.org/wikipedia/commons/0/06/Logo_STB.png"
            alt="STB Logo"
            className="h-12 w-32 mx-auto"
          />
        </Link>

        {/* Collapsible Content */}
        <div className={
          "md:flex md:flex-col md:items-stretch md:opacity-100 md:relative md:mt-4 md:shadow-none shadow absolute top-0 left-0 right-0 z-40 overflow-y-auto overflow-x-hidden h-auto items-center flex-1 rounded " +
          collapseShow
        }>
          {/* Close Button */}
          <div className="md:min-w-full md:hidden block pb-4 mb-4 border-b border-solid border-blueGray-200">
            <div className="flex justify-end">
              <button
                className="cursor-pointer text-black opacity-50 px-3 py-1 text-xl leading-none bg-transparent rounded border border-solid border-transparent"
                onClick={() => setCollapseShow("hidden")}
              >
                <FontAwesomeIcon icon={faTimes} />
              </button>
            </div>
          </div>

          {/* Navigation Links */}
          <div className="flex flex-col justify-between h-full">
            <div>
              <Link to="/admin/dashboard" className="flex items-center p-3 text-gray-600 hover:bg-gray-100 rounded">
                <FontAwesomeIcon icon={faTachometerAlt} className="mr-3" />
                Dashboard
              </Link>

              <Link to="/admin/ticket" className="flex items-center p-3 text-gray-600 hover:bg-gray-100 rounded">
                <FontAwesomeIcon icon={faTicket} className="mr-3" />
                Tickets
              </Link>

              <Link to="/admin/calender" className="flex items-center p-3 text-gray-600 hover:bg-gray-100 rounded">
                <FontAwesomeIcon icon={faCalendarWeek} className="mr-3" />
                Calendrier
              </Link>

              <Link to="/admin/cardtech" className="flex items-center p-3 text-gray-600 hover:bg-gray-100 rounded">
                <FontAwesomeIcon icon={faUserTie} className="mr-3" />
                Techniciens
              </Link>
            </div>

            {/* Déconnexion */}
            <div className="mt-auto border-t pt-4">
              <button 
                onClick={handleLogout}
                className="w-full flex items-center p-3 text-red-600 hover:bg-red-50 rounded"
              >
                <FontAwesomeIcon icon={faSignOutAlt} className="mr-3" />
                Déconnexion
              </button>
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
}
