import React, { useState } from "react";
import PropTypes from "prop-types";

export default function CardTable({ color }) {
  const [items, setItems] = useState([
    { id: 1, subject: 'Problème d\'accès à l\'internet', type: 'Urgent', status: 'En attente', date: '2025-03-29', description: 'Le site intranet était inaccessible depuis plusieurs jours.' },
    { id: 2, subject: 'Mise à jour logiciel', type: 'Normal', status: 'Terminé', date: '2025-04-15', description: 'Mise à jour du logiciel de gestion RH.' }
  ]);
  const [searchQuery, setSearchQuery] = useState('');

  const filteredItems = items.filter(item =>
    item.subject.toLowerCase().includes(searchQuery.toLowerCase()) ||
    item.description.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className={`min-h-screen flex justify-center items-start pt-10 px-4`}>
      <div className={`w-full max-w-7xl rounded-lg shadow-lg ${
        color === "light" ? "bg-transparent" : "bg-slate-800 text-white"
      }`}>
        {/* Barre de recherche */}
        <div className="flex justify-start px-8 pt-8 pb-6">
          <input
            type="text"
            placeholder="🔍 Rechercher un ticket..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="w-full sm:w-80 px-5 py-3 border rounded-lg shadow-sm text-base text-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>

        {/* Tableau */}
        <div className="overflow-x-auto px-8 pb-14">
          <table className="w-full border-collapse text-base">
            <thead>
              <tr className={`text-left ${
                color === "light" ? "bg-gray-100 text-gray-600" : "bg-slate-700 text-slate-200"
              }`}>
                <th className="px-6 py-6 font-semibold min-w-[100px]">ID</th>
                <th className="px-6 py-6 font-semibold min-w-[250px]">Sujet</th>
                <th className="px-6 py-6 font-semibold min-w-[150px]">Type</th>
                <th className="px-6 py-6 font-semibold min-w-[150px]">Statut</th>
                <th className="px-6 py-6 font-semibold min-w-[180px]">Date</th>
                <th className="px-6 py-6 font-semibold min-w-[400px]">Description</th>
                <th className="px-6 py-6 font-semibold min-w-[200px]">Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredItems.map((item) => (
                <tr
                  key={item.id}
                  className="border-t hover:bg-gray-50 transition-colors"
                >
                  <td className="px-6 py-4">{item.id}</td>
                  <td className="px-6 py-4">{item.subject}</td>
                  <td className="px-6 py-4 font-semibold text-red-600">{item.type}</td>
                  <td className="px-6 py-4">
                    <span className={`font-semibold ${
                      item.status === 'En attente' ? 'text-red-600' : 'text-green-600'
                    }`}>
                      {item.status}
                    </span>
                  </td>
                  <td className="px-6 py-4">{item.date}</td>
                  <td className="px-6 py-4 text-gray-600">{item.description}</td>
                  <td className="px-6 py-4">{/* Actions vides */}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

CardTable.defaultProps = {
  color: "light",
};

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
