import React, { useEffect, useState } from "react";
import axios from 'axios';
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faPen } from "@fortawesome/free-solid-svg-icons";

export default function CardTable({ color }) {
  const [users, setUsers] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newUser, setNewUser] = useState({
    nom: "",
    prenom: "",
    email: "",
    id: "",
    role: "",
    tel: "",
    password: ""
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editIndex, setEditIndex] = useState(null);

  const getUsers = async () => {
    try {
      const res = await axios.get("/allusers");
      if (res.data) {
        setUsers(res.data);
      }
    } catch (error) {
      console.error("Erreur lors de la récupération des utilisateurs :", error);
    }
  };

  useEffect(() => {
    getUsers();
  }, []);

  const handleInputChange = (e) => {
    setNewUser({ ...newUser, [e.target.name]: e.target.value });
  };

  const handleAddOrUpdateUser = (e) => {
    e.preventDefault();

    if (isEditing && editIndex !== null) {
      const updatedUsers = [...users];
      updatedUsers[editIndex] = newUser;
      setUsers(updatedUsers);
    }

    setNewUser({ nom: "", prenom: "", email: "", id: "", role: "", tel: "", password: "" });
    setIsEditing(false);
    setEditIndex(null);
    setShowForm(false);
  };

  const handleDeleteUser = (index) => {
    const confirmDelete = window.confirm("Êtes-vous sûr de vouloir supprimer cet utilisateur ?");
    if (confirmDelete) {
      const updatedUsers = users.filter((_, i) => i !== index);
      setUsers(updatedUsers);
    }
  };

  const handleEditUser = (index) => {
    setNewUser({ ...users[index], password: "" });
    setIsEditing(true);
    setEditIndex(index);
    setShowForm(true);
  };

  return (
    <div className={"relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg " + (color === "light" ? "bg-white" : "bg-lightBlue-900 text-white")}>
      <div className="flex justify-between items-center px-6 py-4 border-b border-gray-200">
        <h3 className={"font-bold text-xl " + (color === "light" ? "text-blueGray-700" : "text-white")}>Liste des utilisateurs</h3>
      </div>

      <div className="overflow-x-auto p-4">
        <table className="w-full text-sm text-left text-white">
          <thead className="text-xs uppercase bg-lightBlue-800 text-blueGray-100">
            <tr>
              <th className="px-6 py-3">Nom</th>
              <th className="px-6 py-3">Prénom</th>
              <th className="px-6 py-3">Email</th>
              <th className="px-6 py-3">ID</th>
              <th className="px-6 py-3">Rôle</th>
              <th className="px-6 py-3">Téléphone</th>
              <th className="px-6 py-3 text-right">Actions</th>
            </tr>
          </thead>
          <tbody className="bg-lightBlue-900 divide-y divide-blueGray-700">
            {users.map((user, index) => (
              <tr key={index} className="hover:bg-lightBlue-800">
                <td className="px-6 py-4">{user.nom}</td>
                <td className="px-6 py-4">{user.prenom}</td>
                <td className="px-6 py-4">{user.email}</td>
                <td className="px-6 py-4">{user.id}</td>
                <td className="px-6 py-4">{user.role}</td>
                <td className="px-6 py-4">{user.tel}</td>
                <td className="px-6 py-4 text-right">
                  <button
                    onClick={() => handleDeleteUser(index)}
                    className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded mr-2 text-xs shadow"
                  >
                    <FontAwesomeIcon icon={faTrash} />
                  </button>
                  <button
                    onClick={() => handleEditUser(index)}
                    className="bg-lightBlue-500 hover:bg-lightBlue-600 text-white px-3 py-1 rounded text-xs shadow"
                  >
                    <FontAwesomeIcon icon={faPen} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {showForm && (
          <form onSubmit={handleAddOrUpdateUser} className="mt-6 bg-lightBlue-900 text-white p-6 rounded shadow-md">
            <h4 className="text-lg font-semibold mb-4 text-white">
              Modifier l'utilisateur
            </h4>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mb-4">
              <div>
                <label className="block text-sm mb-1" htmlFor="nom">Nom</label>
                <input
                  id="nom"
                  type="text"
                  name="nom"
                  value={newUser.nom}
                  onChange={handleInputChange}
                  className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 text-white rounded"
                  required
                />
              </div>
              <div>
                <label className="block text-sm mb-1" htmlFor="prenom">Prénom</label>
                <input
                  id="prenom"
                  type="text"
                  name="prenom"
                  value={newUser.prenom}
                  onChange={handleInputChange}
                  className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 text-white rounded"
                  required
                />
              </div>
              <div>
                <label className="block text-sm mb-1" htmlFor="email">Email</label>
                <input
                  id="email"
                  type="email"
                  name="email"
                  value={newUser.email}
                  onChange={handleInputChange}
                  className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 text-white rounded"
                  required
                />
              </div>
              <div>
                <label className="block text-sm mb-1" htmlFor="id">ID</label>
                <input
                  id="id"
                  type="text"
                  name="id"
                  value={newUser.id}
                  className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 text-white rounded"
                  disabled
                  required
                />
              </div>
              <div>
                <label className="block text-sm mb-1" htmlFor="role">Rôle</label>
                <input
                  id="role"
                  type="text"
                  name="role"
                  value={newUser.role}
                  onChange={handleInputChange}
                  className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 text-white rounded"
                  required
                />
              </div>
              <div>
                <label className="block text-sm mb-1" htmlFor="tel">Téléphone</label>
                <input
                  id="tel"
                  type="text"
                  name="tel"
                  value={newUser.tel}
                  onChange={handleInputChange}
                  className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 text-white rounded"
                  required
                />
              </div>
              <div>
                <label className="block text-sm mb-1" htmlFor="password">Mot de passe</label>
                <input
                  id="password"
                  type="password"
                  name="password"
                  placeholder="Nouveau mot de passe"
                  value={newUser.password}
                  onChange={handleInputChange}
                  className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 text-white rounded"
                />
              </div>
            </div>

            <div className="flex justify-end">
              <button
                type="submit"
                className="bg-emerald-500 hover:bg-emerald-600 text-white text-sm font-semibold px-6 py-2 rounded shadow"
              >
                Modifier
              </button>
