import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import axios from "axios";

export default function CardStats({
  statSubtitle,
  statTitle,
  statArrow,
  statPercent,
  statPercentColor,
  statDescripiron,
  statIconName,
  statIconColor,
}) {
  return (
    <div className="w-full md:w-1/3 px-2">
      <div className="relative flex flex-col min-w-0 break-words bg-white rounded shadow-lg">
        <div className="flex-auto p-4">
          <div className="flex flex-wrap">
            <div className="relative w-full pr-4 max-w-full flex-grow flex-1">
              <h5 className="text-blueGray-400 uppercase font-bold text-xs">
                {statSubtitle}
              </h5>
              <span className="font-semibold text-xl text-blueGray-700">
                {statTitle}
              </span>
            </div>
            <div className="relative w-auto pl-4 flex-initial">
              <div
                className={
                  "text-white p-3 text-center inline-flex items-center justify-center w-12 h-12 shadow-lg rounded-full " +
                  statIconColor
                }
              >
                <i className={statIconName}></i>
              </div>
            </div>
          </div>
          <p className="text-sm text-blueGray-400 mt-4">
            <span className={statPercentColor + " mr-2"}>
              <i
                className={
                  statArrow === "up"
                    ? "fas fa-arrow-up"
                    : statArrow === "down"
                    ? "fas fa-arrow-down"
                    : ""
                }
              ></i>{" "}
              {statPercent}%
            </span>
            <span className="whitespace-nowrap">{statDescripiron}</span>
          </p>
        </div>
      </div>
    </div>
  );
}

CardStats.defaultProps = {
  statSubtitle: "Traffic",
  statTitle: "350,897",
  statArrow: "up",
  statPercent: "3.48",
  statPercentColor: "text-emerald-500",
  statDescripiron: "Since last month",
  statIconName: "far fa-chart-bar",
  statIconColor: "bg-red-500",
};

CardStats.propTypes = {
  statSubtitle: PropTypes.string,
  statTitle: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
  statArrow: PropTypes.oneOf(["up", "down"]),
  statPercent: PropTypes.string,
  statPercentColor: PropTypes.string,
  statDescripiron: PropTypes.string,
  statIconName: PropTypes.string,
  statIconColor: PropTypes.string,
};

// âœ… Composant pour afficher les 3 cartes de stats
export function CardStatsUtilisateurs() {
  const [stats, setStats] = useState(null);

  useEffect(() => {
    axios.get("/stats") // adapte si besoin
      .then((res) => setStats(res.data))
      .catch((err) => console.error("Erreur chargement stats:", err));
  }, []);

  if (!stats) return <div>Chargement des statistiques...</div>;

  return (
    <div className="flex flex-wrap">
      <CardStats
        statSubtitle="UTILISATEURS"
        statTitle={stats.utilisateurs}
        statArrow="up"
        statPercent="100"
        statPercentColor="text-emerald-500"
        statDescripiron="Nombre total"
        statIconName="fas fa-users"
        statIconColor="bg-lightBlue-500"
      />
      <CardStats
        statSubtitle="TECHNICIENS"
        statTitle={stats.techniciens}
        statArrow="up"
        statPercent="100"
        statPercentColor="text-emerald-500"
        statDescripiron="Nombre total"
        statIconName="fas fa-tools"
        statIconColor="bg-orange-500"
      />
      <CardStats
        statSubtitle="ADMINS"
        statTitle={stats.admins}
        statArrow="up"
        statPercent="100"
        statPercentColor="text-emerald-500"
        statDescripiron="Nombre total"
        statIconName="fas fa-user-shield"
        statIconColor="bg-red-500"
      />
    </div>
  );
}
