import React, { useState } from "react";
import PropTypes from "prop-types";

export default function CardTable({ color }) {
  const [items, setItems] = useState([
    { 
      id: 1, 
      subject: 'Probl√®me d\'acc√®s √† l\'internet', 
      type: 'Urgent', 
      status: 'En attente', 
      date: '2025-03-29', 
      description: 'Le site intranet √©tait inaccessible depuis plusieurs jours.' 
    },
    { 
      id: 2, 
      subject: 'Mise √† jour logiciel', 
      type: 'Normal', 
      status: 'Termin√©', 
      date: '2025-04-15', 
      description: 'Mise √† jour du logiciel de gestion RH.' 
    }
  ]);
  
  const [searchQuery, setSearchQuery] = useState('');

  const filteredItems = items.filter(item =>
    item.subject.toLowerCase().includes(searchQuery.toLowerCase()) ||
    item.description.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className={`relative h-full w-full ${color === "light" ? "bg-transparent" : "bg-slate-800 text-white"}`}>
      {/* Barre de recherche */}
      <div className="flex justify-start pb-4 px-4">
        <input
          type="text"
          placeholder="üîç Rechercher un ticket..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="w-full sm:w-64 px-4 py-2 border rounded-lg shadow-sm text-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
      </div>

      {/* Tableau plein √©cran avec bordures */}
      <div className="overflow-x-auto w-full h-[calc(100vh-150px)] px-4">
        <table className="w-full border-collapse border border-gray-200 text-sm">
          <thead>
            <tr className={`text-left ${color === "light" ? "text-gray-500 bg-gray-50" : "text-slate-200 bg-slate-700"}`}>
              <th className="px-6 py-4 font-medium border-b border-gray-200">ID</th>
              <th className="px-6 py-4 font-medium border-b border-gray-200">Sujet</th>
              <th className="px-6 py-4 font-medium border-b border-gray-200">Type</th>
              <th className="px-6 py-4 font-medium border-b border-gray-200">Statut</th>
              <th className="px-6 py-4 font-medium border-b border-gray-200">Date</th>
              <th className="px-6 py-4 font-medium border-b border-gray-200">Description</th>
              <th className="px-6 py-4 font-medium border-b border-gray-200">Actions</th>
            </tr>
          </thead>
          
          <tbody>
            {filteredItems.map((item) => (
              <tr 
                key={item.id}
                className={`border-t border-gray-200 hover:bg-gray-50 transition-colors ${
                  color === "dark" ? "hover:bg-slate-600/20" : ""
                }`}
              >
                <td className="px-6 py-4 border-r border-gray-200">{item.id}</td>
                <td className="px-6 py-4 border-r border-gray-200">{item.subject}</td>
                <td className="px-6 py-4 font-semibold text-red-600 border-r border-gray-200">{item.type}</td>
                <td className="px-6 py-4 border-r border-gray-200">
                  <span className={`font-semibold ${item.status === 'En attente' ? 'text-red-600' : 'text-green-600'}`}>
                    {item.status}
                  </span>
                </td>
                <td className="px-6 py-4 border-r border-gray-200">{item.date}</td>
                <td className="px-6 py-4 text-gray-600 truncate max-w-[300px] border-r border-gray-200">
                  {item.description}
                </td>
                <td className="px-6 py-4">
                  {/* Espace r√©serv√© pour les actions */}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

CardTable.defaultProps = {
  color: "light",
};

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
