const ticketItem = {
  backgroundColor: "white",
  borderRadius: "6px",
  padding: "1rem",
  marginBottom: "1rem",
  cursor: "pointer",
  border: "1px solid #eee",
  transition: "all 0.3s",
};
const ticketHeader = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  marginBottom: "0.5rem",
};
const ticketId = { color: "#666", fontSize: "0.9rem" };
const ticketTitle = { fontSize: "1.1rem", color: "#333", margin: "0 0 0.5rem 0" };
const ticketDate = { color: "#999", fontSize: "0.85rem", margin: "0" };
const detailsPanel = {
  backgroundColor: "white",
  borderRadius: "8px",
  padding: "1.5rem",
  border: "1px solid #eee",
};
const detailsHeader = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  marginBottom: "1.5rem",
};
const detailsTitle = { fontSize: "1.5rem", color: "#1a237e", margin: "0" };
const closeButton = {
  background: "none",
  border: "none",
  fontSize: "1.5rem",
  color: "#666",
  cursor: "pointer",
  padding: "0 0.5rem",
};
const detailsContent = { lineHeight: "1.6" };
const detailRow = {
  display: "flex",
  justifyContent: "space-between",
  marginBottom: "1rem",
  paddingBottom: "1rem",
  borderBottom: "1px solid #eee",
};
const detailLabel = { color: "#666", fontWeight: "bold", marginRight: "1rem" };
const detailDescription = { marginTop: "2rem" };
const searchInput = {
  padding: "0.5rem",
  width: "60%",
  maxWidth: "300px",
  borderRadius: "6px",
  border: "1px solid #ccc",
  fontSize: "1rem",
};
const feedbackSection = {
  marginTop: "2rem",
  borderTop: "1px solid #ddd",
  paddingTop: "1rem",
};
const feedbackTitle = {
  fontSize: "1.1rem",
  marginBottom: "0.5rem",
  color: "#1a237e",
};
const feedbackSubmitted = {
  backgroundColor: "#f1f1f1",
  padding: "1rem",
  borderRadius: "6px",
};
const feedbackComment = {
  color: "#333",
  fontStyle: "italic",
};
const feedbackInput = {
  marginBottom: "1rem",
};
const feedbackLabel = {
  fontWeight: "bold",
  display: "block",
  marginBottom: "0.5rem",
};
const textareaStyle = {
  width: "100%",
  borderRadius: "4px",
  border: "1px solid #ccc",
  padding: "0.5rem",
  resize: "vertical",
};
const submitButton = {
  backgroundColor: "#1a237e",
  color: "#fff",
  border: "none",
  borderRadius: "4px",
  padding: "0.5rem 1rem",
  cursor: "pointer",
};

export default MyTickets;
