import React from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";

function NewTicketForm() {
  return (
    <>
      <IndexNavbar />

      <div style={pageWrapper}>
        <div style={backgroundOverlay}></div>
        
        <div style={ticketCard}>
          <div style={headerSection}>
            <div style={titleContainer}>
              <h1 style={titleStyle}>Nouveau Ticket STB</h1>
              <p style={subtitleStyle}>Support technique de la Banque STB</p>
            </div>
            <button style={returnButtonStyle}>
              ← Retour à la liste
            </button>
          </div>

          <div style={formSection}>
            <div style={inputGroup}>
              <label style={labelStyle}>📝 Sujet du ticket</label>
              <input
                type="text"
                placeholder="Ex: Mon accès internet est bloqué"
                style={inputStyle}
              />
            </div>

            <div style={inputGroup}>
              <label style={labelStyle}>🏢 Département concerné</label>
              <div style={selectContainer}>
                <select style={selectStyle}>
                  <option>IT - Informatique</option>
                  <option>RH - Ressources Humaines</option>
                  <option>Comptabilité</option>
                </select>
                <div style={selectArrow}></div>
              </div>
            </div>

            <div style={inputGroup}>
              <label style={labelStyle}>📄 Description détaillée</label>
              <textarea
                rows="5"
                placeholder="Décrivez votre problème ici..."
                style={textareaStyle}
              />
            </div>

            <div style={createButtonContainer}>
              <button style={createButtonStyle}>
                <span style={buttonText}>📨 Envoyer le ticket</span>
                <span style={buttonHoverEffect}></span>
              </button>
            </div>
          </div>
        </div>
      </div>

      <Footerr />
    </>
  );
}

// Styles modernisés avec thème banque/finance
const pageWrapper = {
  fontFamily: "'Inter', sans-serif",
  minHeight: "100vh",
  padding: "3rem 1rem",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  position: "relative",
  background: `linear-gradient(rgba(10, 25, 47, 0.95), rgba(10, 25, 47, 0.95)), url('https://images.unsplash.com/photo-1554224155-3a58922a22c3?ixlib=rb-1.2.1&auto=format&fit=crop&w=1920&q=80')`,
  backgroundSize: "cover",
  backgroundPosition: "center",
  backgroundAttachment: "fixed",
};

const backgroundOverlay = {
  position: "absolute",
  top: 0,
  left: 0,
  right: 0,
  bottom: 0,
  background: "rgba(15, 30, 50, 0.6)",
};

const ticketCard = {
  background: "rgba(255, 255, 255, 0.97)",
  borderRadius: "20px",
  boxShadow: "0 12px 30px rgba(0,0,0,0.25)",
  padding: "2.5rem 3rem",
  maxWidth: "700px",
  width: "100%",
  position: "relative",
  zIndex: 1,
  border: "1px solid rgba(255,255,255,0.2)",
  backdropFilter: "blur(5px)",
};

const headerSection = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "flex-start",
  marginBottom: "2rem",
  borderBottom: "2px solid #f0f4f8",
  paddingBottom: "1.5rem",
};

const titleContainer = {
  maxWidth: "70%",
};

const titleStyle = {
  fontSize: "2.1rem",
  fontWeight: "800",
  color: "#0d47a1",
  marginBottom: "0.25rem",
  background: "linear-gradient(45deg, #0d47a1, #2196f3)",
  WebkitBackgroundClip: "text",
  WebkitTextFillColor: "transparent",
};

const subtitleStyle = {
  fontSize: "0.9rem",
  color: "#607d8b",
  fontWeight: "500",
};

const returnButtonStyle = {
  padding: "0.6rem 1.5rem",
  backgroundColor: "#e3f2fd",
  color: "#0d47a1",
  border: "none",
  borderRadius: "8px",
  cursor: "pointer",
  fontWeight: "600",
  transition: "all 0.2s ease",
  display: "flex",
  alignItems: "center",
  gap: "0.5rem",
  boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
  ':hover': {
    backgroundColor: "#bbdefb",
    transform: "translateY(-1px)",
  },
};

const formSection = {
  display: "flex",
  flexDirection: "column",
  gap: "1.8rem",
};

const inputGroup = {
  display: "flex",
  flexDirection: "column",
};

const labelStyle = {
  fontWeight: "600",
  marginBottom: "0.75rem",
  color: "#37474f",
  fontSize: "0.95rem",
  display: "flex",
  alignItems: "center",
  gap: "0.5rem",
};

const inputStyle = {
  padding: "1rem",
  border: "2px solid #e0e7ff",
  borderRadius: "10px",
  fontSize: "1rem",
  transition: "all 0.3s ease",
  ':focus': {
    borderColor: "#0d47a1",
    boxShadow: "0 0 0 3px rgba(13, 71, 161, 0.1)",
  },
};

const selectContainer = {
  position: "relative",
};

const selectStyle = {
  ...inputStyle,
  appearance: "none",
  width: "100%",
  backgroundColor: "#fff",
};

const selectArrow = {
  position: "absolute",
  right: "15px",
  top: "50%",
  transform: "translateY(-50%)",
  width: "0",
  height: "0",
  borderLeft: "6px solid transparent",
  borderRight: "6px solid transparent",
  borderTop: "6px solid #666",
  pointerEvents: "none",
};

const textareaStyle = {
  ...inputStyle,
  resize: "vertical",
  minHeight: "140px",
  lineHeight: "1.6",
};

const createButtonContainer = {
  display: "flex",
  justifyContent: "flex-end",
  marginTop: "1.5rem",
};

const createButtonStyle = {
  padding: "1.1rem 2.5rem",
  backgroundColor: "#0d47a1",
  color: "#ffffff",
  border: "none",
  borderRadius: "12px",
  cursor: "pointer",
  fontWeight: "700",
  fontSize: "1rem",
  transition: "all 0.3s ease",
  position: "relative",
  overflow: "hidden",
  boxShadow: "0 4px 15px rgba(13, 71, 161, 0.3)",
  ':hover': {
    backgroundColor: "#1565c0",
    transform: "translateY(-2px)",
  },
};

const buttonHoverEffect = {
  position: "absolute",
  top: "-50%",
  left: "-50%",
  width: "200%",
  height: "200%",
  background: "linear-gradient(45deg, transparent, rgba(255,255,255,0.2), transparent)",
  transform: "rotate(45deg)",
  transition: "all 0.5s ease",
};

const buttonText = {
  position: "relative",
  zIndex: 1,
  display: "flex",
  alignItems: "center",
  gap: "0.8rem",
};

export default NewTicketForm;
