import React, { useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faPen, faSquareCheck } from '@fortawesome/free-solid-svg-icons';

// Styles pour les dialogues et les modals
const styles = `...`; // Garde tes styles CSS comme avant

export default function CardTable({ color }) {
  const [items, setItems] = useState([
    {
      id: 1,
      subject: "Problème d'accès à l'internet",
      status: "En attente",
      date: "2025-03-29",
      description: "Le site intranet était inaccessible depuis plusieurs jours.",
      type: "Urgent"
    },
    {
      id: 2,
      subject: "Mise à jour du logiciel",
      status: "En attente",
      date: "2025-04-01",
      description: "Besoin de mettre à jour le logiciel RH.",
      type: "Faible priorité"
    },
    {
      id: 3,
      subject: "Erreur de facturation",
      status: "En attente",
      date: "2025-04-10",
      description: "Facture envoyée avec le mauvais montant.",
      type: "Normal"
    }
  ]);

  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [selectedItemId, setSelectedItemId] = useState(null);

  const priorityOrder = { "Urgent": 1, "Normal": 2, "Faible priorité": 3 };

  const sortedItems = [...items].sort((a, b) => priorityOrder[a.type] - priorityOrder[b.type]);

  const handleDeleteConfirmation = (id) => {
    setSelectedItemId(id);
    setIsDialogOpen(true);
  };

  const handleConfirmDelete = () => {
    setItems(items.filter(item => item.id !== selectedItemId));
    setIsDialogOpen(false);
  };

  const handleCancelDelete = () => {
    setIsDialogOpen(false);
    setSelectedItemId(null);
  };

  const handleComplete = (id) => {
    setItems(items.map(item =>
      item.id === id ? { ...item, status: 'Terminé' } : item
    ));
  };

  const handleEdit = (id) => {
    setItems(items.map(item =>
      item.id === id
        ? {
            ...item,
            subject: 'Problème résolu',
            description: 'Le problème a été résolu avec succès.',
            type: 'Faible priorité'
          }
        : item
    ));
  };

  return (
    <div
      className={
        "relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded " +
        (color === "light" ? "bg-white" : "bg-lightBlue-900 text-white")
      }
    >
      <style>{styles}</style>

      {isDialogOpen && (
        <div className="fixed-dialog-container">
          <div className="dialog-content">
            <p className="text-lg text-gray-700 mb-4">
              Êtes-vous sûr de vouloir supprimer ce ticket ?
            </p>
            <div className="flex justify-end space-x-4">
              <button
                onClick={handleCancelDelete}
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
              >
                Annuler
              </button>
              <button
                onClick={handleConfirmDelete}
                className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
              >
                Supprimer
              </button>
            </div>
          </div>
        </div>
      )}

      <div className="block w-full overflow-x-auto">
        <table className="items-center w-full bg-transparent border-collapse">
          <thead>
            <tr>
              {["Id", "Sujet", "Statut", "Date", "Description", "Type de ticket", "Actions"].map((header, index) => (
                <th
                  key={index}
                  className={
                    "px-6 py-3 border border-solid text-xs uppercase whitespace-nowrap font-semibold text-left " +
                    (color === "light"
                      ? "bg-blueGray-50 text-blueGray-500 border-blueGray-100"
                      : "bg-lightBlue-800 text-lightBlue-300 border-lightBlue-700")
                  }
                >
                  {header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {sortedItems.map((item) => (
              <tr key={item.id} className="hover:bg-gray-100 transition-colors duration-200">
                <td className="border-t p-4 text-xs whitespace-nowrap"><strong>{item.id}</strong></td>
                <td className="border-t p-4 text-xs whitespace-nowrap">{item.subject}</td>
                <td className="border-t p-4 text-xs whitespace-nowrap">
                  <span className="flex items-center">
                    <i className={`fas fa-circle ${item.status === 'En attente' ? 'text-orange-500' : 'text-green-500'} mr-2`} />
                    {item.status}
                  </span>
                </td>
                <td className="border-t p-4 text-xs whitespace-nowrap">{item.date}</td>
                <td className="border-t p-4 text-xs whitespace-nowrap">{item.description}</td>
                <td className="border-t p-4 text-xs whitespace-nowrap font-semibold">{item.type}</td>
                <td className="border-t p-4 text-xs whitespace-nowrap">
                  <button
                    className="bg-red-500 hover:bg-red-600 text-white text-xs font-bold uppercase px-3 py-2 rounded shadow mr-2"
                    onClick={() => handleDeleteConfirmation(item.id)}
                  >
                    <FontAwesomeIcon icon={faTrash} />
                  </button>
                  <button
                    className="bg-indigo-500 hover:bg-indigo-600 text-white text-xs font-bold uppercase px-3 py-2 rounded shadow mr-2"
                    onClick={() => handleEdit(item.id)}
                  >
                    <FontAwesomeIcon icon={faPen} />
                  </button>
                  <button
                    className="bg-green-500 hover:bg-green-600 text-white text-xs font-bold uppercase px-3 py-2 rounded shadow"
                    onClick={() => handleComplete(item.id)}
                  >
                    <FontAwesomeIcon icon={faSquareCheck} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

CardTable.defaultProps = {
  color: "light",
};

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
