import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faPen } from "@fortawesome/free-solid-svg-icons";
import { getAllUsers } from "services/ApiUser";

export default function CardTable({ color }) {
  const [users, setUsers] = useState([
    {
      nom: "Toumi",
      prenom: "Med Rayen",
      email: "toumirayen130@gmail.com",
      id: "05",
    },
  ]);
  const [showForm, setShowForm] = useState(false);
  const [newUser, setNewUser] = useState({
    nom: "",
    prenom: "",
    email: "",
    id: "",
  });

  const getUsers = async () => {
    try {
      const res = await getAllUsers();
      if (res.data?.userList) {
        setUsers(res.data.userList);
      }
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    // getUsers(); // DÃ©commente pour charger depuis l'API rÃ©elle
  }, []);

  const handleInputChange = (e) => {
    setNewUser({ ...newUser, [e.target.name]: e.target.value });
  };

  const handleAddUser = (e) => {
    e.preventDefault();
    setUsers([...users, newUser]);
    setNewUser({ nom: "", prenom: "", email: "", id: "" });
    setShowForm(false);
  };

  return (
    <div
      className={`relative flex flex-col min-w-0 break-words w-full mb-6 shadow-xl rounded-2xl overflow-hidden ${
        color === "light"
          ? "bg-white text-blueGray-800"
          : "bg-gradient-to-br from-blue-900 to-lightBlue-900 text-white"
      }`}
    >
      <div className="flex justify-between items-center px-6 py-5 border-b border-blueGray-200">
        <h3 className="font-bold text-2xl">ðŸ‘¤ Liste des utilisateurs</h3>
        <button
          className="bg-emerald-500 hover:bg-emerald-600 transition-all duration-200 text-white text-sm font-semibold px-4 py-2 rounded-xl shadow-md"
          onClick={() => setShowForm(!showForm)}
        >
          {showForm ? "Annuler" : "+ Ajouter un utilisateur"}
        </button>
      </div>

      <div className="overflow-x-auto p-4">
        <table className="w-full text-sm text-left rounded-lg overflow-hidden">
          <thead className="text-xs uppercase bg-blue-800 text-blueGray-100">
            <tr>
              <th className="px-6 py-3">Nom</th>
              <th className="px-6 py-3">PrÃ©nom</th>
              <th className="px-6 py-3">Email</th>
              <th className="px-6 py-3">ID</th>
              <th className="px-6 py-3 text-right">Actions</th>
            </tr>
          </thead>
          <tbody className="bg-blue-900 divide-y divide-blue-700">
            {users.map((user, index) => (
              <tr key={index} className="hover:bg-blue-800 transition-all duration-150">
                <td className="px-6 py-4">{user.nom}</td>
                <td className="px-6 py-4">{user.prenom}</td>
                <td className="px-6 py-4">{user.email}</td>
                <td className="px-6 py-4">{user.id}</td>
                <td className="px-6 py-4 text-right">
                  <button className="bg-red-500 hover:bg-red-600 transition duration-200 text-white px-3 py-1 rounded-xl mr-2 text-xs shadow-md">
                    <FontAwesomeIcon icon={faTrash} />
                  </button>
                  <button className="bg-indigo-500 hover:bg-indigo-600 transition duration-200 text-white px-3 py-1 rounded-xl text-xs shadow-md">
                    <FontAwesomeIcon icon={faPen} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {showForm && (
          <form
            onSubmit={handleAddUser}
            className="mt-6 bg-blue-800 p-6 rounded-2xl shadow-md space-y-4 animate-fade-in"
          >
            <h4 className="text-md font-semibold text-white">
              Ajouter un utilisateur
            </h4>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <input
                type="text"
                name="nom"
                placeholder="Nom"
                value={newUser.nom}
                onChange={handleInputChange}
                className="p-2 bg-blue-700 border border-blue-600 text-white placeholder-blue-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-emerald-400"
                required
              />
              <input
                type="text"
                name="prenom"
                placeholder="PrÃ©nom"
                value={newUser.prenom}
                onChange={handleInputChange}
                className="p-2 bg-blue-700 border border-blue-600 text-white placeholder-blue-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-emerald-400"
                required
              />
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={newUser.email}
                onChange={handleInputChange}
                className="p-2 bg-blue-700 border border-blue-600 text-white placeholder-blue-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-emerald-400"
                required
              />
              <input
                type="text"
                name="id"
                placeholder="ID"
                value={newUser.id}
                onChange={handleInputChange}
                className="p-2 bg-blue-700 border border-blue-600 text-white placeholder-blue-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-emerald-400"
                required
              />
            </div>
            <div className="flex justify-end pt-2">
              <button
                type="submit"
                className="bg-emerald-500 hover:bg-emerald-600 transition-all duration-200 text-white text-sm font-semibold px-5 py-2 rounded-xl shadow-md"
              >
                âœ… Ajouter
              </button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
}

CardTable.defaultProps = {
  color: "dark",
};

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
