import React, { useState, useEffect } from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";

function MyTickets() {
  const [tickets, setTickets] = useState([]);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [searchId, setSearchId] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [editedSubject, setEditedSubject] = useState("");
  const [editedDescription, setEditedDescription] = useState("");

  const getShortCode = (id) => {
    const strId = String(id);
    if (!strId || strId.length < 10) return "XXXX";
    return strId.substring(6, 10);
  };

  useEffect(() => {
    document.body.style.margin = "0";
    document.body.style.minHeight = "100vh";
    document.body.style.display = "flex";
    document.body.style.flexDirection = "column";

    const fetchTickets = async () => {
      try {
        const response = await fetch('/alltickets');
        const data = await response.json();
        setTickets(data);
      } catch (error) {
        console.error("Erreur lors de la récupération des tickets:", error);
      }
    };

    fetchTickets();
  }, []);

  useEffect(() => {
    if (selectedTicket) {
      setEditedSubject(selectedTicket.sujet);
      setEditedDescription(selectedTicket.description);
    }
  }, [selectedTicket]);

  const status = (statut) => ({
    padding: "0.3rem 0.8rem",
    borderRadius: "4px",
    fontSize: "0.85rem",
    backgroundColor: {
      "Ouvert": "#ffebee",
      "En cours": "#e3f2fd",
      "Résolu": "#e8f5e9",
    }[statut],
    color: {
      "Ouvert": "#c62828",
      "En cours": "#1565c0",
      "Résolu": "#2e7d32",
    }[statut],
    fontWeight: "bold",
  });

  const handleSave = async () => {
    try {
      const response = await fetch(`/tickets/${selectedTicket._id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          sujet: editedSubject,
          description: editedDescription
        }),
      });

      if (!response.ok) throw new Error('Échec de la mise à jour');

      const updatedTicket = await response.json();
      setTickets(tickets.map(t => 
        t._id === updatedTicket._id ? updatedTicket : t
      ));
      setSelectedTicket(updatedTicket);
      setIsEditing(false);
      
    } catch (error) {
      console.error("Erreur lors de la mise à jour:", error);
      alert("Une erreur est survenue lors de la sauvegarde");
    }
  };

  const filteredTickets = tickets.filter(ticket =>
    searchId === "" || ticket._id?.toString().includes(searchId)
  );

  return (
    <>
      <IndexNavbar />
      <div style={container}>
        <div style={contentWrapper}>
          <img
            src="/images/support-banner.jpg"
            alt="Bannière Support"
            style={bannerImage}
          />
          <h1 style={title}>Espace Support Client</h1>
          <p style={subtitle}>Consultez vos tickets et suivez leur avancement</p>

          <div style={{ marginBottom: "1rem", textAlign: "center" }}>
            <input
              type="text"
              placeholder="Rechercher par numéro de ticket"
              value={searchId}
              onChange={(e) => setSearchId(e.target.value)}
              style={searchInput}
            />
          </div>

          <div style={ticketGrid}>
            <div style={ticketList}>
              {filteredTickets.map((ticket) => (
                <div
                  key={ticket._id}
                  style={ticketItem}
                  onClick={() => setSelectedTicket(ticket)}
                >
                  <div style={ticketHeader}>
                    <span style={ticketId}>
                      #{ticket._id ? getShortCode(ticket._id) : "XXXX"}
                    </span>
                    <span style={status(ticket.statut)}>{ticket.statut}</span>
                  </div>
                  <h3 style={ticketTitle}>{ticket.sujet}</h3>
                  <p style={ticketDate}>{ticket.date?.slice(0, 10)}</p>
                </div>
              ))}
              {filteredTickets.length === 0 && (
                <p style={{ textAlign: "center", color: "#999" }}>Aucun ticket trouvé.</p>
              )}
            </div>

            {selectedTicket && (
              <div style={detailsPanel}>
                <div style={detailsHeader}>
                  <h2 style={detailsTitle}>Détails du ticket</h2>
                  <div>
                    {isEditing ? (
                      <>
                        <button style={saveButton} onClick={handleSave}>
                          Enregistrer
                        </button>
                        <button 
                          style={cancelButton} 
                          onClick={() => setIsEditing(false)}
                        >
                          Annuler
                        </button>
                      </>
                    ) : (
                      <button 
                        style={editButton} 
                        onClick={() => setIsEditing(true)}
                      >
                        Modifier
                      </button>
                    )}
                    <button style={closeButton} onClick={() => {
                      setSelectedTicket(null);
                      setIsEditing(false);
                    }}>×</button>
                  </div>
                </div>
                <div style={detailsContent}>
                  <div style={detailRow}>
                    <span style={detailLabel}>Numéro :</span>
                    <span>#{getShortCode(selectedTicket._id)}</span>
                  </div>
                  <div style={detailRow}>
                    <span style={detailLabel}>Statut :</span>
                    <span style={status(selectedTicket.statut)}>
                      {selectedTicket.statut}
                    </span>
                  </div>
                  <div style={detailRow}>
                    <span style={detailLabel}>Date :</span>
                    <span>{selectedTicket.date?.slice(0, 10)}</span>
                  </div>
                  <div style={detailRow}>
                    <span style={detailLabel}>Sujet :</span>
                    {isEditing ? (
                      <input
                        value={editedSubject}
                        onChange={(e) => setEditedSubject(e.target.value)}
                        style={inputStyle}
                      />
                    ) : (
                      <span>{selectedTicket.sujet}</span>
                    )}
                  </div>
                  <div style={detailDescription}>
                    <p style={detailLabel}>Description :</p>
                    {isEditing ? (
                      <textarea
                        value={editedDescription}
                        onChange={(e) => setEditedDescription(e.target.value)}
                        style={{...inputStyle, height: '100px'}}
                      />
                    ) : (
                      <p>{selectedTicket.description}</p>
                    )}
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
      <Footerr />
    </>
  );
}

