import React, { useState, useEffect } from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";

function MyTickets() {
  const [tickets, setTickets] = useState([]);

  useEffect(() => {
    const fetchTickets = async () => {
      try {
        const response = await fetch('/getticket');
        const data = await response.json();
        setTickets(data);
      } catch (error) {
        console.error('Erreur:', error);
      }
    };
    fetchTickets();
  }, []);

  return (
    <>
      <IndexNavbar />
      <div style={{ padding: '2rem' }}>
        <h2 style={{ color: '#003366', marginBottom: '1.5rem' }}>Mes Tickets</h2>
        <div style={{ display: 'grid', gap: '1rem' }}>
          {tickets.map(ticket => (
            <div key={ticket._id} style={{ 
              padding: '1.5rem',
              border: '1px solid #e0e0e0',
              borderRadius: '12px',
              backgroundColor: '#fff',
              boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
            }}>
              <h3 style={{ color: '#003366', marginBottom: '0.5rem' }}>{ticket.sujet}</h3>
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1rem' }}>
                <p><strong>Type:</strong> {ticket.type}</p>
                <p><strong>Urgence:</strong> {ticket.urgence}</p>
                <p><strong>Statut:</strong> <span style={{ 
                  color: ticket.statut === 'Ouvert' ? '#28a745' : '#dc3545',
                  fontWeight: '600'
                }}>{ticket.statut}</span></p>
                <p><strong>Date:</strong> {new Date(ticket.createdAt).toLocaleDateString('fr-FR')}</p>
              </div>
              <p style={{ marginTop: '1rem' }}><strong>Description:</strong> {ticket.description}</p>
            </div>
          ))}
        </div>
      </div>
      <Footerr />
    </>
  );
}

export default MyTickets;
