import React, { useEffect, useState } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";

const MyTickets = () => {
  const [tickets, setTickets] = useState([]);

  useEffect(() => {
    const storedTickets = localStorage.getItem("tickets");
    if (storedTickets) {
      setTickets(JSON.parse(storedTickets));
    }
  }, []);

  // Transformer les tickets en Ã©vÃ©nements pour le calendrier
  const calendarEvents = tickets.map((ticket) => ({
    title: ticket.title,
    date: ticket.createdAt,
    backgroundColor: "#007bff",
    borderColor: "#007bff",
  }));

  return (
    <div style={{ padding: "2rem" }}>
      <h2 style={{ fontSize: "24px", fontWeight: "bold", marginBottom: "1rem" }}>
        Mes Tickets
      </h2>

      {/* âœ… Affichage tableau classique */}
      <table style={{ width: "100%", borderCollapse: "collapse", marginBottom: "2rem" }}>
        <thead>
          <tr>
            <th style={styles.th}>ID</th>
            <th style={styles.th}>Titre</th>
            <th style={styles.th}>Statut</th>
            <th style={styles.th}>PrioritÃ©</th>
            <th style={styles.th}>CrÃ©Ã© le</th>
            <th style={styles.th}>Mis Ã  jour le</th>
          </tr>
        </thead>
        <tbody>
          {tickets.map((ticket) => (
            <tr key={ticket.id}>
              <td style={styles.td}>{ticket.id}</td>
              <td style={styles.td}>{ticket.title}</td>
              <td style={styles.td}>{ticket.status}</td>
              <td style={styles.td}>{ticket.priority}</td>
              <td style={styles.td}>{ticket.createdAt}</td>
              <td style={styles.td}>{ticket.updatedAt}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* ðŸ“… Calendrier des tickets */}
      <div style={{ border: "1px solid #ccc", borderRadius: "8px", padding: "1rem" }}>
        <h3 style={{ fontSize: "20px", marginBottom: "1rem" }}>Calendrier des Tickets</h3>
        <FullCalendar
          plugins={[dayGridPlugin]}
          initialView="dayGridMonth"
          events={calendarEvents}
          height="auto"
        />
      </div>
    </div>
  );
};

const styles = {
  th: {
    border: "1px solid #ddd",
    padding: "8px",
    backgroundColor: "#f4f4f4",
    fontWeight: "bold",
  },
  td: {
    border: "1px solid #ddd",
    padding: "8px",
    textAlign: "center",
  },
};

export default MyTickets;
