import React, { useState } from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";
import { Link } from "react-router-dom";

function NewTicketForm() {
  const [formData, setFormData] = useState({
    sujet: "",
    type: "",
    urgence: "",
    description: "",
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError(null);

    try {
      const newTicket = {
        ...formData,
        id: Date.now(),
        date: new Date().toLocaleDateString(),
        statut: "Ouvert",
      };

      const response = await fetch("/addticket", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error(`Erreur HTTP ${response.status}`);
      }

      const storedTickets = JSON.parse(localStorage.getItem("tickets")) || [];
      storedTickets.push(newTicket);
      localStorage.setItem("tickets", JSON.stringify(storedTickets));

      window.location.href = "/MyTickets";
    } catch (error) {
      console.error("Erreur:", error);
      const storedTickets = JSON.parse(localStorage.getItem("tickets")) || [];
      storedTickets.push({
        ...formData,
        id: Date.now(),
        date: new Date().toLocaleDateString(),
        statut: "Erreur d'envoi - Sauvegard√© localement",
      });
      localStorage.setItem("tickets", JSON.stringify(storedTickets));

      setError(
        `√âchec de l'envoi au serveur. Ticket sauvegard√© localement. ${error.message}`
      );
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <>
      <IndexNavbar />
      <div className="min-h-screen bg-gradient-to-b from-gray-100 to-gray-300 py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-4xl mx-auto bg-white shadow-xl rounded-2xl overflow-hidden">
          <div className="grid grid-cols-1 md:grid-cols-2">
            <div className="p-8">
              <h2 className="text-2xl font-bold text-blue-900 mb-2">üé´ Cr√©er un ticket d'assistance</h2>
              <p className="text-gray-600 mb-6">
                Merci de remplir les informations n√©cessaires pour traiter votre demande.
              </p>

              {error && (
                <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
                  {error}
                </div>
              )}

              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label htmlFor="sujet" className="block text-blue-900 font-semibold mb-1">
                    Sujet
                  </label>
                  <input
                    type="text"
                    id="sujet"
                    name="sujet"
                    value={formData.sujet}
                    onChange={handleChange}
                    required
                    className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-600"
                    placeholder="Ex : Virement non effectu√©"
                  />
                </div>

                <div>
                  <label htmlFor="type" className="block text-blue-900 font-semibold mb-1">
                    Type
                  </label>
                  <select
                    id="type"
                    name="type"
                    value={formData.type}
                    onChange={handleChange}
                    required
                    className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-600"
                  >
                    <option value="">-- S√©lectionner --</option>
                    <option value="IT">IT - Informatique</option>
                    <option value="RH">RH - Ressources Humaines</option>
                    <option value="Comptabilit√©">Comptabilit√©</option>
                  </select>
                </div>

                <div>
                  <label htmlFor="urgence" className="block text-blue-900 font-semibold mb-1">
                    Urgence
                  </label>
                  <select
                    id="urgence"
                    name="urgence"
                    value={formData.urgence}
                    onChange={handleChange}
                    required
                    className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-600"
                  >
                    <option value="">-- Choisir --</option>
                    <option value="Urgent">Urgent</option>
                    <option value="Normal">Normal</option>
                    <option value="Faible">Faible priorit√©</option>
                  </select>
                </div>

                <div>
                  <label htmlFor="description" className="block text-blue-900 font-semibold mb-1">
                    Description
                  </label>
                  <textarea
                    id="description"
                    name="description"
                    rows="4"
                    value={formData.description}
                    onChange={handleChange}
                    required
                    className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-600 resize-vertical"
                    placeholder="D√©crivez le probl√®me rencontr√©..."
                  ></textarea>
                </div>

                <div className="flex items-center justify-between mt-6">
                  <Link to="/MyTickets" className="text-blue-800 font-semibold hover:underline">
                    ‚Üê Retour
                  </Link>
                  <button
                    type="submit"
                    disabled={isSubmitting}
                    className="bg-blue-900 text-white px-6 py-2 rounded-lg font-semibold hover:bg-blue-800 disabled:opacity-70"
                  >
                    {isSubmitting ? "Envoi en cours..." : "Soumettre"}
                  </button>
                </div>
              </form>
            </div>

            <div className="hidden md:block">
              <img
                src="https://images.unsplash.com/photo-1605902711622-cfb43c4437d4?auto=format&fit=crop&w=1000&q=80"
                alt="Assistance bancaire"
                className="w-full h-full object-cover"
              />
            </div>
          </div>
        </div>
      </div>
      <Footerr />
    </>
  );
}

export default NewTicketForm;
