import React, { useState, useEffect } from "react";
import bcrypt from 'bcryptjs';

export default function CardSettings() {
  const [userData, setUserData] = useState({
    nom: "",
    prenom: "",
    email: "",
    tel: "",
    password: "",
  });

  const [initialUserData, setInitialUserData] = useState(null);
  const [passwordData, setPasswordData] = useState({
    currentPassword: "",
    newPassword: "",
    confirmPassword: "",
  });
  const [errors, setErrors] = useState({}); // pour stocker les erreurs

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const parsedUser = JSON.parse(storedUser);
      const user = {
        nom: parsedUser.nom || "",
        prenom: parsedUser.prenom || "",
        email: parsedUser.email || "",
        tel: parsedUser.tel || "",
        password: parsedUser.password || "",
      };
      setUserData(user);
      setInitialUserData(user);
    }
  }, []);

  const handleUserChange = (e) => {
    setUserData({
      ...userData,
      [e.target.id]: e.target.value
    });
  };

  const handlePasswordChange = (e) => {
    setPasswordData({
      ...passwordData,
      [e.target.id]: e.target.value
    });
  };

  const handleCancel = () => {
    if (initialUserData) {
      setUserData(initialUserData);
    }
    setPasswordData({
      currentPassword: "",
      newPassword: "",
      confirmPassword: ""
    });
    setErrors({});
  };

  // Fonction pour valider le numéro de téléphone
  const validatePhoneNumber = (tel) => {
    const phoneRegex = /^[259]\d{7}$/; // Commence par 2,5,9 et total 8 chiffres
    return phoneRegex.test(tel);
  };

  // Fonction de validation des champs
  const validateFields = () => {
    const newErrors = {};

    // Vérification numéro de téléphone
    if (!validatePhoneNumber(userData.tel)) {
      newErrors.tel = "Numéro de téléphone incorrect.";
    }

    // Vérifications sur mot de passe
    if (!passwordData.currentPassword) {
      newErrors.currentPassword = "Veuillez entrer votre ancien mot de passe.";
    }

    if (!passwordData.newPassword) {
      newErrors.newPassword = "Veuillez entrer un nouveau mot de passe.";
    } else if (passwordData.newPassword.length < 6) {
      newErrors.newPassword = "Le nouveau mot de passe doit contenir au moins 6 caractères.";
    }

    if (!passwordData.confirmPassword) {
      newErrors.confirmPassword = "Veuillez confirmer votre nouveau mot de passe.";
    } else if (passwordData.newPassword !== passwordData.confirmPassword) {
      newErrors.confirmPassword = "Les mots de passe ne correspondent pas.";
    }

    return newErrors;
  };

  const handleSave = (e) => {
    e.preventDefault();
    const validationErrors = validateFields(); // <== utilise la nouvelle validation complète
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    bcrypt.compare(passwordData.currentPassword, userData.password, function (err, isMatch) {
      if (err) {
        console.error(err);
        setErrors({ currentPassword: "Erreur de vérification du mot de passe." });
        return;
      }

      if (!isMatch) {
        setErrors({ currentPassword: "Ancien mot de passe incorrect." });
      } else {
        // Succès : Tu peux continuer la sauvegarde ici
        alert("✅ Mot de passe modifié avec succès !");
        const updatedUserData = { ...userData, password: bcrypt.hashSync(passwordData.newPassword, 10) };
        setUserData(updatedUserData);
        setInitialUserData(updatedUserData);
        localStorage.setItem("user", JSON.stringify(updatedUserData));

        setPasswordData({
          currentPassword: "",
          newPassword: "",
          confirmPassword: ""
        });
        setErrors({});
      }
    });
  };

  return (
    <>
      <div className="relative flex flex-col min-w-0 break-words w-full max-w-[1000px] mb-6 shadow-lg rounded-lg bg-[var(--ubci-card-bg)] border border-[var(--ubci-gray-200)] overflow-hidden">
        <div className="rounded-t bg-white mb-0 px-6 py-6">
          <div className="text-center flex justify-between">
            <h6 className="text-blueGray-700 text-xl font-bold">Mon Compte</h6>
          </div>
        </div>

        <div className="flex-auto px-4 lg:px-10 py-10 pt-0 bg-blueGray-50 rounded-b-lg overflow-auto">
          <form>
            {/* Informations Personnelles */}
            <h6 className="text-blueGray-400 text-sm mt-3 mb-6 font-bold uppercase flex items-center">
            <svg
                className="h-5 w-5 text-lightBlue-600 mr-2"
                fill="currentColor"
                viewBox="0 0 24 24"
              >
                <path d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
              </svg>
              Informations Personnelles
            </h6>

            <div className="flex flex-wrap">
              {/* Nom */}
              <div className="w-full lg:w-6/12 px-4 mb-4">
                <label className="block font-semibold mb-1" htmlFor="nom">Nom</label>
                <input
                  type="text"
                  className="gp-form-input"
                  id="nom"
                  value={userData.nom}
                  onChange={handleUserChange}
                />
              </div>

              {/* Prénom */}
              <div className="w-full lg:w-6/12 px-4 mb-4">
                <label className="block font-semibold mb-1" htmlFor="prenom">Prénom</label>
                <input
                  type="text"
                  className="gp-form-input"
                  id="prenom"
                  value={userData.prenom}
                  onChange={handleUserChange}
                />
              </div>

              {/* Email */}
              <div className="w-full lg:w-6/12 px-4 mb-4">
                <label className="block font-semibold mb-1" htmlFor="email">Email</label>
                <input
                  type="email"
                  className="gp-form-input"
                  id="email"
                  value={userData.email}
                  onChange={handleUserChange}
                />
              </div>

              {/* Téléphone */}
              <div className="w-full lg:w-6/12 px-4 mb-4">
                <label className="block font-semibold mb-1" htmlFor="tel">Num Tel</label>
                <input
                  type="text"
                  className="gp-form-input"
                  id="tel"
                  value={userData.tel}
                  onChange={handleUserChange}
                />
                {errors.tel && (
                  <p className="error-message">{errors.tel}</p> // Affichage de l'erreur si elle existe
                )}
              </div>
            </div>

            <hr className="mt-6 border-b-1 border-blueGray-300" />

            {/* Changer mot de passe */}
            <h6 className="text-blueGray-400 text-sm mt-3 mb-6 font-bold uppercase flex items-center">
            <svg
                className="h-5 w-5 text-lightBlue-600 mr-2"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={3}
                  d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z"
                />
              </svg>
              Changer le mot de passe
            </h6>

            <div className="flex flex-wrap">
              {/* Ancien mot de passe */}
              <div className="w-full lg:w-6/12 px-4 mb-4">
                <label className="block font-semibold mb-1" htmlFor="currentPassword">Ancien mot de passe</label>
                <input
                  type="password"
                  className="gp-form-input"
                  id="currentPassword"
                  value={passwordData.currentPassword}
                  onChange={handlePasswordChange}
                />
                {errors.currentPassword && (
                  <p className="error-message">{errors.currentPassword}</p>
                )}
              </div>

              {/* Nouveau mot de passe */}
              <div className="w-full lg:w-6/12 px-4 mb-4">
                <label className="block font-semibold mb-1" htmlFor="newPassword">Nouveau mot de passe</label>
                <input
                  type="password"
                  className="gp-form-input"
                  id="newPassword"
                  value={passwordData.newPassword}
                  onChange={handlePasswordChange}
                />
                {errors.newPassword && (
                  <p className="error-message">{errors.newPassword}</p>
                )}
              </div>

              {/* Confirmer mot de passe */}
              <div className="w-full lg:w-6/12 px-4 mb-4">
                <label className="block font-semibold mb-1" htmlFor="confirmPassword">Confirmer mot de passe</label>
                <input
                  type="password"
                  className="gp-form-input"
                  id="confirmPassword"
                  value={passwordData.confirmPassword}
                  onChange={handlePasswordChange}
                />
                {errors.confirmPassword && (
                  <p className="error-message">{errors.confirmPassword}</p>
                )}
              </div>
            </div>

            {/* Boutons */}
            <div className="flex justify-end mt-4 gap-3">
              <button
                type="button"
                className="gp-btn gp-btn-cancel"
                onClick={handleCancel}
              >
                Annuler
              </button>
              <button
                type="submit"
                className="gp-btn gp-btn-save"
                onClick={handleSave}
              >
                Enregistrer
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Styles */}
      <style jsx>{`
        .gp-btn {
          padding: 0.5rem 1.25rem;
          border-radius: 0.375rem;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.2s ease;
        }
        .gp-btn-cancel {
          background-color: rgb(224, 224, 224);
          color: #374151;
        }
        .gp-btn-cancel:hover {
          background-color: #e5e7eb;
        }
        .gp-btn-save {
          background-color: #0ea5e9;
          color: white;
        }
        .gp-btn-save:hover {
          background-color: #0284c7;
        }
        .gp-form-input {
          width: 100%;
          padding: 0.5rem;
          border: 1px solid #ccc;
          border-radius: 0.375rem;
        }
        .error-message {
          color: red;
          font-size: 0.875rem;
          margin-top: 0.25rem;
        }
      `}</style>
    </>
  );
}
