import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faPen } from "@fortawesome/free-solid-svg-icons";
import { getAllUsers, addUser, updateUser, deleteUser } from "";

export default function CardTable({ color }) {
  const [users, setUsers] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newUser, setNewUser] = useState({
    nom: "",
    prenom: "",
    email: "",
    telephone: "",
    password: ""
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editIndex, setEditIndex] = useState(null);

  const generatePassword = () => {
    const length = 12;
    const charset = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*()";
    let password = "";
    for (let i = 0; i < length; i++) {
      const randomIndex = Math.floor(Math.random() * charset.length);
      password += charset[randomIndex];
    }
    return password;
  };

  const getUsers = async () => {
    try {
      const res = await getAllUsers();
      setUsers(res.data || []);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    getUsers();
  }, []);

  const handleInputChange = (e) => {
    setNewUser({ ...newUser, [e.target.name]: e.target.value });
  };

  const handleAddOrUpdateUser = async (e) => {
    e.preventDefault();

    try {
      if (isEditing && editIndex !== null) {
        const userToUpdate = users[editIndex];
        await updateUser(userToUpdate._id, newUser);
      } else {
        const password = generatePassword();
        await addUser({ ...newUser, password });
      }

      setNewUser({ nom: "", prenom: "", email: "", telephone: "", password: "" });
      setIsEditing(false);
      setEditIndex(null);
      setShowForm(false);
      getUsers();
    } catch (error) {
      console.error("Erreur lors de l'ajout ou de la modification :", error);
    }
  };

  const handleDeleteUser = async (index) => {
    const confirmDelete = window.confirm("Êtes-vous sûr de vouloir supprimer cet utilisateur ?");
    if (confirmDelete) {
      try {
        const userId = users[index]._id;
        await deleteUser(userId);
        getUsers();
      } catch (error) {
        console.error("Erreur lors de la suppression :", error);
      }
    }
  };

  const handleEditUser = (index) => {
    const { nom, prenom, email, tel, password } = users[index];
    setNewUser({ nom, prenom, email, telephone: tel, password });
    setIsEditing(true);
    setEditIndex(index);
    setShowForm(true);
  };

  return (
    <div className={`relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg ${color === "light" ? "bg-white" : "bg-lightBlue-900 text-white"}`}>
      <div className="flex justify-between items-center px-6 py-4 border-b border-gray-200">
        <h3 className={`font-bold text-xl ${color === "light" ? "text-blueGray-700" : "text-white"}`}>
          Liste des utilisateurs
        </h3>
        <button
          className="bg-emerald-500 hover:bg-emerald-600 text-white text-sm font-semibold px-4 py-2 rounded shadow"
          onClick={() => {
            setShowForm(!showForm);
            setNewUser({ nom: "", prenom: "", email: "", telephone: "", password: "" });
            setIsEditing(false);
          }}
        >
          {showForm && !isEditing ? "Annuler" : "Ajouter un utilisateur"}
        </button>
      </div>

      <div className="overflow-x-auto p-4">
        <table className="w-full text-sm text-left text-white">
          <thead className="text-xs uppercase bg-lightBlue-800 text-blueGray-100">
            <tr>
              <th className="px-6 py-3">Nom</th>
              <th className="px-6 py-3">Prénom</th>
              <th className="px-6 py-3">Email</th>
              <th className="px-6 py-3">Téléphone</th>
              <th className="px-6 py-3 text-right">Actions</th>
            </tr>
          </thead>
          <tbody className="bg-lightBlue-900 divide-y divide-blueGray-700">
            {users.map((user, index) => (
              <tr key={index} className="hover:bg-lightBlue-800">
                <td className="px-6 py-4">{user.nom}</td>
                <td className="px-6 py-4">{user.prenom}</td>
                <td className="px-6 py-4">{user.email}</td>
                <td className="px-6 py-4">{user.tel}</td>
                <td className="px-6 py-4 text-right">
                  <button
                    onClick={() => handleDeleteUser(index)}
                    className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded mr-2 text-xs shadow"
                  >
                    <FontAwesomeIcon icon={faTrash} />
                  </button>
                  <button
                    onClick={() => handleEditUser(index)}
                    className="bg-lightBlue-500 hover:bg-lightBlue-600 text-white px-3 py-1 rounded text-xs shadow"
                  >
                    <FontAwesomeIcon icon={faPen} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {showForm && (
          <form onSubmit={handleAddOrUpdateUser} className="mt-6 bg-lightBlue-900 text-white p-6 rounded shadow-md">
            <h4 className="text-lg font-semibold mb-4 text-white">
              {isEditing ? "Modifier l'utilisateur" : "Ajouter un nouvel utilisateur"}
            </h4>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mb-4">
              <div>
                <label className="block text-sm mb-1">Nom</label>
                <input
                  type="text"
                  name="nom"
                  value={newUser.nom}
                  onChange={handleInputChange}
                  className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 text-white rounded"
                  required
                />
              </div>
              <div>
                <label className="block text-sm mb-1">Prénom</label>
                <input
                  type="text"
                  name="prenom"
                  value={newUser.prenom}
                  onChange={handleInputChange}
                  className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 text-white rounded"
                  required
                />
              </div>
              <div>
                <label className="block text-sm mb-1">Email</label>
                <input
                  type="email"
                  name="email"
                  value={newUser.email}
                  onChange={handleInputChange}
                  className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 text-white rounded"
                  required
                />
              </div>
              <div>
                <label className="block text-sm mb-1">Téléphone</label>
                <input
                  type="text"
                  name="telephone"
                  value={newUser.telephone}
                  onChange={handleInputChange}
                  className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 text-white rounded"
                  required
                />
              </div>
              {isEditing && (
                <div>
                  <label className="block text-sm mb-1">Mot de passe</label>
                  <input
                    type="text"
                    name="password"
                    value={newUser.password}
                    onChange={handleInputChange}
                    className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 text-white rounded"
                  />
                </div>
              )}
            </div>
            <div className="flex justify-end">
              <button type="submit" className="bg-emerald-500 hover:bg-emerald-600 text-white text-sm font-semibold px-6 py-2 rounded shadow">
                {isEditing ? "Modifier" : "Ajouter"}
              </button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
}

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
