import React, { useState, useEffect } from "react";
import CryptoJS from "crypto-js";  // Importation de la librairie

export default function CardSettings() {
  const [userData, setUserData] = useState({
    nom: "",
    prenom: "",
    email: "",
    tel: ""
  });

  const [initialUserData, setInitialUserData] = useState(null); // pour stocker l'état initial

  const [passwordData, setPasswordData] = useState({
    currentPassword: "",
    newPassword: "",
    confirmPassword: ""
  });
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const parsedUser = JSON.parse(storedUser);
      
      // Vérifier le mot de passe dans le localStorage avant de le déchiffrer
      console.log("Mot de passe chiffré : ", parsedUser.password);
  
      if (parsedUser.password) {
        // Déchiffrement du mot de passe
        const bytes = CryptoJS.AES.decrypt(parsedUser.password, 'secret-key');
        const originalPassword = bytes.toString(CryptoJS.enc.Utf8);
        
        // Vérifier si le mot de passe a bien été déchiffré
        console.log("Mot de passe déchiffré : ", originalPassword);
        
        const user = {
          nom: parsedUser.nom || "",
          prenom: parsedUser.prenom || "",
          email: parsedUser.email || "",
          tel: parsedUser.tel || "",
          password: originalPassword || ""  // Le mot de passe déchiffré
        };
  
        setUserData(user);
        setInitialUserData(user); // stocker l'état initial
      }
    }
  }, []);
  

  const handleUserChange = (e) => {
    setUserData({
      ...userData,
      [e.target.id]: e.target.value
    });
  };

  const handlePasswordChange = (e) => {
    setPasswordData({
      ...passwordData,
      [e.target.id]: e.target.value
    });
  };

  const handleCancel = () => {
    if (initialUserData) {
      setUserData(initialUserData); // remettre les données initiales
    }
    setPasswordData({
      currentPassword: "",
      newPassword: "",
      confirmPassword: ""
    }); // vider aussi les champs mot de passe
  };

 const handleSave = (e) => {
  e.preventDefault();

  if (passwordData.newPassword === passwordData.confirmPassword) {
    // Chiffrement du nouveau mot de passe avant de le stocker
    const encryptedPassword = CryptoJS.AES.encrypt(passwordData.newPassword, 'secret-key').toString();

    // Mise à jour des données de l'utilisateur
    const updatedUserData = {
      ...userData,
      password: encryptedPassword
    };

    // Stockage des données dans le localStorage
    localStorage.setItem("user", JSON.stringify(updatedUserData));

    // Alerta le mot de passe déchiffré
    const bytes = CryptoJS.AES.decrypt(encryptedPassword, 'secret-key');
    const decryptedPassword = bytes.toString(CryptoJS.enc.Utf8);

    // Vérifier le mot de passe déchiffré avant de l'afficher
    console.log("Mot de passe déchiffré après l'enregistrement : ", decryptedPassword);

    // Affichage de l'alerte avec le mot de passe déchiffré
    alert(`Mot de passe déchiffré : ${decryptedPassword}`);

    // Réinitialiser les champs
    setPasswordData({
      currentPassword: "",
      newPassword: "",
      confirmPassword: ""
    });
  } else {
    alert("Les mots de passe ne correspondent pas.");
  }
};


  return (
    <>
      <div className="relative flex flex-col min-w-0 break-words w-full max-w-[1000px] mb-6 shadow-lg rounded-lg bg-[var(--ubci-card-bg)] border border-[var(--ubci-gray-200)] overflow-hidden">
        <div className="rounded-t bg-white mb-0 px-6 py-6">
          <div className="text-center flex justify-between">
            <h6 className="text-blueGray-700 text-xl font-bold">My Account</h6>
          </div>
        </div>

        <div className="flex-auto px-4 lg:px-10 py-10 pt-0 bg-blueGray-50 rounded-b-lg overflow-auto">
          <form>
            {/* Informations Personnelles */}
            <h6 className="text-blueGray-400 text-sm mt-3 mb-6 font-bold uppercase flex items-center">
              {/* icon */} Informations Personnelles
            </h6>

            {/* Inputs for userData */}
            <div className="flex flex-wrap">
              {/* Nom */}
              <div className="w-full lg:w-6/12 px-4">
                <label className="block font-semibold mb-1" htmlFor="nom">Nom</label>
                <input
                  type="text"
                  className="gp-form-input"
                  id="nom"
                  value={userData.nom}
                  onChange={handleUserChange}
                />
              </div>

              {/* Prénom */}
              <div className="w-full lg:w-6/12 px-4">
                <label className="block font-semibold mb-1" htmlFor="prenom">Prénom</label>
                <input
                  type="text"
                  className="gp-form-input"
                  id="prenom"
                  value={userData.prenom}
                  onChange={handleUserChange}
                />
              </div>

              {/* Email */}
              <div className="w-full lg:w-6/12 px-4">
                <label className="block font-semibold mb-1" htmlFor="email">Email Address</label>
                <input
                  type="email"
                  className="gp-form-input"
                  id="email"
                  value={userData.email}
                  onChange={handleUserChange}
                />
              </div>

              {/* Téléphone */}
              <div className="w-full lg:w-6/12 px-4">
                <label className="block font-semibold mb-1" htmlFor="tel">Num Tel</label>
                <input
                  type="text"
                  className="gp-form-input"
                  id="tel"
                  value={userData.tel}
                  onChange={handleUserChange}
                />
              </div>
            </div>

            <hr className="mt-6 border-b-1 border-blueGray-300" />

            {/* Changer mot de passe */}
            <h6 className="text-blueGray-400 text-sm mt-3 mb-6 font-bold uppercase flex items-center">
              {/* icon */} Changer le mot de passe
            </h6>

            <div className="flex flex-wrap">
              {/* Ancien mot de passe */}
              <div className="w-full lg:w-6/12 px-4">
                <label className="block font-semibold mb-1" htmlFor="currentPassword">Ancien mot de passe</label>
                <input
                  type="password"
                  className="gp-form-input"
                  id="currentPassword"
                  value={passwordData.currentPassword}
                  onChange={handlePasswordChange}
                  placeholder="Ancien mot de passe"
                />
              </div>

              {/* Nouveau mot de passe */}
              <div className="w-full lg:w-6/12 px-4">
                <label className="block font-semibold mb-1" htmlFor="newPassword">Nouveau mot de passe</label>
                <input
                  type="password"
                  className="gp-form-input"
                  id="newPassword"
                  value={passwordData.newPassword}
                  onChange={handlePasswordChange}
                  placeholder="Nouveau mot de passe"
                />
              </div>
            </div>

            {/* Boutons */}
            <div className="flex justify-end mt-4" style={{ gap: "12px" }}>
              <button
                type="button"
                className="gp-btn gp-btn-cancel"
                onClick={handleCancel}
              >
                Annuler
              </button>
              <button
                type="submit"
                className="gp-btn gp-btn-save"
                onClick={handleSave}
              >
                Enregistrer
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Styles */}
      <style jsx> {
        `.gp-btn {
          padding: 0.5rem 1.25rem;
          border-radius: 0.375rem;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.2s ease;
        }
        .gp-btn-cancel {
          background-color: rgb(224, 224, 224);
          color: #374151;
        }
        .gp-btn-cancel:hover {
          background-color: #e5e7eb;
        }
        .gp-btn-save {
          background-color: #0ea5e9;
          color: white;
        }
        .gp-btn-save:hover {
          background-color: #0284c7;
        }
        .gp-form-input {
          width: 100%;
          padding: 0.5rem;
          border: 1px solid #ccc;
          border-radius: 0.375rem;
        }`}</style>
    </>
  );
}
