import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faPen, faPlus } from '@fortawesome/free-solid-svg-icons';
import { getAllUsers } from "services/ApiUser";

export default function CardTable({ color }) {
  const [users, setUsers] = useState([]);

  const getUsers = async () => {
    try {
      await getAllUsers().then((res) => {
        setUsers(res.data.userList);
      });
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => { getUsers(); }, []);

  return (
    <div className={`relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg 
      ${color === "light" ? "bg-white" : "bg-slate-800 text-white"}`}>

      <div className="rounded-t mb-0 px-4 py-3 border-0">
        <div className="flex flex-wrap items-center justify-between">
          <h3 className={`font-semibold text-xl ${color === "light" ? "text-slate-700" : "text-white"}`}>
            Users Management
          </h3>
          <button className="bg-emerald-500 text-white active:bg-emerald-600 font-bold uppercase text-xs px-4 py-2 rounded shadow hover:shadow-md outline-none focus:outline-none ease-linear transition-all duration-150">
            <FontAwesomeIcon icon={faPlus} className="mr-2" />
            Add User
          </button>
        </div>
      </div>

      <div className="block w-full overflow-x-auto">
        <table className="items-center w-full bg-transparent border-collapse">
          <thead>
            <tr>
              {["Nom", "Prénom", "Email", "ID", "Actions"].map((header, index) => (
                <th key={index} className={`px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left 
                  ${color === "light" 
                    ? "bg-slate-50 text-slate-500 border-slate-100" 
                    : "bg-slate-700 text-slate-200 border-slate-600"}`}>
                  {header}
                </th>
              ))}
            </tr>
          </thead>

          <tbody>
            {users.map((user, index) => (
              <tr key={user.id} className={`hover:${color === "light" ? "bg-slate-50" : "bg-slate-700"} transition-colors`}>
                <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-sm p-4">
                  {user.nom}
                </td>
                <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-sm p-4">
                  {user.prenom}
                </td>
                <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-sm p-4">
                  {user.email}
                </td>
                <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-sm p-4">
                  <span className={`px-2 py-1 rounded-full ${color === "light" ? "bg-slate-200" : "bg-slate-600"}`}>
                    #{user.id}
                  </span>
                </td>
                <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-sm p-4 space-x-2">
                  <button className="text-blue-500 hover:text-blue-700 transition-colors">
                    <FontAwesomeIcon icon={faPen} className="text-sm" />
                  </button>
                  <button className="text-red-500 hover:text-red-700 transition-colors">
                    <FontAwesomeIcon icon={faTrash} className="text-sm" />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

CardTable.defaultProps = {
  color: "dark",
};

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
