import React, { useState } from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";
import { Link } from "react-router-dom";

function NewTicketForm() {
  const [formData, setFormData] = useState({
    sujet: "",
    departement: "",
    type: "",
    description: "",
    start: "",
    end: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const newTicket = {
      ...formData,
      id: Date.now(),
      date: new Date().toLocaleDateString(),
      statut: "Ouvert",
      start: new Date(formData.start),
      end: new Date(formData.end),
    };

    const storedTickets = JSON.parse(localStorage.getItem("tickets")) || [];
    storedTickets.push(newTicket);
    localStorage.setItem("tickets", JSON.stringify(storedTickets));

    window.location.href = "/MyTickets"; 
  };

  return (
    <>
      <IndexNavbar />
      <div style={container}>
        <div style={card}>
          <div style={leftSide}>
            <h2 style={title}>üé´ Cr√©er un ticket d‚Äôassistance</h2>
            <p style={subtitle}>Merci de remplir les informations n√©cessaires pour traiter votre demande.</p>

            <form style={form} onSubmit={handleSubmit}>
              <div style={inputGroup}>
                <label htmlFor="sujet" style={label}>Sujet</label>
                <input
                  type="text"
                  id="sujet"
                  name="sujet"
                  value={formData.sujet}
                  onChange={handleChange}
                  placeholder="Ex : Virement non effectu√©"
                  style={input}
                  required
                />
              </div>

              <div style={inputGroup}>
                <label htmlFor="departement" style={label}>D√©partement</label>
                <select
                  id="departement"
                  name="departement"
                  value={formData.departement}
                  onChange={handleChange}
                  style={input}
                  required
                >
                  <option value="">-- S√©lectionner --</option>
                  <option value="IT">IT - Informatique</option>
                  <option value="RH">RH - Ressources Humaines</option>
                  <option value="Comptabilit√©">Comptabilit√©</option>
                </select>
              </div>

              <div style={inputGroup}>
                <label htmlFor="type" style={label}>Priorit√©</label>
                <select
                  id="type"
                  name="type"
                  value={formData.type}
                  onChange={handleChange}
                  style={input}
                  required
                >
                  <option value="">-- Choisir --</option>
                  <option value="Urgent">Urgent</option>
                  <option value="Normal">Normal</option>
                  <option value="Faible">Faible priorit√©</option>
                </select>
              </div>

              <div style={inputGroup}>
                <label htmlFor="start" style={label}>Date de d√©but</label>
                <input
                  type="datetime-local"
                  id="start"
                  name="start"
                  value={formData.start}
                  onChange={handleChange}
                  style={input}
                  required
                />
              </div>

              <div style={inputGroup}>
                <label htmlFor="end" style={label}>Date de fin</label>
                <input
                  type="datetime-local"
                  id="end"
                  name="end"
                  value={formData.end}
                  onChange={handleChange}
                  style={input}
                  required
                />
              </div>

              <div style={buttonGroup}>
                <Link to="/MyTickets" style={backLink}>‚Üê Retour</Link>
                <button type="submit" style={submitButton}>Soumettre</button>
              </div>
            </form>
          </div>

          <div style={rightSide}>
            <img
              src="https://images.unsplash.com/photo-1605902711622-cfb43c4437d4?auto=format&fit=crop&w=1000&q=80"
              alt="Assistance bancaire"
              style={imageStyle}
            />
          </div>
        </div>
      </div>

      <Footerr />
    </>
  );
}

// üåü Styles sobres et bancaires
const container = {
  backgroundColor: "#f1f3f6",
  minHeight: "100vh",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  padding: "2rem",
};

const card = {
  display: "flex",
  flexDirection: "row",
  backgroundColor: "#fff",
  borderRadius: "16px",
  boxShadow: "0 8px 24px rgba(0, 0, 0, 0.07)",
  overflow: "hidden",
  maxWidth: "1080px",
  width: "100%",
};

const leftSide = {
  flex: 1,
  padding: "2.5rem",
};

const rightSide = {
  flex: 1,
  backgroundColor: "#e3e9f1",
};

const imageStyle = {
  width: "100%",
  height: "100%",
  objectFit: "cover",
};

const title = {
  fontSize: "1.8rem",
  fontWeight: "700",
  color: "#003366",
  marginBottom: "1rem",
};

const subtitle = {
  fontSize: "1rem",
  color: "#555",
  marginBottom: "2rem",
};

const form = {
  display: "flex",
  flexDirection: "column",
  gap: "1.2rem",
};

const inputGroup = {
  display: "flex",
  flexDirection: "column",
};

const label = {
  fontWeight: "600",
  marginBottom: "0.5rem",
  color: "#003366",
};

const input = {
  padding: "0.8rem",
  fontSize: "1rem",
  borderRadius: "8px",
  border: "1px solid #ccc",
  backgroundColor: "#f9f9f9",
};

const buttonGroup = {
  marginTop: "1.5rem",
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
};

const submitButton = {
  backgroundColor: "#003366",
  color: "#fff",
  border: "none",
  padding: "0.8rem 1.6rem",
  fontSize: "1rem",
  borderRadius: "8px",
  cursor: "pointer",
  fontWeight: "600",
};

const backLink = {
  color: "#003366",
  textDecoration: "none",
  fontWeight: "500",
};

export default NewTicketForm;
