import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";

export default function NewTicketForm() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    sujet: "",
    departement: "",
    priorite: "",
    description: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    const newTicket = {
      ...formData,
      id: Date.now(),
      statut: "Ouvert",
      date: new Date().toLocaleString('fr-FR', {
        day: '2-digit',
        month: '2-digit',
        year: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      })
    };

    const tickets = JSON.parse(localStorage.getItem('tickets') || '[]');
    localStorage.setItem('tickets', JSON.stringify([...tickets, newTicket]));
    
    navigate("/MyTickets", { replace: true });
  };

  return (
    <>
      <IndexNavbar />

      <div style={styles.container}>
        <div style={styles.card}>
          <div style={styles.leftSide}>
            <h2 style={styles.title}>ðŸŽ« CrÃ©er un nouveau ticket</h2>
            
            <form onSubmit={handleSubmit} style={styles.form}>
              <div style={styles.inputGroup}>
                <label>Sujet</label>
                <input
                  type="text"
                  name="sujet"
                  value={formData.sujet}
                  onChange={handleChange}
                  placeholder="Exemple : ProblÃ¨me de connexion"
                  required
                />
              </div>

              <div style={styles.inputGroup}>
                <label>DÃ©partement</label>
                <select
                  name="departement"
                  value={formData.departement}
                  onChange={handleChange}
                  required
                >
                  <option value="">SÃ©lectionnez un dÃ©partement</option>
                  <option value="IT">Support Technique</option>
                  <option value="RH">Ressources Humaines</option>
                  <option value="ComptabilitÃ©">Service Comptable</option>
                </select>
              </div>

              <div style={styles.inputGroup}>
                <label>PrioritÃ©</label>
                <select
                  name="priorite"
                  value={formData.priorite}
                  onChange={handleChange}
                  required
                >
                  <option value="">Choisir une prioritÃ©</option>
                  <option value="Urgent">ðŸ”´ Urgent</option>
                  <option value="Normal">ðŸŸ  Normal</option>
                  <option value="Faible">ðŸŸ¢ Faible</option>
                </select>
              </div>

              <div style={styles.inputGroup}>
                <label>Description</label>
                <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
                  rows="5"
                  placeholder="DÃ©crivez votre problÃ¨me en dÃ©tails..."
                  required
                />
              </div>

              <div style={styles.buttonContainer}>
                <Link to="/MyTickets" style={styles.cancelButton}>
                  Annuler
                </Link>
                <button type="submit" style={styles.submitButton}>
                  Envoyer le ticket
                </button>
              </div>
            </form>
          </div>

          <div style={styles.rightSide}>
            <img
              src="https://img.freepik.com/vecteurs-livre/equipe-support-client_52683-37424.jpg"
              alt="Support client"
              style={styles.image}
            />
          </div>
        </div>
      </div>

      <Footerr />
    </>
  );
}

const styles = {
  container: {
    minHeight: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#f8f9fa',
    padding: '20px'
  },
  card: {
    display: 'flex',
    maxWidth: '1200px',
    width: '100%',
    backgroundColor: 'white',
    borderRadius: '15px',
    boxShadow: '0 5px 15px rgba(0,0,0,0.1)'
  },
  leftSide: {
    flex: 1,
    padding: '40px'
  },
  rightSide: {
    flex: 1,
    background: '#f1f3f5',
    borderLeft: '1px solid #eee'
  },
  title: {
    color: '#2c3e50',
    marginBottom: '30px',
    fontSize: '28px'
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '25px'
  },
  inputGroup: {
    display: 'flex',
    flexDirection: 'column',
    gap: '8px',
    
    '& label': {
      fontWeight: '600',
      color: '#34495e'
    },
    
    '& input, & select, & textarea': {
      padding: '12px 15px',
      border: '1px solid #ddd',
      borderRadius: '8px',
      fontSize: '16px',
      transition: 'all 0.3s',
      
      '&:focus': {
        borderColor: '#3498db',
        boxShadow: '0 0 0 3px rgba(52,152,219,0.2)',
        outline: 'none'
      }
    },
    
    '& textarea': {
      resize: 'vertical',
      minHeight: '120px'
    }
  },
  buttonContainer: {
    display: 'flex',
    justifyContent: 'space-between',
    marginTop: '30px',
    gap: '20px'
  },
  cancelButton: {
    padding: '12px 25px',
    backgroundColor: '#e74c3c',
    color: 'white',
    borderRadius: '8px',
    textDecoration: 'none',
    textAlign: 'center',
    flex: 1
  },
  submitButton: {
    padding: '12px 25px',
    backgroundColor: '#2ecc71',
    color: 'white',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    fontSize: '16px',
    flex: 2,
    transition: 'background 0.3s',
    
    '&:hover': {
      backgroundColor: '#27ae60'
    }
  },
  image: {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
    borderTopRightRadius: '15px',
    borderBottomRightRadius: '15px'
  }
};
