import React, { useEffect, useState } from 'react'
import { Calendar, dateFnsLocalizer } from 'react-big-calendar'
import format from 'date-fns/format'
import parse from 'date-fns/parse'
import startOfWeek from 'date-fns/startOfWeek'
import getDay from 'date-fns/getDay'
import enUS from 'date-fns/locale/en-US'
import 'react-big-calendar/lib/css/react-big-calendar.css'

const locales = { 'en-US': enUS }

const localizer = dateFnsLocalizer({
  format,
  parse,
  startOfWeek,
  getDay,
  locales,
})

const convertTicketsToEvents = (tickets) => {
  return tickets.map((ticket) => {
    const start = new Date(ticket.date)
    const end = new Date(ticket.date)
    end.setHours(start.getHours() + 1)

    return {
      title: ticket.sujet,
      type: ticket.type,
      start,
      end,
    }
  })
}

const eventStyleGetter = (event) => {
  let backgroundColor = '#3174ad'
  if (event.type === 'RÃ©clamation' || event.type === 'Urgent') backgroundColor = '#e74c3c'
  if (event.type === 'Demande' || event.type === 'Normal') backgroundColor = '#27ae60'
  if (event.type === 'Faible') backgroundColor = '#f39c12'

  return {
    style: {
      backgroundColor,
      color: 'white',
      borderRadius: '6px',
      border: 'none',
      padding: '4px',
      fontSize: '14px',
    },
  }
}

const Calender = () => {
  const [events, setEvents] = useState([])

  useEffect(() => {
    const storedTickets = JSON.parse(localStorage.getItem("tickets")) || []
    const eventsFromTickets = convertTicketsToEvents(storedTickets)
    setEvents(eventsFromTickets)
  }, [])

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      minHeight: '100vh',
      fontFamily: 'Segoe UI, sans-serif',
      backgroundColor: '#f4f6f9',
      padding: '20px',
    }}>
      <div style={{ flexGrow: 1 }}>
        <h2 style={{ fontSize: '24px', color: '#2c3e50' }}>ðŸ“… Calendrier des Tickets STB</h2>
        <p style={{ color: "#555", marginBottom: "1rem" }}>
          Chaque ticket soumis apparaÃ®t automatiquement ici avec sa prioritÃ©.
        </p>
      </div>

      <div style={{
        backgroundColor: '#fff',
        padding: '15px',
        borderRadius: '10px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        marginBottom: '80px',
      }}>
        <Calendar
          localizer={localizer}
          events={events}
          startAccessor="start"
          endAccessor="end"
          tooltipAccessor={(event) => `${event.title} (${event.type})`}
          eventPropGetter={eventStyleGetter}
          style={{ height: 700 }}
        />
      </div>
    </div>
  )
}

export default Calender
