import React, { useState } from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";
import { Link } from "react-router-dom";

function NewTicketForm() {
  const [formData, setFormData] = useState({
    sujet: "",
    type: "",
    urgence: "",
    description: "",
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError(null);

    try {
      const newTicket = {
        ...formData,
        id: Date.now(),
        date: new Date().toLocaleDateString(),
        statut: "Ouvert",
      };

      const response = await fetch("/addticket", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) throw new Error(`Erreur HTTP ${response.status}`);

      const storedTickets = JSON.parse(localStorage.getItem("tickets")) || [];
      storedTickets.push(newTicket);
      localStorage.setItem("tickets", JSON.stringify(storedTickets));

      window.location.href = "/MyTickets";
    } catch (error) {
      console.error("Erreur:", error);
      const storedTickets = JSON.parse(localStorage.getItem("tickets")) || [];
      storedTickets.push({
        ...formData,
        id: Date.now(),
        date: new Date().toLocaleDateString(),
        statut: "Erreur d'envoi - Sauvegard√© localement",
      });
      localStorage.setItem("tickets", JSON.stringify(storedTickets));

      setError(
        `√âchec de l'envoi au serveur. Ticket sauvegard√© localement. ${error.message}`
      );
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <>
      <IndexNavbar />
      
      {/* Int√©gration des polices directement dans le composant */}
      <style>
        {`
          @import url('https://fonts.googleapis.com/css2?family=Playfair+Display:wght@600&family=Roboto:wght@400;500;700&display=swap');
        `}
      </style>

      <div style={container}>
        <div style={card}>
          <div style={leftSide}>
            <div style={header}>
              <h2 style={title}>
                <span style={titleIcon}>üì®</span> Nouveau Ticket d'Assistance
              </h2>
              <p style={subtitle}>
                Service Client √âlectronique - Banque Priv√©e de Gen√®ve
              </p>
            </div>

            {error && <div style={errorMessage}>{error}</div>}

            <form style={form} onSubmit={handleSubmit}>
              <div style={inputGroup}>
                <label htmlFor="sujet" style={label}>
                  Sujet
                </label>
                <input
                  type="text"
                  id="sujet"
                  name="sujet"
                  value={formData.sujet}
                  onChange={handleChange}
                  placeholder="Ex : Virement non effectu√©"
                  style={input}
                  required
                />
              </div>

              <div style={inputGroup}>
                <label htmlFor="type" style={label}>
                  Type
                </label>
                <select
                  id="type"
                  name="type"
                  value={formData.type}
                  onChange={handleChange}
                  style={input}
                  required
                >
                  <option value="">-- S√©lectionner --</option>
                  <option value="IT">IT - Informatique</option>
                  <option value="RH">RH - Ressources Humaines</option>
                  <option value="Comptabilit√©">Comptabilit√©</option>
                </select>
              </div>

              <div style={inputGroup}>
                <label htmlFor="urgence" style={label}>
                  Urgence
                </label>
                <select
                  id="urgence"
                  name="urgence"
                  value={formData.urgence}
                  onChange={handleChange}
                  style={input}
                  required
                >
                  <option value="">-- Choisir --</option>
                  <option value="Urgent">Urgent</option>
                  <option value="Normal">Normal</option>
                  <option value="Faible">Faible priorit√©</option>
                </select>
              </div>

              <div style={inputGroup}>
                <label htmlFor="description" style={label}>
                  Description
                </label>
                <textarea
                  id="description"
                  name="description"
                  rows="4"
                  value={formData.description}
                  onChange={handleChange}
                  placeholder="D√©crivez le probl√®me rencontr√©..."
                  style={textarea}
                  required
                ></textarea>
              </div>

              <div style={buttonGroup}>
                <Link to="/MyTickets" style={backLink}>
                  ‚Üê Retour aux tickets
                </Link>
                <button
                  type="submit"
                  style={submitButton}
                  disabled={isSubmitting}
                >
                  {isSubmitting ? "Envoi en cours..." : "Soumettre le ticket"}
                </button>
              </div>
            </form>
          </div>

          <div style={rightSide}>
            <div style={overlay}></div>
            <img
              src="https://images.unsplash.com/photo-1604594849809-dfedbc827105?ixlib=rb-1.2.1&auto=format&fit=crop&w=1950&q=80"
              alt="Assistance bancaire s√©curis√©e"
              style={imageStyle}
            />
            <div style={sideContent}>
              <div style={badge}>BPG-2024</div>
              <h3 style={sideTitle}>Assistance S√©curis√©e</h3>
              <p style={sideText}>
                Toutes les communications sont chiffr√©es selon les standards
                bancaires suisses (norme FINMA) et prot√©g√©es par notre syst√®me
                de s√©curit√© de niveau militaire.
              </p>
            </div>
          </div>
        </div>
      </div>
      <Footerr />
    </>
  );
}

// Styles avec polices int√©gr√©es
const container = {
  backgroundColor: "#f8f9fa",
  minHeight: "100vh",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  padding: "2rem",
  fontFamily: "'Roboto', sans-serif",
};

const card = {
  display: "flex",
  backgroundColor: "#fff",
