import React, { useState, useEffect } from 'react';
import { Pencil, Trash2, Plus, Search, Filter } from 'lucide-react';

const DepartmentTable = () => {
  const [departments, setDepartments] = useState([
    { id: '68094df1...', name: 'Toumi' }
  ]);
  const [searchTerm, setSearchTerm] = useState('');

  const handleDelete = (id) => {
    setDepartments(departments.filter(dep => dep.id !== id));
  };

  const handleEdit = (id) => {
    console.log("Éditer département:", id);
    // Implémente ici la navigation vers la page d'édition ou un modal
  };

  const handleAdd = () => {
    console.log("Ajouter un département");
    // Implémente ici l’ajout d’un nouveau département
  };

  const filteredDepartments = departments.filter(dep =>
    dep.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    dep.id.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="p-6 bg-white rounded-xl shadow-md">
      <h2 className="text-2xl font-bold mb-4">Gestion des Départements</h2>

      <div className="flex items-center gap-4 mb-4">
        <div className="relative flex-1">
          <input
            type="text"
            placeholder="Rechercher par nom ou ID"
            className="w-full border rounded-lg px-4 py-2 pl-10"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <Search className="absolute left-3 top-2.5 text-gray-400" size={20} />
        </div>
        <button className="border rounded-lg px-4 py-2 flex items-center gap-2">
          <Filter size={16} />
          Filtrer par
        </button>
        <button
          onClick={handleAdd}
          className="bg-teal-600 hover:bg-teal-700 text-white rounded-lg px-4 py-2 flex items-center gap-2"
        >
          <Plus size={16} />
          Ajouter
        </button>
      </div>

      <table className="w-full text-left">
        <thead className="bg-gray-100">
          <tr>
            <th className="p-3">ID</th>
            <th className="p-3">DÉPARTEMENT</th>
            <th className="p-3">ACTIONS</th>
          </tr>
        </thead>
        <tbody>
          {filteredDepartments.map(dep => (
            <tr key={dep.id} className="border-t">
              <td className="p-3">{dep.id}</td>
              <td className="p-3">
                <span className="bg-green-100 text-green-700 px-3 py-1 rounded-full text-sm">
                  {dep.name}
                </span>
              </td>
              <td className="p-3 flex gap-3">
                <button onClick={() => handleEdit(dep.id)} className="text-blue-600 hover:text-blue-800">
                  <Pencil size={20} />
                </button>
                <button onClick={() => handleDelete(dep.id)} className="text-red-600 hover:text-red-800">
                  <Trash2 size={20} />
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <p className="text-sm text-gray-500 mt-4">
        Affichage de {filteredDepartments.length} sur {departments.length} départements
      </p>
    </div>
  );
};

export default DepartmentTable;
