import React, { useState } from "react";
import { Link } from "react-router-dom";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";

function NewTicketCheckout() {
  const [formData, setFormData] = useState({
    sujet: "",
    type: "",
    urgence: "",
    description: "",
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError(null);

    try {
      const newTicket = {
        ...formData,
        id: Date.now(),
        date: new Date().toLocaleDateString(),
        statut: "Ouvert",
      };

      const response = await fetch("/addticket", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) throw new Error(`Erreur HTTP ${response.status}`);

      const storedTickets = JSON.parse(localStorage.getItem("tickets")) || [];
      storedTickets.push(newTicket);
      localStorage.setItem("tickets", JSON.stringify(storedTickets));

      window.location.href = "/MyTickets";
    } catch (error) {
      const storedTickets = JSON.parse(localStorage.getItem("tickets")) || [];
      storedTickets.push({
        ...formData,
        id: Date.now(),
        date: new Date().toLocaleDateString(),
        statut: "Erreur d'envoi - Sauvegardé localement",
      });
      localStorage.setItem("tickets", JSON.stringify(storedTickets));
      setError(`Échec de l'envoi. ${error.message}`);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <>
      <IndexNavbar />
      <div style={styles.container}>
        <div style={styles.card}>
          <div style={styles.summary}>
            <h2 style={styles.sectionTitle}>Résumé du ticket</h2>
            <p><strong>Support technique :</strong> Inclus</p>
            <p><strong>Type :</strong> {formData.type || "Non défini"}</p>
            <p><strong>Urgence :</strong> {formData.urgence || "Non définie"}</p>
            <p><strong>Sujet :</strong> {formData.sujet || "Non défini"}</p>
          </div>
          <form style={styles.form} onSubmit={handleSubmit}>
            <h2 style={styles.sectionTitle}>Créer un ticket</h2>
            {error && <div style={styles.error}>{error}</div>}

            <label style={styles.label}>Sujet</label>
            <input type="text" name="sujet" value={formData.sujet} onChange={handleChange} style={styles.input} required />

            <label style={styles.label}>Type</label>
            <select name="type" value={formData.type} onChange={handleChange} style={styles.input} required>
              <option value="">-- Sélectionner --</option>
              <option value="IT">Informatique</option>
              <option value="RH">Ressources Humaines</option>
              <option value="Comptabilité">Comptabilité</option>
            </select>

            <label style={styles.label}>Urgence</label>
            <select name="urgence" value={formData.urgence} onChange={handleChange} style={styles.input} required>
              <option value="">-- Choisir --</option>
              <option value="Urgent">Urgent</option>
              <option value="Normal">Normal</option>
              <option value="Faible">Faible</option>
            </select>

            <label style={styles.label}>Description</label>
            <textarea name="description" value={formData.description} onChange={handleChange} rows="4" style={styles.textarea} required />

            <div style={styles.actions}>
              <Link to="/MyTickets" style={styles.link}>← Retour</Link>
              <button type="submit" style={styles.submitBtn} disabled={isSubmitting}>
                {isSubmitting ? "Soumission..." : "Soumettre"}
              </button>
            </div>
          </form>
        </div>
      </div>
      <Footerr />
    </>
  );
}

const styles = {
  container: {
    backgroundColor: "#f5f7fa",
    minHeight: "100vh",
    display: "flex",
    justifyContent: "center",
    padding: "3rem",
  },
  card: {
    backgroundColor: "#fff",
    borderRadius: "12px",
    boxShadow: "0 4px 16px rgba(0, 0, 0, 0.1)",
    display: "flex",
    maxWidth: "1000px",
    width: "100%",
    overflow: "hidden",
  },
  summary: {
    flex: "1",
    backgroundColor: "#f0f4f8",
    padding: "2rem",
    borderRight: "1px solid #ddd",
  },
  form: {
    flex: "2",
    padding: "2rem",
    display: "flex",
    flexDirection: "column",
    gap: "1rem",
  },
  sectionTitle: {
    fontSize: "1.4rem",
    fontWeight: "bold",
    color: "#003366",
    marginBottom: "1rem",
  },
  label: {
    fontWeight: "600",
    color: "#003366",
    marginBottom: "0.2rem",
  },
  input: {
    padding: "0.8rem",
    fontSize: "1rem",
    border: "1px solid #ccc",
    borderRadius: "8px",
    backgroundColor: "#f9f9f9",
  },
  textarea: {
    padding: "0.8rem",
    borderRadius: "8px",
    fontSize: "1rem",
    border: "1px solid #ccc",
    backgroundColor: "#f9f9f9",
  },
  actions: {
    display: "flex",
    justifyContent: "space-between",
    marginTop: "1.5rem",
  },
  submitBtn: {
    backgroundColor: "#003366",
    color: "#fff",
    padding: "0.8rem 1.5rem",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    fontWeight: "600",
  },
  link: {
    color: "#003366",
    textDecoration: "none",
    fontWeight: "500",
  },
  error: {
    backgroundColor: "#ffdddd",
    color: "#a00",
    padding: "0.8rem",
    borderRadius: "8px",
  },
};

export default NewTicketCheckout;
