import React, { useEffect, useState } from "react";
import CardStats from "./CardStats"; // adapte le chemin si besoin
import axios from "axios";

export default function StatsSection() {
  const [stats, setStats] = useState({
    totalUsers: 0,
    techniciens: 0,
    admins: 0,
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const res = await axios.get("/stats");
        setStats({
          totalUsers: res.data.totalUsers,
          techniciens: res.data.techniciens,
          admins: res.data.admins,
        });
      } catch (err) {
        console.error("Erreur récupération des stats:", err);
      }
    };

    fetchStats();
  }, []);

  return (
    <div className="flex flex-wrap">
      <div className="w-full lg:w-4/12 xl:w-4/12 px-4">
        <CardStats
          statSubtitle="UTILISATEURS"
          statTitle={stats.totalUsers.toString()}
          statArrow="up"
          statPercent="100"
          statPercentColor="text-emerald-500"
          statDescripiron="Total utilisateurs"
          statIconName="fas fa-users"
          statIconColor="bg-blue-500"
        />
      </div>
      <div className="w-full lg:w-4/12 xl:w-4/12 px-4">
        <CardStats
          statSubtitle="TECHNICIENS"
          statTitle={stats.techniciens.toString()}
          statArrow="up"
          statPercent="100"
          statPercentColor="text-emerald-500"
          statDescripiron="Total techniciens"
          statIconName="fas fa-tools"
          statIconColor="bg-green-500"
        />
      </div>
      <div className="w-full lg:w-4/12 xl:w-4/12 px-4">
        <CardStats
          statSubtitle="ADMINS"
          statTitle={stats.admins.toString()}
          statArrow="up"
          statPercent="100"
          statPercentColor="text-emerald-500"
          statDescripiron="Total administrateurs"
          statIconName="fas fa-user-shield"
          statIconColor="bg-red-500"
        />
      </div>
    </div>
  );
}
