import React, { useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faFilter, faCog } from '@fortawesome/free-solid-svg-icons';

export default function DepartmentTable({ color }) {
  const [searchQuery, setSearchQuery] = useState('');
  const [filter, setFilter] = useState('Tous');

  return (
    <div className={`relative flex flex-col min-w-0 rounded-lg shadow mb-4 ${
      color === "light" ? "bg-white" : "bg-slate-800 text-white"
    }`}>
      {/* Header avec barre de recherche et boutons */}
      <div className="flex flex-wrap items-center justify-between p-4 gap-3 border-b">
        <div className="flex items-center gap-3 flex-1 max-w-3xl">
          <div className="relative flex-1">
            <FontAwesomeIcon 
              icon={faSearch} 
              className={`absolute left-3 top-1/2 -translate-y-1/2 ${
                color === "light" ? "text-gray-400" : "text-gray-300"
              }`}
            />
            <input
              type="text"
              placeholder="Rechercher par nom ou ID"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className={`w-full pl-10 pr-4 py-2 rounded-lg border ${
                color === "light" 
                  ? "border-gray-200 focus:ring-2 focus:ring-blue-500" 
                  : "border-slate-600 bg-slate-700 focus:ring-2 focus:ring-blue-400"
              }`}
            />
          </div>

          <button 
            className={`flex items-center gap-2 px-4 py-2 rounded-lg border ${
              color === "light" 
                ? "border-gray-200 hover:bg-gray-50 text-gray-700" 
                : "border-slate-600 hover:bg-slate-700 text-slate-200"
            }`}
          >
            <FontAwesomeIcon icon={faFilter} />
            <span>Filtrer par</span>
          </button>
        </div>

        <button 
          className={`px-4 py-2 rounded-lg border ${
            color === "light" 
              ? "border-blue-600 text-blue-600 hover:bg-blue-50" 
              : "border-blue-400 text-blue-400 hover:bg-slate-700"
          }`}
        >
          Router
        </button>
      </div>

      {/* Tableau des résultats */}
      <div className="overflow-x-auto p-4">
        <table className="w-full border-collapse">
          <thead>
            <tr className={`text-left text-sm ${
              color === "light" 
                ? "bg-gray-50 text-gray-500" 
                : "bg-slate-700 text-slate-300"
            }`}>
              <th className="px-4 py-3 font-medium w-20">ID</th>
              <th className="px-4 py-3 font-medium min-w-[200px]">DÉPARTEMENT</th>
              <th className="px-4 py-3 font-medium w-32">ACTIONS</th>
            </tr>
          </thead>
          <tbody>
            {/* État vide */}
            <tr className="border-t">
              <td colSpan="3" className="px-4 py-6 text-center">
                <div className={`space-y-1 ${
                  color === "light" ? "text-gray-400" : "text-slate-400"
                }`}>
                  <p className="text-sm">Aucun département trouvé</p>
                  <p className="text-xs">Affichage de 0 sur 0 départements</p>
                </div>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
}

DepartmentTable.defaultProps = {
  color: "light",
};

DepartmentTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
