import React, { useState } from "react";
import { Link } from "react-router-dom";
import IndexNavbar from "components/Navbars/IndexNavbar";
import Footerr from "components/Footers/Footerr";

export default function NewTicketForm() {
  const [formData, setFormData] = useState({
    sujet: "",
    type: "",
    urgence: "",
    description: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newTicket = {
      ...formData,
      id: Date.now(),
      date: new Date().toLocaleDateString(),
      statut: "Ouvert",
    };

    try {
      const response = await fetch("/addticket", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      if (!response.ok) throw new Error("Erreur lors de l'envoi");

      const localTickets = JSON.parse(localStorage.getItem("tickets")) || [];
      localTickets.push(newTicket);
      localStorage.setItem("tickets", JSON.stringify(localTickets));

      window.location.href = "/MyTickets";
    } catch {
      const localTickets = JSON.parse(localStorage.getItem("tickets")) || [];
      localTickets.push(newTicket);
      localStorage.setItem("tickets", JSON.stringify(localTickets));
    }
  };

  return (
    <>
      <IndexNavbar />
      <div className="min-h-screen bg-gray-50 py-12 flex justify-center items-center">
        <div className="bg-white border border-gray-200 shadow-sm rounded-lg p-8 w-full max-w-xl">
          <h2 className="text-xl font-semibold text-gray-800 mb-1">
            Créer un ticket d'assistance
          </h2>
          <p className="text-sm text-gray-500 mb-6">
            Remplissez le formulaire pour signaler votre problème.
          </p>

          <form onSubmit={handleSubmit} className="space-y-5">
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Sujet
              </label>
              <input
                type="text"
                name="sujet"
                value={formData.sujet}
                onChange={handleChange}
                required
                className="mt-1 w-full border border-gray-300 rounded-md p-2 focus:ring focus:ring-blue-200"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700">
                Type
              </label>
              <select
                name="type"
                value={formData.type}
                onChange={handleChange}
                required
                className="mt-1 w-full border border-gray-300 rounded-md p-2 focus:ring focus:ring-blue-200"
              >
                <option value="">-- Sélectionner --</option>
                <option value="IT">IT - Informatique</option>
                <option value="RH">RH</option>
                <option value="Comptabilité">Comptabilité</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700">
                Urgence
              </label>
              <select
                name="urgence"
                value={formData.urgence}
                onChange={handleChange}
                required
                className="mt-1 w-full border border-gray-300 rounded-md p-2 focus:ring focus:ring-blue-200"
              >
                <option value="">-- Choisir --</option>
                <option value="Urgent">Urgent</option>
                <option value="Normal">Normal</option>
                <option value="Faible">Faible</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700">
                Description
              </label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleChange}
                rows="4"
                required
                className="mt-1 w-full border border-gray-300 rounded-md p-2 focus:ring focus:ring-blue-200"
              />
            </div>

            <div className="flex justify-between">
              <Link to="/MyTickets" className="text-sm text-blue-700 hover:underline">
                ← Retour
              </Link>
              <button
                type="submit"
                className="bg-blue-700 text-white px-4 py-2 rounded-md hover:bg-blue-800 transition"
              >
                Soumettre
              </button>
            </div>
          </form>
        </div>
      </div>
      <Footerr />
    </>
  );
}
