import React, { useEffect, useState } from "react";
import "../style/GestionPage.css";
import {
  fetchData,
  addData,
  updateData,
  deleteData,
} from "../Functions/ApiGestion";
import { AiFillEdit, AiFillDelete } from "react-icons/ai";
import { Modal, Button, Form } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

function PageTechnicien() {
  const [techniciens, setTechniciens] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedTechnicien, setSelectedTechnicien] = useState(null);
  const [newTechnicien, setNewTechnicien] = useState({
    nom: "",
    specialite: "",
  });

  const getTechniciens = async () => {
    try {
      const data = await fetchData("/alltechnicien");
      setTechniciens(data);
    } catch (error) {
      console.error("Erreur lors de la récupération des techniciens", error);
    }
  };

  useEffect(() => {
    getTechniciens();
  }, []);

  const handleAdd = async () => {
    try {
      await addData("/addtechnicien", newTechnicien);
      getTechniciens();
      setShowAddModal(false);
      setNewTechnicien({ nom: "", specialite: "" });
    } catch (error) {
      console.error("Erreur lors de l'ajout du technicien", error);
    }
  };

  const handleEdit = async () => {
    try {
      await updateData(`/updatetechnicien/${selectedTechnicien._id}`, selectedTechnicien);
      getTechniciens();
      setShowEditModal(false);
      setSelectedTechnicien(null);
    } catch (error) {
      console.error("Erreur lors de la modification du technicien", error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteData(`/deletetechnicien/${id}`);
      getTechniciens();
    } catch (error) {
      console.error("Erreur lors de la suppression du technicien", error);
    }
  };

  const filteredTechniciens = techniciens.filter((technicien) =>
    technicien.nom.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="gp-container">
      <h2>Gestion des Techniciens</h2>
      <div className="gp-actions">
        <input
          type="text"
          placeholder="Rechercher par nom..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="gp-search-input"
        />
        <button onClick={() => setShowAddModal(true)} className="gp-add-button">
          Ajouter Technicien
        </button>
      </div>
      <table className="gp-table">
        <thead>
          <tr>
            <th className="gp-th">ID</th>
            <th className="gp-th">Nom</th>
            <th className="gp-th">Spécialité</th>
            <th className="gp-th" style={{ textAlign: "center" }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredTechniciens.map((technicien) => (
            <tr className="gp-tr" key={technicien._id}>
              <td className="gp-td">{technicien._id.substring(0, 8)}...</td>
              <td className="gp-td">{technicien.nom}</td>
              <td className="gp-td">{technicien.specialite}</td>
              <td className="gp-td" style={{ textAlign: "center" }}>
                <button
                  className="gp-icon-button"
                  onClick={() => {
                    setSelectedTechnicien(technicien);
                    setShowEditModal(true);
                  }}
                >
                  <AiFillEdit />
                </button>
                <button
                  className="gp-icon-button"
                  onClick={() => handleDelete(technicien._id)}
                >
                  <AiFillDelete />
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal Ajout */}
      <Modal show={showAddModal} onHide={() => setShowAddModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Ajouter un Technicien</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>Nom</Form.Label>
              <Form.Control
                type="text"
                value={newTechnicien.nom}
                onChange={(e) =>
                  setNewTechnicien({ ...newTechnicien, nom: e.target.value })
                }
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Spécialité</Form.Label>
              <Form.Control
                type="text"
                value={newTechnicien.specialite}
                onChange={(e) =>
                  setNewTechnicien({ ...newTechnicien, specialite: e.target.value })
                }
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowAddModal(false)}>
            Fermer
          </Button>
          <Button variant="primary" onClick={handleAdd}>
            Ajouter
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Modal Édition */}
      <Modal show={showEditModal} onHide={() => setShowEditModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Modifier un Technicien</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedTechnicien && (
            <Form>
              <Form.Group>
                <Form.Label>Nom</Form.Label>
                <Form.Control
                  type="text"
                  value={selectedTechnicien.nom}
                  onChange={(e) =>
                    setSelectedTechnicien({
                      ...selectedTechnicien,
                      nom: e.target.value,
                    })
                  }
                />
              </Form.Group>
              <Form.Group>
                <Form.Label>Spécialité</Form.Label>
                <Form.Control
                  type="text"
                  value={selectedTechnicien.specialite}
                  onChange={(e) =>
                    setSelectedTechnicien({
                      ...selectedTechnicien,
                      specialite: e.target.value,
                    })
                  }
                />
              </Form.Group>
            </Form>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowEditModal(false)}>
            Fermer
          </Button>
          <Button variant="primary" onClick={handleEdit}>
            Enregistrer
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default PageTechnicien;
