import React, { useState } from "react";
import { FaEdit, FaTrash } from "react-icons/fa";

const ListeUtilisateurs = () => {
  const [utilisateurs, setUtilisateurs] = useState([
    {
      id: "1",
      nom: "Rayen",
      email: "rayen@example.com",
      tel: "12345678",
      role: "Admin",
      statut: "Actif",
      dateAjout: "2024-04-01",
    },
    {
      id: "2",
      nom: "Nour",
      email: "nour@example.com",
      tel: "98765432",
      role: "User",
      statut: "Inactif",
      dateAjout: "2024-04-15",
    },
  ]);

  const [userEnCours, setUserEnCours] = useState(null);

  const handleModifier = (user) => {
    setUserEnCours({ ...user });
  };

  const handleSupprimer = (id) => {
    setUtilisateurs(utilisateurs.filter((u) => u.id !== id));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserEnCours((prev) => ({ ...prev, [name]: value }));
  };

  const handleEnregistrer = () => {
    setUtilisateurs((prev) =>
      prev.map((u) => (u.id === userEnCours.id ? userEnCours : u))
    );
    setUserEnCours(null);
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Liste des utilisateurs</h2>
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.th}>ID</th>
            <th style={styles.th}>Nom</th>
            <th style={styles.th}>Email</th>
            <th style={styles.th}>Téléphone</th>
            <th style={styles.th}>Rôle</th>
            <th style={styles.th}>Statut</th>
            <th style={styles.th}>Date d’ajout</th>
            <th style={styles.th}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {utilisateurs.map((user) => (
            <tr key={user.id} style={styles.tr}>
              <td style={styles.td}>{user.id}</td>
              <td style={styles.td}>{user.nom}</td>
              <td style={styles.td}>{user.email}</td>
              <td style={styles.td}>{user.tel}</td>
              <td style={styles.td}>{user.role}</td>
              <td style={styles.td}>{user.statut}</td>
              <td style={styles.td}>{user.dateAjout}</td>
              <td style={styles.actions}>
                <button onClick={() => handleModifier(user)} style={styles.actionBtn}>
                  <FaEdit />
                </button>
                <button onClick={() => handleSupprimer(user.id)} style={{ ...styles.actionBtn, backgroundColor: "#dc3545" }}>
                  <FaTrash />
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {userEnCours && (
        <div style={styles.formulaire}>
          <h3 style={{ marginBottom: "10px" }}>Modifier l'utilisateur</h3>
          <div style={styles.formRow}>
            <label>ID:</label>
            <input disabled name="id" value={userEnCours.id} style={styles.input} />
          </div>
          <div style={styles.formRow}>
            <label>Nom:</label>
            <input name="nom" value={userEnCours.nom} onChange={handleChange} style={styles.input} />
          </div>
          <div style={styles.formRow}>
            <label>Email:</label>
            <input name="email" value={userEnCours.email} onChange={handleChange} style={styles.input} />
          </div>
          <div style={styles.formRow}>
            <label>Téléphone:</label>
            <input name="tel" value={userEnCours.tel} onChange={handleChange} style={styles.input} />
          </div>
          <div style={styles.formRow}>
            <label>Rôle:</label>
            <input name="role" value={userEnCours.role} onChange={handleChange} style={styles.input} />
          </div>
          <div style={styles.formRow}>
            <label>Statut:</label>
            <input name="statut" value={userEnCours.statut} onChange={handleChange} style={styles.input} />
          </div>
          <div style={styles.formRow}>
            <label>Date d’ajout:</label>
            <input name="dateAjout" value={userEnCours.dateAjout} onChange={handleChange} style={styles.input} />
          </div>
          <button style={styles.btnEnregistrer} onClick={handleEnregistrer}>
            Enregistrer
          </button>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    fontFamily: "Arial",
    padding: "20px",
    backgroundColor: "#1e1e2f",
    borderRadius: "10px",
    color: "#fff",
    minHeight: "100vh",
  },
  title: {
    textAlign: "center",
    marginBottom: "20px",
    color: "#fff",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    backgroundColor: "#2c2f48",
    borderRadius: "10px",
    overflow: "hidden",
  },
  th: {
    padding: "12px",
    backgroundColor: "#44476a",
    color: "#fff",
    textAlign: "left",
  },
  tr: {
    borderBottom: "1px solid #444",
  },
  td: {
    padding: "12px",
    color: "#ddd",
  },
  actions: {
    display: "flex",
    gap: "8px",
    justifyContent: "center",
  },
  actionBtn: {
    padding: "6px",
    backgroundColor: "#17a2b8",
    border: "none",
    borderRadius: "5px",
    color: "white",
    cursor: "pointer",
  },
  formulaire: {
    marginTop: "30px",
    backgroundColor: "#2c2f48",
    padding: "20px",
    borderRadius: "10px",
    maxWidth: "600px",
    marginLeft: "auto",
    marginRight: "auto",
    color: "#fff",
  },
  formRow: {
    display: "flex",
    flexDirection: "column",
    marginBottom: "10px",
  },
  input: {
    padding: "8px",
    borderRadius: "4px",
    border: "1px solid #ccc",
    backgroundColor: "#fff",
    color: "#000",
  },
  btnEnregistrer: {
    marginTop: "15px",
    backgroundColor: "#28a745",
    color: "white",
    border: "none",
    padding: "10px 15px",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default ListeUtilisateurs;
