import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { Search, Edit, Trash, Plus, X, Check, Filter } from "lucide-react";
import axios from "axios";

// Styles CSS corrigés
const encapsulatedStyles = `
.gp-table-container {
  width: 100%;
  overflow-x: auto;
  margin: 1rem 0;
  border-radius: 8px;
  border: 1px solid #e2e8f0;
}

.gp-table {
  width: 100%;
  border-collapse: collapse;
  background: white;
  min-width: 800px;
}

.gp-th {
  padding: 1rem;
  font-size: 0.75rem;
  font-weight: 600;
  text-align: left;
  color: #6b7280;
  background-color: #f9fafb;
  border-bottom: 2px solid #e5e7eb;
  text-transform: uppercase;
}

.gp-td {
  padding: 1rem;
  color: #374151;
  border-bottom: 1px solid #f3f4f6;
  vertical-align: middle;
}

.gp-tr:hover {
  background-color: #f8fafc;
}

.gp-department-badge {
  display: inline-block;
  padding: 0.25rem 0.75rem;
  border-radius: 20px;
  font-size: 0.875rem;
  background-color: #f0fdf4;
  color: #166534;
  border: 1px solid #bbf7d0;
}

.gp-action-button {
  padding: 0.5rem;
  border-radius: 6px;
  transition: all 0.2s;
  display: inline-flex;
  margin: 0 2px;
}

.gp-edit-button {
  background-color: #dbeafe;
  color: #1d4ed8;
  
  &:hover {
    background-color: #bfdbfe;
  }
}

.gp-delete-button {
  background-color: #fee2e2;
  color: #b91c1c;
  
  &:hover {
    background-color: #fecaca;
  }
}

/* Reste du CSS inchangé */
${/* ... (le reste de votre CSS original) */ ''}
`;

// Toast component corrigé
const Toast = ({ message, type, onClose }) => {
  const toastClass = `gp-toast ${type === "success" ? "gp-toast-success" : "gp-toast-error"}`;

  useEffect(() => {
    const timer = setTimeout(onClose, 3000);
    return () => clearTimeout(timer);
  }, [onClose]);

  return (
    <div className={toastClass}>
      {type === "success" ? <Check size={20} /> : <X size={20} />}
      <p>{message}</p>
    </div>
  );
};

// Section principale du composant (identique à votre code)
export default function CardDepartment({ color = "light" }) {
  // ... (garder tout le state et les fonctions tel quel)

  return (
    <>
      <style>{encapsulatedStyles}</style>
      
      {/* Section du tableau inchangée */}
      <div className="gp-table-container">
        <table className="gp-table">
          <thead>
            <tr>
              <th className="gp-th">ID</th>
              <th className="gp-th">Département</th>
              <th className="gp-th" style={{ textAlign: "center" }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {isLoading ? (
              <tr>
                <td colSpan="3" className="gp-td" style={{ textAlign: "center" }}>
                  Chargement...
                </td>
              </tr>
            ) : filteredDepartments.length > 0 ? (
              filteredDepartments.map((department) => (
                <tr className="gp-tr" key={department._id}>
                  <td className="gp-td">#{department._id.substring(0, 8)}</td>
                  <td className="gp-td">
                    <span className="gp-department-badge">
                      {department.nom}
                    </span>
                  </td>
                  <td className="gp-td">
                    <div style={{ display: "flex", justifyContent: "center" }}>
                      <button
                        onClick={() => handleAction("modifier", department._id)}
                        className="gp-action-button gp-edit-button"
                      >
                        <Edit size={18} />
                      </button>
                      <button
                        onClick={() => handleAction("supprimer", department._id)}
                        className="gp-action-button gp-delete-button"
                      >
                        <Trash size={18} />
                      </button>
                    </div>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="3" className="gp-td" style={{ textAlign: "center" }}>
                  Aucun résultat
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Les modales et le reste du code inchangé */}
      {/* ... */}
    </>
  );
}

CardDepartment.propTypes = {
  color: PropTypes.string,
};
