import React, { useState } from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";
import { Link } from "react-router-dom";

function NewTicketForm() {
  const [formData, setFormData] = useState({
    sujet: "",
    type: "",
    type: "",
    description: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const newTicket = {
      id: Date.now(),
      ...formData,
      statut: "Ouvert",
      date: new Date().toLocaleDateString("fr-FR"),
      start: new Date(),
      end: new Date(new Date().getTime() + 60 * 60 * 1000), // dur√©e 1h
    };

    const existingTickets = JSON.parse(localStorage.getItem("tickets")) || [];
    const updatedTickets = [...existingTickets, newTicket];

    localStorage.setItem("tickets", JSON.stringify(updatedTickets));
    window.location.href = "/MyTickets";
  };

  return (
    <>
      <IndexNavbar />
      <div style={container}>
        <div style={card}>
          <div style={leftSide}>
            <h2 style={title}>üé´ Cr√©er un ticket d‚Äôassistance</h2>
            <form style={form} onSubmit={handleSubmit}>
              <div style={inputGroup}>
                <label htmlFor="sujet" style={label}>Sujet</label>
                <input type="text" id="sujet" name="sujet" value={formData.sujet} onChange={handleChange} style={input} required />
              </div>
              <div style={inputGroup}>
                <label htmlFor="departement" style={label}>D√©partement</label>
                <select id="departement" name="departement" value={formData.departement} onChange={handleChange} style={input} required>
                  <option value="">-- S√©lectionner --</option>
                  <option value="IT">IT</option>
                  <option value="RH">RH</option>
                  <option value="Comptabilit√©">Comptabilit√©</option>
                </select>
              </div>
              <div style={inputGroup}>
                <label htmlFor="type" style={label}>Priorit√©</label>
                <select id="type" name="type" value={formData.type} onChange={handleChange} style={input} required>
                  <option value="">-- Choisir --</option>
                  <option value="Urgent">Urgent</option>
                  <option value="Normal">Normal</option>
                  <option value="Faible">Faible</option>
                </select>
              </div>
              <div style={inputGroup}>
                <label htmlFor="description" style={label}>Description</label>
                <textarea id="description" name="description" rows="4" value={formData.description} onChange={handleChange} style={input} required />
              </div>
              <div style={buttonGroup}>
                <Link to="/MyTickets" style={backLink}>‚Üê Retour</Link>
                <button type="submit" style={submitButton}>Soumettre</button>
              </div>
            </form>
          </div>
          <div style={rightSide}>
            <img src="https://images.unsplash.com/photo-1605902711622-cfb43c4437d4?auto=format&fit=crop&w=1000&q=80" alt="support" style={imageStyle} />
          </div>
        </div>
      </div>
      <Footerr />
    </>
  );
}

const container = { backgroundColor: "#f1f3f6", minHeight: "100vh", display: "flex", justifyContent: "center", alignItems: "center", padding: "2rem" };
const card = { display: "flex", backgroundColor: "#fff", borderRadius: "16px", maxWidth: "1080px", width: "100%", overflow: "hidden" };
const leftSide = { flex: 1, padding: "2.5rem" };
const rightSide = { flex: 1 };
const imageStyle = { width: "100%", height: "100%", objectFit: "cover" };
const title = { fontSize: "1.8rem", fontWeight: "700", color: "#003366", marginBottom: "1rem" };
const form = { display: "flex", flexDirection: "column", gap: "1rem" };
const inputGroup = { display: "flex", flexDirection: "column" };
const label = { fontWeight: "600", marginBottom: "0.5rem", color: "#003366" };
const input = { padding: "0.8rem", fontSize: "1rem", borderRadius: "8px", border: "1px solid #ccc" };
const buttonGroup = { display: "flex", justifyContent: "space-between", alignItems: "center", marginTop: "1.5rem" };
const submitButton = { backgroundColor: "#003366", color: "#fff", padding: "0.8rem 1.6rem", border: "none", borderRadius: "8px", cursor: "pointer" };
const backLink = { color: "#003366", textDecoration: "none", fontWeight: "500" };

export default NewTicketForm;
