import React, { useState, useEffect } from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";

function MyTickets() {
  const [tickets, setTickets] = useState([]);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [searchId, setSearchId] = useState("");

  // Fonction de formatage corrigée
  const formatDisplayId = (id) => {
    const strId = String(id).padStart(4, '0');
    if (strId.length < 4) return strId;
    const start = Math.floor((strId.length - 4) / 2);
    return strId.slice(start, start + 4);
  };

  useEffect(() => {
    document.body.style.margin = "0";
    document.body.style.minHeight = "100vh";
    document.body.style.display = "flex";
    document.body.style.flexDirection = "column";

    const fetchTickets = async () => {
      try {
        const response = await fetch('/alltickets');
        if (!response.ok) throw new Error('Erreur réseau');
        const data = await response.json();
        console.log("Tickets reçus:", data); // Debug
        setTickets(data);
      } catch (error) {
        console.error("Erreur:", error);
        setTickets([]);
      }
    };

    fetchTickets();
  }, []);

  const statusStyles = (statut) => ({
    padding: "0.3rem 0.8rem",
    borderRadius: "4px",
    fontSize: "0.85rem",
    backgroundColor: {
      "Ouvert": "#ffebee",
      "En cours": "#e3f2fd",
      "Résolu": "#e8f5e9",
    }[statut],
    color: {
      "Ouvert": "#c62828",
      "En cours": "#1565c0",
      "Résolu": "#2e7d32",
    }[statut],
    fontWeight: "bold",
  });

  const filteredTickets = tickets.filter(ticket =>
    searchId === "" || String(ticket.id).includes(searchId)
  );

  return (
    <>
      <IndexNavbar />
      <div style={container}>
        <div style={contentWrapper}>
          <img 
            src="/images/support-banner.jpg" 
            alt="Bannière Support"
            style={bannerImage}
          />
          <h1 style={title}>Espace Support Client</h1>
          <p style={subtitle}>Consultez vos tickets et suivez leur avancement</p>

          <div style={{ marginBottom: "1rem", textAlign: "center" }}>
            <input
              type="text"
              placeholder="Rechercher par numéro de ticket"
              value={searchId}
              onChange={(e) => setSearchId(e.target.value)}
              style={searchInput}
            />
          </div>

          <div style={ticketGrid}>
            <div style={ticketList}>
              {filteredTickets.map((ticket) => (
                <div 
                  key={ticket.id}
                  style={ticketItem} 
                  onClick={() => setSelectedTicket(ticket)}
                >
                  <div style={ticketHeader}>
                    <span style={ticketId}>#{formatDisplayId(ticket.id)}</span>
                    <span style={statusStyles(ticket.statut)}>{ticket.statut}</span>
                  </div>
                  <h3 style={ticketTitle}>{ticket.sujet}</h3>
                  <p style={ticketDate}>{new Date(ticket.date).toLocaleDateString()}</p>
                </div>
              ))}
              {filteredTickets.length === 0 && (
                <p style={{ textAlign: "center", color: "#999" }}>
                  {tickets.length === 0 ? "Chargement..." : "Aucun ticket trouvé."}
                </p>
              )}
            </div>

            {selectedTicket && (
              <div style={detailsPanel}>
                <div style={detailsHeader}>
                  <h2 style={detailsTitle}>Détails du ticket</h2>
                  <button 
                    style={closeButton} 
                    onClick={() => setSelectedTicket(null)}
                    aria-label="Fermer"
                  >
                    ×
                  </button>
                </div>
                <div style={detailsContent}>
                  <div style={detailRow}>
                    <span style={detailLabel}>Numéro :</span>
                    <span>#{formatDisplayId(selectedTicket.id)}</span>
                  </div>
                  <div style={detailRow}>
                    <span style={detailLabel}>Statut :</span>
                    <span style={statusStyles(selectedTicket.statut)}>
                      {selectedTicket.statut}
                    </span>
                  </div>
                  <div style={detailRow}>
                    <span style={detailLabel}>Date :</span>
                    <span>{new Date(selectedTicket.date).toLocaleString()}</span>
                  </div>
                  <div style={detailDescription}>
                    <p style={detailLabel}>Description :</p>
                    <p>{selectedTicket.description}</p>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
      <Footerr />
    </>
  );
}

// Styles (inchangés mais inclus pour complétude)
const container = {/* ... */};
const contentWrapper = {/* ... */};
const bannerImage = {/* ... */};
const title = {/* ... */};
const subtitle = {/* ... */};
const ticketGrid = {/* ... */};
const ticketList = {/* ... */};
const ticketItem = {/* ... */};
const ticketHeader = {/* ... */};
const ticketId = {/* ... */};
const ticketTitle = {/* ... */};
const ticketDate = {/* ... */};
const detailsPanel = {/* ... */};
const detailsHeader = {/* ... */};
const detailsTitle = {/* ... */};
const closeButton = {/* ... */};
const detailsContent = {/* ... */};
const detailRow = {/* ... */};
const detailLabel = {/* ... */};
const detailDescription = {/* ... */};
const searchInput = {
  padding: "0.5rem",
  width: "60%",
  maxWidth: "300px",
  borderRadius: "6px",
  border: "1px solid #ccc",
  fontSize: "1rem"
};

export default MyTickets;
