import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { Search, Edit, Trash, Plus, X, Check, Filter } from "lucide-react";
import axios from "axios";

// CSS encapsulé avec préfixe "gp-" (cohérent avec Gestion Personnel)
const encapsulatedStyles = `
  .gp-department-container {
    background: white;
    border-radius: 8px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
    overflow: hidden;
  }
  
  .gp-header-section {
    padding: 1.5rem;
    border-bottom: 1px solid #e2e8f0;
  }
  
  .gp-search-container {
    display: flex;
    flex-wrap: wrap;
    gap: 1rem;
    margin-bottom: 1.5rem;
  }
  
  .gp-search-input {
    flex: 1;
    position: relative;
  }
  
  .gp-search-icon {
    position: absolute;
    left: 10px;
    top: 50%;
    transform: translateY(-50%);
    color: #9ca3af;
  }
  
  .gp-tab-button {
    padding: 0.5rem 1rem;
    border-bottom: 2px solid transparent;
    color: #6b7280;
    font-weight: 500;
    transition: all 0.2s ease;
  }
  
  .gp-tab-button.gp-active {
    border-bottom-color: #14b8a6;
    color: #14b8a6;
  }
  
  .gp-tab-button:hover:not(.gp-active) {
    color: #374151;
  }
  
  .gp-table-container {
    width: 100%;
    overflow-x: auto;
  }
  
  .gp-table {
    width: 100%;
    border-collapse: collapse;
  }
  
  .gp-th {
    text-align: left;
    padding: 0.75rem 1rem;
    font-size: 0.75rem;
    font-weight: 600;
    text-transform: uppercase;
    color: #6b7280;
    background-color: #f9fafb;
    border-bottom: 1px solid #e5e7eb;
  }
  
  .gp-td {
    padding: 1rem;
    vertical-align: middle;
    color: #374151;
    border-bottom: 1px solid #f3f4f6;
  }
  
  .gp-tr:hover {
    background-color: #f9fafb;
  }
  
  .gp-department-badge {
    background-color: #e6fffa;
    color: #047857;
    padding: 0.25rem 0.5rem;
    border-radius: 9999px;
    font-size: 0.75rem;
    font-weight: 500;
  }
  
  .gp-action-button {
    padding: 0.375rem;
    border-radius: 0.375rem;
    transition: all 0.2s ease;
  }
  
  .gp-edit-button {
    background-color: #dbeafe;
    color: #3b82f6;
  }
  
  .gp-edit-button:hover {
    background-color: #bfdbfe;
  }
  
  .gp-delete-button {
    background-color: #fee2e2;
    color: #ef4444;
  }
  
  .gp-delete-button:hover {
    background-color: #fecaca;
  }
  
  .gp-add-button {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    background-color: #14b8a6;
    color: white;
    padding: 0.5rem 1rem;
    border-radius: 0.375rem;
    font-weight: 500;
    transition: all 0.2s ease;
  }
  
  .gp-add-button:hover {
    background-color: #0d9488;
  }
  
  .gp-pagination {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 1.5rem;
    padding: 0 1rem;
  }
  
  .gp-modal-overlay {
    position: fixed;
    inset: 0;
    background-color: rgba(0, 0, 0, 0.5);
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 1rem;
    z-index: 50;
  }
  
  .gp-modal-container {
    background-color: white;
    border-radius: 0.5rem;
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
    padding: 1.5rem;
    width: 100%;
    max-width: 28rem;
  }
  
  .gp-modal-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 1.5rem;
  }
  
  .gp-modal-title {
    font-size: 1.25rem;
    font-weight: 700;
    color: #1f2937;
  }
  
  .gp-modal-close {
    color: #6b7280;
    transition: color 0.2s ease;
  }
  
  .gp-modal-close:hover {
    color: #1f2937;
  }
  
  .gp-form-group {
    margin-bottom: 1rem;
  }
  
  .gp-form-label {
    display: block;
    margin-bottom: 0.25rem;
    font-size: 0.875rem;
    font-weight: 500;
    color: #374151;
  }
  
  .gp-form-input {
    width: 100%;
    padding: 0.5rem;
    border: 1px solid #d1d5db;
    border-radius: 0.375rem;
    transition: all 0.2s ease;
  }
  
  .gp-form-input:focus {
    outline: none;
    border-color: #14b8a6;
    box-shadow: 0 0 0 2px rgba(20, 184, 166, 0.2);
  }
  
  .gp-form-select {
    width: 100%;
    padding: 1 rem;
    border: 1px solid #d1d5db;
    border-radius: 0.375rem;
    transition: all 0.2s ease;
  }
  
  .gp-form-select:focus {
    outline: none;
    border-color: #14b8a6;
    box-shadow: 0 0 0 2px rgba(20, 184, 166, 0.2);
  }
  
  .gp-modal-footer {
    display: flex;
    justify-content: flex-end;
    gap: 0.5rem;
    margin-top: 1.5rem;
  }
  
  .gp-btn {
    padding: 0.5rem 1rem;
    border-radius: 0.375rem;
    font-weight: 500;
    transition: all 0.2s ease;
  }
  
  .gp-btn-cancel {
    background-color: #f3f4f6;
    color: #374151;
  }
  
  .gp-btn-cancel:hover {
    background-color: #e5e7eb;
  }
  
  .gp-btn-save {
    background-color: #14b8a6;
    color: white;
  }
  
  .gp-btn-save:hover {
    background-color: #0d9488;
  }
  
  .gp-toast {
    position: fixed;
    bottom: 1rem;
    right: 1rem;
    padding: 1rem;
    border-radius: 0.375rem;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
    display: flex;
    align-items: center;
    gap: 0.5rem;
    color: white;
    z-index: 50;
  }
  
  .gp-toast-success {
    background-color: #10b981;
  }
  
  .gp-toast-error {
    background-color: #ef4444;
  }
`;
// Toast component for notifications
const Toast = ({ message, type, onClose }) => { 
  const toastClass =
    type === "success"
      ? "gp-toast-success"
      : type === "error"
      ? "gp-toast-error"
      : "gp-toast-info";

  React.useEffect(() => {
    const timer = setTimeout(() => {
      onClose();
    }, 3000);

    return () => clearTimeout(timer);
  }, [onClose]);

  return (
    <div className={`gp-toast ${toastClass}`}>
      {type === "success" && <Check size={20} />}
      {type === "error" && <X size={20} />}
      <p>{message}</p>
    </div>
  );
};

export default function CardUser({ color = "light" }) {
  const [searchTerm, setSearchTerm] = useState("");
  const [sortOption, setSortOption] = useState("");
  const [users, setUsers] = useState([]);
  const [modalOuvert, setModalOuvert] = useState(false);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [selectedUserId, setSelectedUserId] = useState(null);
  const [selectedUserName, setSelectedUserName] = useState("");
  const [newUser, setNewUser] = useState({
    nom: "",
    prenom: "",
    role: "",
    telephone: "",
    email: ""
  });
  const [selectedUser, setSelectedUser] = useState(null);
  const [errors, setErrors] = useState({});
  const [toast, setToast] = useState({ affiche: false, message: "", type: "" });
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      setIsLoading(true);
      const response = await axios.get(`/allusers`);
      setUsers(response.data);
      setIsLoading(false);
    } catch (error) {
      console.error("Error fetching users:", error);
      afficherToast("Erreur lors du chargement des utilisateurs", "error");
      setIsLoading(false);
    }
  };

  const filteredUsers = users
    .filter((user) => {
      const searchLower = searchTerm.toLowerCase();
      return (
        user._id.includes(searchTerm) ||
        user.nom.toLowerCase().includes(searchLower) ||
        user.prenom.toLowerCase().includes(searchLower) ||
        user.email.toLowerCase().includes(searchLower)
      );
    })
    .sort((a, b) => {
      if (!sortOption) return 0;
      if (sortOption === "id-asc") return a._id.localeCompare(b._id);
      if (sortOption === "id-desc") return b._id.localeCompare(a._id);
      if (sortOption === "nom") return a.nom.localeCompare(b.nom);
      return 0;
    });

  const handleAction = (action, id, nom) => {
    switch (action) {
      case "modifier":
        const userToEdit = users.find((user) => user._id === id);
        setSelectedUser(userToEdit);
        setIsEditModalOpen(true);
        break;
      case "supprimer":
        setSelectedUserId(id);
        setSelectedUserName(nom);
        setModalOuvert(true);
        break;
      default:
        break;
    }
  };

  const handleConfirmDelete = async () => {
    try {
      await axios.delete(`/deleteuser/${selectedUserId}`);
      setUsers(users.filter((user) => user._id !== selectedUserId));
      setModalOuvert(false);
      setSelectedUserName("");
      afficherToast("Utilisateur supprimé avec succès", "success");
    } catch (error) {
      console.error("Error deleting user:", error);
      afficherToast("Erreur lors de la suppression de l'utilisateur", "error");
    }
  };

  const handleAddUser = () => {
    setNewUser({
      nom: "",
      prenom: "",
      role: "",
      telephone: "",
      email: ""
    });
    setSelectedUserId(null);
    setIsEditModalOpen(false);
    setModalOuvert(true);
  };

  const validateForm = (user) => {
    const newErrors = {};
    if (!user.nom) newErrors.nom = "Le nom est obligatoire";
    if (!user.email) newErrors.email = "L'email est obligatoire";
    if (user.email && !/\S+@\S+\.\S+/.test(user.email)) {
      newErrors.email = "Email invalide";
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleModalSubmit = async () => {
    if (!validateForm(newUser)) return;

    try {
      const response = await axios.post(`/adduser`, newUser);
      setUsers([...users, response.data]);
      setModalOuvert(false);
      setNewUser({
        nom: "",
        prenom: "",
        role: "",
        telephone: "",
        email: ""
      });
      setErrors({});
      afficherToast("Utilisateur ajouté avec succès", "success");
    } catch (error) {
      console.error("Error adding user:", error);
      afficherToast("Erreur lors de l'ajout de l'utilisateur", "error");
    }
  };

  const handleEditSubmit = async () => {
    if (!validateForm(selectedUser)) return;

    try {
      await axios.put(`/updateuser/${selectedUser._id}`, selectedUser);
      const updatedUsers = users.map((user) =>
        user._id === selectedUser._id ? selectedUser : user
      );
      setUsers(updatedUsers);
      setIsEditModalOpen(false);
      afficherToast("Utilisateur modifié avec succès", "success");
    } catch (error) {
      console.error("Error updating user:", error);
      afficherToast("Erreur lors de la modification de l'utilisateur", "error");
    }
  };

  const handleEditCancel = () => {
    setIsEditModalOpen(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (isEditModalOpen) {
      setSelectedUser((prev) => ({ ...prev, [name]: value }));
    } else {
      setNewUser((prev) => ({ ...prev, [name]: value }));
    }
  };

  return (
    <>
      <div className={"relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded"}>
        <div className="gp-department-container">
          <div className="gp-header-section">
            <h1 className="gp-modal-title" style={{ fontSize: "1.5rem", marginBottom: "1.5rem" }}>
              Gestion des Utilisateurs
            </h1>
            <div className="gp-search-container">
              <div className="gp-search-input">
                <div className="gp-search-icon">
                  <Search size={20} />
                </div>
                <input
                  type="text"
                  placeholder="Rechercher par nom, email ou ID"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
              <div>
                <Filter size={20} style={{ color: "#9ca3af" }} />
                <select
                  value={sortOption}
                  onChange={(e) => setSortOption(e.target.value)}
                >
                  <option value="">Filtrer par</option>
                  <option value="id-asc">ID Ascendant</option>
                  <option value="id-desc">ID Descendant</option>
                  <option value="nom">Par Nom</option>
                </select>
              </div>
              <button onClick={handleAddUser}>
                <Plus size={20} />
                Ajouter
              </button>
            </div>

            <div className="gp-table-container">
              <table className="gp-table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Nom</th>
                    <th>Prénom</th>
                    <th>Rôle</th>
                    <th>Téléphone</th>
                    <th>Email</th>
                    <th style={{ textAlign: "center" }}>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {isLoading ? (
                    <tr>
                      <td colSpan="7" style={{ textAlign: "center", padding: "1.5rem 0" }}>
                        Chargement des utilisateurs...
                      </td>
                    </tr>
                  ) : filteredUsers.length > 0 ? (
                    filteredUsers.map((user) => (
                      <tr key={user._id}>
                        <td>{user._id.substring(0, 8)}...</td>
                        <td>{user.nom}</td>
                        <td>{user.prenom}</td>
                        <td>{user.role}</td>
                        <td>{user.telephone}</td>
                        <td>{user.email}</td>
                        <td>
                          <div style={{ display: "flex", justifyContent: "center", gap: "0.5rem" }}>
                            <button onClick={() => handleAction("modifier", user._id, user.nom)}>
                              <Edit size={16} />
                            </button>
                            <button onClick={() => handleAction("supprimer", user._id, user.nom)}>
                              <Trash size={16} />
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="7" style={{ textAlign: "center", padding: "1.5rem 0" }}>
                        Aucun utilisateur trouvé
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </div>

        {modalOuvert && selectedUserId && (
          <div className="gp-modal-overlay">
            <div className="gp-modal-container">
              {/* Modal de suppression */}
            </div>
          </div>
        )}

        {modalOuvert && !selectedUserId && (
          <div className="gp-modal-overlay">
            <div className="gp-modal-container">
              <div className="gp-modal-header">
                <h2>Ajouter un utilisateur</h2>
                <button onClick={() => setModalOuvert(false)}>
                  <X size={24} />
                </button>
              </div>
              <div>
                <label>Nom</label>
                <input
                  type="text"
                  name="nom"
                  value={newUser.nom}
                  onChange={handleChange}
                />
                {errors.nom && <p>{errors.nom}</p>}
                <label>Prénom</label>
                <input
                  type="text"
                  name="prenom"
                  value={newUser.prenom}
                  onChange={handleChange}
                />
                <label>Rôle</label>
                <input
                  type="text"
                  name="role"
                  value={newUser.role}
                  onChange={handleChange}
                />
                <label>Téléphone</label>
                <input
                  type="tel"
                  name="telephone"
                  value={newUser.telephone}
                  onChange={handleChange}
                />
                <label>Email</label>
                <input
                  type="email"
                  name="email"
                  value={newUser.email}
                  onChange={handleChange}
                />
                {errors.email && <p>{errors.email}</p>}
                <button onClick={handleModalSubmit}>Ajouter</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </>
  );
}

CardUser.propTypes = {
  color: PropTypes.string,
};
