import React, { useState } from "react";
import { useHistory } from "react-router-dom";

function NewTicketForm() {
  const [sujet, setSujet] = useState("");
  const [type, setType] = useState("");
  const [urgence, setUrgence] = useState("");
  const [description, setDescription] = useState("");
  const [date, setDate] = useState("");
  const [statut, setStatut] = useState("Ouvert");
  const [loading, setLoading] = useState(false);
  const history = useHistory(); // Pour rediriger aprÃ¨s la soumission

  // Fonction pour gÃ©rer la soumission du formulaire
  const handleSubmit = async (event) => {
    event.preventDefault(); // EmpÃªche la page de se recharger

    const newTicket = {
      sujet,
      type,
      urgence,
      description,
      date,
      statut,
    };

    setLoading(true); // On commence Ã  charger (affichage d'un spinner ou autre)

    try {
      const response = await fetch("/api/tickets", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newTicket),
      });

      const result = await response.json();

      if (response.ok) {
        // Si la soumission est rÃ©ussie, on redirige vers la page "Mes Tickets"
        history.push("/MyTickets");
      } else {
        console.error("Erreur lors de la soumission :", result);
      }
    } catch (error) {
      console.error("Erreur rÃ©seau :", error);
    } finally {
      setLoading(false); // On arrÃªte l'affichage du chargement
    }
  };

  return (
    <div style={container}>
      <div style={card}>
        <h2 style={title}>CrÃ©er un Nouveau Ticket</h2>
        <form onSubmit={handleSubmit}>
          <div style={inputGroup}>
            <label htmlFor="sujet" style={label}>Sujet</label>
            <input
              type="text"
              id="sujet"
              value={sujet}
              onChange={(e) => setSujet(e.target.value)}
              required
              style={input}
            />
          </div>
          
          <div style={inputGroup}>
            <label htmlFor="type" style={label}>Type</label>
            <input
              type="text"
              id="type"
              value={type}
              onChange={(e) => setType(e.target.value)}
              required
              style={input}
            />
          </div>

          <div style={inputGroup}>
            <label htmlFor="urgence" style={label}>Urgence</label>
            <input
              type="text"
              id="urgence"
              value={urgence}
              onChange={(e) => setUrgence(e.target.value)}
              required
              style={input}
            />
          </div>

          <div style={inputGroup}>
            <label htmlFor="description" style={label}>Description</label>
            <textarea
              id="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
              style={textarea}
            />
          </div>

          <div style={inputGroup}>
            <label htmlFor="date" style={label}>Date</label>
            <input
              type="date"
              id="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
              style={input}
            />
          </div>

          <div style={buttonGroup}>
            <button type="submit" style={submitButton}>
              {loading ? "Soumission en cours..." : "Soumettre"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

// ðŸŒŸ Styles sobres et bancaires
const container = {
  backgroundColor: "#f1f3f6",
  minHeight: "100vh",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  padding: "2rem",
};

const card = {
  backgroundColor: "#fff",
  borderRadius: "16px",
  boxShadow: "0 8px 24px rgba(0, 0, 0, 0.07)",
  padding: "2rem",
  maxWidth: "600px",
  width: "100%",
};

const title = {
  fontSize: "2rem",
  fontWeight: "700",
  color: "#003366",
  marginBottom: "1.5rem",
};

const inputGroup = {
  marginBottom: "1rem",
};

const label = {
  fontSize: "1rem",
  fontWeight: "600",
  color: "#003366",
  marginBottom: "0.5rem",
  display: "block",
};

const input = {
  width: "100%",
  padding: "0.8rem",
  borderRadius: "8px",
  border: "1px solid #ccc",
  marginBottom: "0.5rem",
  fontSize: "1rem",
};

const textarea = {
  width: "100%",
  padding: "0.8rem",
  borderRadius: "8px",
  border: "1px solid #ccc",
  fontSize: "1rem",
  minHeight: "100px",
};

const buttonGroup = {
  textAlign: "center",
};

const submitButton = {
  backgroundColor: "#003366",
  color: "#fff",
  padding: "0.8rem 1.6rem",
  borderRadius: "8px",
  fontWeight: "600",
  border: "none",
  cursor: "pointer",
  fontSize: "1rem",
};

export default NewTicketForm;
