import React, { useState, useEffect } from "react";
import axios from "axios";

const CardSettings = () => {
  const storedUser = localStorage.getItem("user");
  const parsedUser = storedUser ? JSON.parse(storedUser) : null;

  const [userData, setUserData] = useState({
    nom: "",
    prenom: "",
    email: "",
    tel: "",
  });

  const [initialUserData, setInitialUserData] = useState({});
  const [passwordData, setPasswordData] = useState({
    currentPassword: "",
    newPassword: "",
  });

  const [message, setMessage] = useState("");

  useEffect(() => {
    if (parsedUser) {
      setUserData({
        nom: parsedUser.nom || "",
        prenom: parsedUser.prenom || "",
        email: parsedUser.email || "",
        tel: parsedUser.tel || "",
      });
      setInitialUserData(parsedUser);
    }
  }, []);

  const handleChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };

  const handlePasswordChange = (e) => {
    setPasswordData({ ...passwordData, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      const updatedData = {
        ...userData,
        currentPassword: passwordData.currentPassword,
        newPassword: passwordData.newPassword,
      };

      const response = await axios.put(
        `http://localhost:3001/updateuser/${parsedUser._id}`,
        updatedData
      );

      setMessage("Données mises à jour avec succès.");
      localStorage.setItem("user", JSON.stringify(response.data));
      setPasswordData({ currentPassword: "", newPassword: "" });
    } catch (error) {
      console.error("Erreur lors de la mise à jour :", error);
      if (error.response && error.response.data && error.response.data.message) {
        setMessage(error.response.data.message);
      } else {
        setMessage("Erreur lors de la mise à jour des données.");
      }
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial", maxWidth: "400px", margin: "auto" }}>
      <h2>Paramètres du compte</h2>

      <label>Nom:</label>
      <input type="text" name="nom" value={userData.nom} onChange={handleChange} />

      <label>Prénom:</label>
      <input type="text" name="prenom" value={userData.prenom} onChange={handleChange} />

      <label>Email:</label>
      <input type="email" name="email" value={userData.email} onChange={handleChange} />

      <label>Téléphone:</label>
      <input type="text" name="tel" value={userData.tel} onChange={handleChange} />

      <hr />

      <label>Mot de passe actuel:</label>
      <input
        type="password"
        name="currentPassword"
        value={passwordData.currentPassword}
        onChange={handlePasswordChange}
      />

      <label>Nouveau mot de passe:</label>
      <input
        type="password"
        name="newPassword"
        value={passwordData.newPassword}
        onChange={handlePasswordChange}
      />

      <button onClick={handleSave} style={{ marginTop: "10px" }}>
        Enregistrer
      </button>

      {message && <p style={{ color: "red" }}>{message}</p>}
    </div>
  );
};

export default CardSettings;
