import React, { useState } from "react";
import PropTypes from "prop-types";
import { MdDelete, MdEdit } from "react-icons/md";

const styles = `
  .fixed-dialog-container {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(0, 0, 0, 0.5);
    display: flex;
    align-items: flex-start;
    justify-content: center;
    z-index: 50;
    padding-top: 2rem;
  }

  .dialog-content {
    background-color: #f7fafc;
    padding: 2rem;
    border-radius: 0.5rem;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
    width: 100%;
    max-width: 30rem;
  }

  .modal-container {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(0, 0, 0, 0.5);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 50;
  }

  .modal-content {
    background-color: white;
    padding: 2rem;
    border-radius: 0.5rem;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
    width: 100%;
    max-width: 30rem;
  }

  .modal-content input,
  .modal-content select {
    width: 100%;
    padding: 0.75rem;
    margin-bottom: 1rem;
    border: 1px solid #ddd;
    border-radius: 0.25rem;
    transition: all 0.3s;
  }

  .modal-content input:focus,
  .modal-content select:focus {
    border-color: #3182ce;
    outline: none;
    box-shadow: 0 0 5px rgba(0, 122, 255, 0.5);
  }

  .modal-content button {
    padding: 0.75rem 1.5rem;
    border-radius: 0.25rem;
    cursor: pointer;
    transition: background-color 0.3s;
  }

  .modal-content button.cancel {
    background-color: #e2e8f0;
    margin-right: 1rem;
    color: #4a5568;
  }

  .modal-content button.cancel:hover {
    background-color: #cbd5e0;
  }

  .modal-content button.submit {
    background-color: #38a169;
    color: white;
  }

  .modal-content button.submit:hover {
    background-color: #2f855a;
  }

  .error-message {
    color: red;
    font-size: 0.875rem;
    margin-bottom: 1rem;
    font-weight: bold;
  }

  .table-container {
    overflow-x: auto;
    padding: 1.5rem;
    background-color: #f7fafc;
    border-radius: 0.5rem;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
  }

  table {
    width: 100%;
    border-collapse: collapse;
    background-color: #ffffff;
    border-radius: 0.5rem;
  }

  th,
  td {
    padding: 1rem;
    text-align: left;
    border-bottom: 1px solid #e2e8f0;
  }

  th {
    background-color: #edf2f7;
    font-weight: 600;
    color: #4a5568;
  }

  td {
    font-size: 0.9rem;
    color: #2d3748;
  }

  td button {
    padding: 0.5rem;
    border-radius: 0.25rem;
    margin-right: 0.5rem;
    cursor: pointer;
    transition: all 0.3s;
  }

  td button:hover {
    opacity: 0.85;
  }

  .btn-edit {
    background-color: #fbbf24;
    color: white;
  }

  .btn-delete {
    background-color: #e53e3e;
    color: white;
  }

  .btn-add {
    background-color: #3182ce;
    color: white;
    padding: 0.75rem 2rem;
    border-radius: 0.25rem;
    font-weight: bold;
    transition: background-color 0.3s;
    display: flex;
    align-items: center;
    gap: 0.5rem;
  }

  .btn-add:hover {
    background-color: #2b6cb0;
  }

  .search-input {
    padding: 0.75rem 2rem;
    border: 1px solid #ddd;
    border-radius: 0.5rem;
    width: 250px;
  }

  .filter-select {
    padding: 0.75rem;
    border: 1px solid #ddd;
    border-radius: 0.5rem;
    background-color: white;
  }

  .header-tools {
    display: flex;
    gap: 1rem;
    padding: 1.5rem;
    align-items: center;
    background-color: #f7fafc;
    border-bottom: 1px solid #e2e8f0;
  }
`;

export default function CardDepartment({ color = "light" }) {
  const [searchTerm, setSearchTerm] = useState("");
  const [sortOption, setSortOption] = useState("");
  const [departements, setDepartements] = useState([
    { id: 1, nom: "Informatique" },
    { id: 2, nom: "Ressources Humaines" },
    { id: 3, nom: "Finance" },
  ]);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [selectedDepartementId, setSelectedDepartementId] = useState(null);
  const [selectedDepartementName, setSelectedDepartementName] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [newDepartement, setNewDepartement] = useState({ nom: "" });
  const [selectedDepartement, setSelectedDepartement] = useState(null);
  const [errors, setErrors] = useState({});

  const filteredDepartements = departements
    .filter((departement) => {
      if (!isNaN(searchTerm)) {
        return departement.id.toString().includes(searchTerm);
      }
      return departement.nom.toLowerCase().includes(searchTerm.toLowerCase());
    })
    .sort((a, b) => {
      if (!sortOption) return 0;
      if (sortOption === "id-asc") return a.id - b.id;
      if (sortOption === "id-desc") return b.id - a.id;
      if (sortOption === "nom") return a.nom.localeCompare(b.nom);
      return 0;
    });

  const handleAction = (action, id, nom) => {
    switch (action) {
      case "modifier":
        const departementToEdit = departements.find((d) => d.id === id);
        setSelectedDepartement(departementToEdit);
        setIsEditModalOpen(true);
        break;
      case "supprimer":
        setSelectedDepartementId(id);
        setSelectedDepartementName(nom);
        setIsDialogOpen(true);
        break;
      default:
        break;
    }
  };

  const handleConfirmDelete = () => {
    setDepartements(departements.filter((d) => d.id !== selectedDepartementId));
    setIsDialogOpen(false);
    setSelectedDepartementName("");
  };

  const handleAddDepartement = () => setIsModalOpen(true);

  const validateForm = () => {
    const newErrors = {};
    if (!newDepartement.nom) newErrors.nom = "Le nom est obligatoire";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleModalSubmit = () => {
    if (!validateForm()) return;

    const newEntry = {
      id: departements.length + 1,
      nom: newDepartement.nom,
    };
    setDepartements([...departements, newEntry]);
    setIsModalOpen(false);
    setNewDepartement({ nom: "" });
    setErrors({});
  };

  const handleEditSubmit = () => {
    if (!validateForm()) return;

    const updated = departements.map((d) =>
      d.id === selectedDepartement.id ? { ...selectedDepartement } : d
    );
    setDepartements(updated);
    setIsEditModalOpen(false);
  };

  return (
    <>
      <style>{styles}</style>

      <div className={`relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded ${
        color === "light" ? "bg-white" : "bg-lightBlue-900 text-white"
      }`}>
        {/* Confirmation Dialog */}
        {isDialogOpen && (
          <div className="fixed-dialog-container">
            <div className="dialog-content">
              <p className="text-lg text-gray-700 mb-4">
                Êtes-vous sûr de vouloir supprimer "{selectedDepartementName}" ?
              </p>
              <div className="flex justify-end space-x-4">
                <button
                  onClick={() => setIsDialogOpen(false)}
                  className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                >
                  Annuler
                </button>
                <button
                  onClick={handleConfirmDelete}
                  className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
                >
                  Supprimer
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Header avec outils */}
        <div className="header-tools">
          <div className="flex items-center gap-4">
            <div className="relative">
              <input
                type="text"
                placeholder="Rechercher par nom ou ID"
                className="search-input"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              <svg
                className="absolute left-3 top-3 h-5 w-5 text-gray-400"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
              </svg>
            </div>
            
            <select
              className="filter-select"
              value={sortOption}
              onChange={(e) => setSortOption(e.target.value)}
            >
              <option value="">Filtrer par</option>
              <option value="id-asc">ID Croissant</option>
              <option value="id-desc">ID Décroissant</option>
              <option value="nom">Nom</option>
            </select>
          </div>

          <button onClick={handleAddDepartement} className="btn-add">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
            </svg>
            Ajouter
          </button>
        </div>

        {/* Tableau */}
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Nom</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {filteredDepartements.map((departement) => (
                <tr key={departement.id}>
                  <td>{departement.id}</td>
                  <td>{departement.nom}</td>
                  <td>
                    <button
                      className="btn-edit"
                      onClick={() => handleAction("modifier", departement.id)}
                    >
                      <MdEdit />
                    </button>
                    <button
                      className="btn-delete"
                      onClick={() => handleAction("supprimer", departement.id, departement.nom)}
                    >
                      <MdDelete />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Modales */}
        {isModalOpen && (
          <div className="modal-container">
            <div className="modal-content">
              <h2 className="text-lg font-semibold mb-4">Ajouter un département</h2>
              {errors.nom && <div className="error-message">{errors.nom}</div>}
              <input
                placeholder="Nom du département"
                value={newDepartement.nom}
                onChange={(e) => setNewDepartement({...newDepartement, nom: e.target.value})}
              />
              <div className="flex justify-end">
                <button className="cancel" onClick={() => setIsModalOpen(false)}>
                  Annuler
                </button>
                <button className="submit" onClick={handleModalSubmit}>
                  Ajouter
                </button>
              </div>
            </div>
          </div>
        )}

        {isEditModalOpen && selectedDepartement && (
          <div className="modal-container">
            <div className="modal-content">
              <h2 className="text-lg font-semibold mb-4">Modifier le département</h2>
              <input
                placeholder="Nom du département"
                value={selectedDepartement.nom}
                onChange={(e) => setSelectedDepartement({...selectedDepartement, nom: e.target.value})}
              />
              <div className="flex justify-end">
                <button className="cancel" onClick={() => setIsEditModalOpen(false)}>
                  Annuler
                </button>
                <button className="submit" onClick={handleEditSubmit}>
                  Enregistrer
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </>
  );
}

CardDepartment.propTypes = {
  color: PropTypes.string,
};
