import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { FaGoogle } from "react-icons/fa";

export default function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [forgotEmail, setForgotEmail] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [showForgotForm, setShowForgotForm] = useState(false);
  const [forgotSuccess, setForgotSuccess] = useState("");

  const history = useHistory();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setEmailError("");
    setPasswordError("");

    try {
      const loginResponse = await fetch("/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
        credentials: "include",
      });

      const loginData = await loginResponse.json();

      if (!loginResponse.ok) {
        throw new Error(loginData.message || "Échec de la connexion");
      }

      const { token, user } = loginData;

      localStorage.setItem("jwt_token", token);
      localStorage.setItem("user", JSON.stringify(user));

      switch (user.role) {
        case "admin":
          history.push("/admin/dashboard");
          break;
        case "technicien":
        default:
          history.push("/homepage");
      }
    } catch (error) {
      setError(error.message);
      if (error.message.toLowerCase().includes("email")) {
        setEmailError("Veuillez vérifier votre adresse e-mail");
      } else if (error.message.toLowerCase().includes("password")) {
        setPasswordError("Veuillez vérifier votre mot de passe");
      } else {
        setEmailError("Vérifiez votre email");
        setPasswordError("Vérifiez votre mot de passe");
      }
    } finally {
      setLoading(false);
    }
  };

  const handleForgotPassword = async (e) => {
    e.preventDefault();
    setForgotSuccess("");
    setError("");
    try {
      const response = await fetch("/forgot-password", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email: forgotEmail }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || "Erreur lors de l'envoi");
      }

      setForgotSuccess("Lien de réinitialisation envoyé avec succès.");
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div style={{
      display: "flex",
      height: "100vh",
      alignItems: "center",
      justifyContent: "center",
      backgroundImage: "url('https://img.freepik.com/premium-vector/abstract-geometric-white-gray-color-background-vector_665257-159.jpg?semt=ais_hybrid')",
      backgroundSize: "cover",
      backgroundPosition: "center",
      padding: "16px"
    }}>
      <div style={{
        display: "grid",
        gridTemplateColumns: "1fr 1fr",
        boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
        borderRadius: "12px",
        overflow: "hidden",
        maxWidth: "1000px",
        width: "100%",
        position: "relative"
      }}>

        <img
          src="https://upload.wikimedia.org/wikipedia/commons/0/06/Logo_STB.png"
          alt="STB Logo"
          style={{
            position: "absolute",
            top: "12px",
            left: "12px",
            width: "120px",
            height: "auto"
          }}
        />

        <form onSubmit={showForgotForm ? handleForgotPassword : handleSubmit} style={{
          padding: "32px",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          background: "white"
        }}>
          <h2 style={{ fontSize: "24px", fontWeight: "bold", color: "#1f2937", textAlign: "center" }}>
            {showForgotForm ? "Mot de passe oublié" : "Log In"}
          </h2>

          {error && <div style={{ color: "red", marginBottom: "12px" }}>{error}</div>}
          {forgotSuccess && <div style={{ color: "green", marginBottom: "12px" }}>{forgotSuccess}</div>}

          {showForgotForm ? (
            <>
              <div style={{ marginTop: "16px" }}>
                <label style={{ display: "block", fontSize: "14px", fontWeight: "500", color: "#4b5563" }}>Email</label>
                <input
                  type="email"
                  placeholder="Entrez votre email"
                  value={forgotEmail}
                  onChange={(e) => setForgotEmail(e.target.value)}
                  required
                  style={{
                    width: "100%",
                    padding: "8px",
                    border: "1px solid #d1d5db",
                    borderRadius: "6px",
                    marginTop: "4px"
                  }}
                />
              </div>
              <button
                type="submit"
                style={{
                  width: "100%",
                  background: "#3b82f6",
                  color: "white",
                  padding: "10px",
                  marginTop: "16px",
                  borderRadius: "6px",
                  fontWeight: "bold",
                  cursor: "pointer"
                }}
              >
                Envoyer
              </button>
              <p style={{ textAlign: "center", marginTop: "16px", fontSize: "14px" }}>
                <a
                  href="#"
                  onClick={(e) => { e.preventDefault(); setShowForgotForm(false); }}
                  style={{ color: "#3b82f6", textDecoration: "none" }}
                >
                  Retour à la connexion
                </a>
              </p>
            </>
          ) : (
            <>
              <div style={{ marginTop: "16px" }}>
                <label style={{ display: "block", fontSize: "14px", fontWeight: "500", color: "#4b5563" }}>Email</label>
                <input
                  type="email"
                  placeholder="Enter your email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  style={{
                    width: "100%",
                    padding: "8px",
                    border: "1px solid #d1d5db",
                    borderRadius: "6px",
                    marginTop: "4px"
                  }}
                />
                {emailError && <p style={{ color: "red", fontSize: "12px", marginTop: "4px" }}>{emailError}</p>}
              </div>

              <div style={{ marginTop: "12px" }}>
                <label style={{ display: "block", fontSize: "14px", fontWeight: "500", color: "#4b5563" }}>Password</label>
                <input
                  type="password"
                  placeholder="Enter your password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  style={{
                    width: "100%",
                    padding: "8px",
                    border: "1px solid #d1d5db",
                    borderRadius: "6px",
                    marginTop: "4px"
                  }}
                />
                {passwordError && <p style={{ color: "red", fontSize: "12px", marginTop: "4px" }}>{passwordError}</p>}
              </div>

              <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginTop: "12px" }}>
                <div style={{ display: "flex", alignItems: "center" }}>
                  <input type="checkbox" id="remember" />
                  <label htmlFor="remember" style={{ marginLeft: "6px", fontSize: "14px", color: "#4b5563" }}>Remember me</label>
                </div>
                <a
                  href="#"
                  onClick={(e) => { e.preventDefault(); setShowForgotForm(true); }}
                  style={{ fontSize: "14px", color: "#3b82f6", textDecoration: "none" }}
                >
                  Forgot password?
                </a>
              </div>

              <button
                type="submit"
                disabled={loading}
                style={{
                  width: "100%",
                  background: loading ? "#9ca3af" : "#3b82f6",
                  color: "white",
                  padding: "10px",
                  marginTop: "16px",
                  borderRadius: "6px",
                  fontWeight: "bold",
                  cursor: loading ? "not-allowed" : "pointer"
                }}
              >
                {loading ? "Chargement..." : "LOG IN"}
              </button>

              <div style={{ display: "flex", alignItems: "center", margin: "16px 0" }}>
                <div style={{ flex: "1", height: "1px", background: "#d1d5db" }}></div>
                <span style={{ margin: "0 8px", fontSize: "14px", color: "#6b7280" }}>OR</span>
                <div style={{ flex: "1", height: "1px", background: "#d1d5db" }}></div>
              </div>

              <button
                type="button"
                style={{
                  width: "100%",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  gap: "8px",
                  border: "1px solid #d1d5db",
                  padding: "10px",
                  borderRadius: "6px",
                  fontWeight: "bold",
                  cursor: "pointer",
                  background: "white"
                }}
              >
                <FaGoogle style={{ color: "#3b82f6" }} /> Continue with Google
              </button>

              <p style={{ fontSize: "14px", textAlign: "center", color: "#6b7280", marginTop: "12px" }}>
                Don't have an account? <a href="Register" style={{ color: "#3b82f6", textDecoration: "none" }}>Sign Up</a>
              </p>
            </>
          )}
        </form>

        <div style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          backgroundImage: "url('https://blogger.googleusercontent.com/img/b/R29vZ2xl/AVvXsEjrxCZS8RUx52KhkLFeYR19uXX3GnbF9-sH75brySGs70DJ1EINGwkFnr6K5LqopCqGHTVK79x_gscCHE_cR1wmpIADhJXkTZIJhDz_VtYuxwekNibQUVl6VKNuq3uOlTsfnFW2F_ZVXsm2m7DU2IGJREbwsM16cR45D3-4iI5AwRArtjCnR713SvfIJpg/s1934/stb%20%20thebanker.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          color: "white",
          padding: "32px",
          textAlign: "center"
        }}>
        </div>
      </div>
    </div>
  );
}
