import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faPen } from "@fortawesome/free-solid-svg-icons";
import { getAllUsers } from "services/ApiUser";

export default function CardTable({ color }) {
  const [users, setUsers] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newUser, setNewUser] = useState({
    nom: "",
    prenom: "",
    email: "",
    id: "",
    role: "",
    tel: "",
    motdepasse: "",
  });

  const [isEditing, setIsEditing] = useState(false);
  const [editIndex, setEditIndex] = useState(null);

  const getUsers = async () => {
    try {
      const res = await getAllUsers();
      if (res.data?.userList) {
        setUsers(res.data.userList);
      }
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getUsers();
  }, []);

  const handleInputChange = (e) => {
    setNewUser({ ...newUser, [e.target.name]: e.target.value });
  };

  const handleUpdateUser = (e) => {
    e.preventDefault();
    if (isEditing && editIndex !== null) {
      const updatedUsers = [...users];
      updatedUsers[editIndex] = newUser;
      setUsers(updatedUsers);
    }

    setNewUser({
      nom: "",
      prenom: "",
      email: "",
      id: "",
      role: "",
      tel: "",
      motdepasse: "",
    });
    setIsEditing(false);
    setEditIndex(null);
    setShowForm(false);
  };

  const handleDeleteUser = (index) => {
    const confirmDelete = window.confirm("Êtes-vous sûr de vouloir supprimer cet utilisateur ?");
    if (confirmDelete) {
      const updatedUsers = users.filter((_, i) => i !== index);
      setUsers(updatedUsers);
    }
  };

  const handleEditUser = (index) => {
    setNewUser({ ...users[index], motdepasse: "" }); // champ à vide
    setIsEditing(true);
    setEditIndex(index);
    setShowForm(true);
  };

  return (
    <div
      className={
        "relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg " +
        (color === "light" ? "bg-white" : "bg-lightBlue-900 text-white")
      }
    >
      <div className="flex justify-between items-center px-6 py-4 border-b border-gray-200">
        <h3 className={"font-bold text-xl " + (color === "light" ? "text-blueGray-700" : "text-white")}>
          Liste des utilisateurs
        </h3>
      </div>

      <div className="overflow-x-auto p-4">
        <table className="w-full text-sm text-left text-white">
          <thead className="text-xs uppercase bg-lightBlue-800 text-blueGray-100">
            <tr>
              <th className="px-6 py-3">Nom</th>
              <th className="px-6 py-3">Prénom</th>
              <th className="px-6 py-3">Email</th>
              <th className="px-6 py-3">ID</th>
              <th className="px-6 py-3">Role</th>
              <th className="px-6 py-3">Téléphone</th>
              <th className="px-6 py-3 text-right">Actions</th>
            </tr>
          </thead>
          <tbody className="bg-lightBlue-900 divide-y divide-blueGray-700">
            {users.map((user, index) => (
              <tr key={index} className="hover:bg-lightBlue-800">
                <td className="px-6 py-4">{user.nom}</td>
                <td className="px-6 py-4">{user.prenom}</td>
                <td className="px-6 py-4">{user.email}</td>
                <td className="px-6 py-4">{user.id}</td>
                <td className="px-6 py-4">{user.role}</td>
                <td className="px-6 py-4">{user.tel}</td>
                <td className="px-6 py-4 text-right">
                  <button
                    onClick={() => handleDeleteUser(index)}
                    className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded mr-2 text-xs shadow"
                  >
                    <FontAwesomeIcon icon={faTrash} />
                  </button>
                  <button
                    onClick={() => handleEditUser(index)}
                    className="bg-lightBlue-500 hover:bg-lightBlue-600 text-white px-3 py-1 rounded text-xs shadow"
                  >
                    <FontAwesomeIcon icon={faPen} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {showForm && (
          <form
            onSubmit={handleUpdateUser}
            className="mt-6 bg-lightBlue-900 text-white p-6 rounded shadow-md"
          >
            <h4 className="text-lg font-semibold mb-4 text-white">Modifier l'utilisateur</h4>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-4">
              {["nom", "prenom", "email", "id", "role", "tel"].map((field) => (
                <div key={field}>
                  <label className="block text-sm mb-1" htmlFor={field}>
                    {field.charAt(0).toUpperCase() + field.slice(1)}
                  </label>
                  <input
                    id={field}
                    type={field === "email" ? "email" : "text"}
                    name={field}
                    placeholder={`Entrer ${field}`}
                    value={newUser[field]}
                    onChange={handleInputChange}
                    readOnly={field === "id"}
                    className={`w-full p-2 ${
                      field === "id" ? "bg-lightBlue-900 cursor-not-allowed" : "bg-lightBlue-800"
                    } border border-lightBlue-700 text-white placeholder-blueGray-200 rounded`}
                    required
                  />
                </div>
              ))}
              <div>
                <label className="block text-sm mb-1" htmlFor="motdepasse">Mot de passe</label>
                <input
                  id="motdepasse"
                  type="password"
                  name="motdepasse"
                  placeholder="Nouveau mot de passe"
                  value={newUser.motdepasse}
                  onChange={handleInputChange}
                  className="w-full p-2 bg-lightBlue-800 border border-lightBlue-700 text-white placeholder-blueGray-200 rounded"
                />
              </div>
            </div>

            <div className="flex justify-end">
              <button
                type="submit"
                className="bg-emerald-500 hover:bg-emerald-600 text-white text-sm font-semibold px-6 py-2 rounded shadow"
              >
                Modifier
              </button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
}
