import React, { useState } from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";

function MyTickets() {
  const [selectedTicket, setSelectedTicket] = useState(null);

  const tickets = [
    {
      id: 1,
      sujet: "Connexion VPN bloqu√©e",
      departement: "IT",
      statut: "Ouvert",
      date: "2025-04-04",
      description: "Je n'arrive plus √† me connecter au VPN depuis ce matin.",
    },
    {
      id: 2,
      sujet: "Erreur sur fiche de paie",
      departement: "RH",
      statut: "En cours",
      date: "2025-04-01",
      description: "Il y a une erreur dans le montant net affich√©.",
    },
    {
      id: 3,
      sujet: "Probl√®me d'acc√®s √† l'intranet",
      departement: "IT",
      statut: "R√©solu",
      date: "2025-03-29",
      description: "Le site intranet √©tait inaccessible depuis plusieurs jours.",
    },
  ];

  return (
    <>
      <IndexNavbar />

      <div style={pageStyle}>
        <div style={cardStyle}>
          <h1 style={titleStyle}>Mes Tickets</h1>

          <div style={ticketListStyle}>
            {tickets.map((ticket) => (
              <div key={ticket.id} style={ticketStyle}>
                <div style={ticketHeaderStyle}>
                  <h3 style={{ margin: 0, color: "#0d47a1" }}>{ticket.sujet}</h3>
                  <span style={statusStyle(ticket.statut)}>{ticket.statut}</span>
                </div>
                <p style={metaStyle}>
                  D√©partement : {ticket.departement} | Date : {ticket.date}
                </p>
                <button
                  style={buttonStyle}
                  onClick={() => setSelectedTicket(ticket)}
                >
                  Voir d√©tails
                </button>
              </div>
            ))}
          </div>

          {selectedTicket && (
            <div style={detailStyle}>
              <h3>D√©tails du ticket</h3>
              <p><strong>Sujet :</strong> {selectedTicket.sujet}</p>
              <p><strong>D√©partement :</strong> {selectedTicket.departement}</p>
              <p><strong>Statut :</strong> {selectedTicket.statut}</p>
              <p><strong>Date :</strong> {selectedTicket.date}</p>
              <p><strong>Description :</strong> {selectedTicket.description}</p>
              <button onClick={() => setSelectedTicket(null)} style={closeButtonStyle}>
                Fermer
              </button>
            </div>
          )}
        </div>
      </div>

      <Footerr />
    </>
  );
}

// üé® Style √©pur√© et professionnel
const pageStyle = {
  backgroundColor: "#f4f6f9",
  minHeight: "100vh",
  padding: "4rem 1rem",
  display: "flex",
  justifyContent: "center",
  alignItems: "start",
};

const cardStyle = {
  backgroundColor: "#ffffff",
  borderRadius: "12px",
  padding: "2rem",
  width: "100%",
  maxWidth: "900px",
  boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)",
};

const titleStyle = {
  fontSize: "2rem",
  fontWeight: "700",
  color: "#003366",
  marginBottom: "2rem",
  textAlign: "center",
};

const ticketListStyle = {
  display: "flex",
  flexDirection: "column",
  gap: "1.5rem",
};

const ticketStyle = {
  backgroundColor: "#f9f9f9",
  borderRadius: "8px",
  padding: "1.2rem 1.5rem",
  borderLeft: "4px solid #0d47a1",
};

const ticketHeaderStyle = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
};

const metaStyle = {
  fontSize: "0.9rem",
  color: "#666",
  marginTop: "0.5rem",
};

const statusStyle = (statut) => {
  let bgColor;
  switch (statut) {
    case "Ouvert":
      bgColor = "#ff9800";
      break;
    case "En cours":
      bgColor = "#2196f3";
      break;
    case "R√©solu":
      bgColor = "#4caf50";
      break;
    default:
      bgColor = "#aaa";
  }
  return {
    backgroundColor: bgColor,
    color: "#fff",
    borderRadius: "6px",
    padding: "0.3rem 0.7rem",
    fontSize: "0.8rem",
    fontWeight: "600",
  };
};

const buttonStyle = {
  marginTop: "1rem",
  backgroundColor: "#003366",
  color: "#fff",
  border: "none",
  padding: "0.6rem 1rem",
  borderRadius: "6px",
  cursor: "pointer",
  fontWeight: "600",
};

const detailStyle = {
  marginTop: "2rem",
  backgroundColor: "#eef2f5",
  padding: "1.5rem",
  borderRadius: "8px",
};

const closeButtonStyle = {
  marginTop: "1rem",
  backgroundColor: "#d32f2f",
  color: "#fff",
  border: "none",
  padding: "0.5rem 1rem",
  borderRadius: "6px",
  cursor: "pointer",
};

export default MyTickets;
