import React, { useState, useEffect } from "react";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Footerr from "components/Footers/Footerr";

function MyTickets() {
  const [tickets, setTickets] = useState([]);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [searchId, setSearchId] = useState("");
  const [modifiedTicket, setModifiedTicket] = useState(null);  // Ajouté pour les modifications

  const getShortCode = (id) => {
    const strId = String(id);
    if (!strId || strId.length < 10) return "XXXX";
    return strId.substring(6, 10); // index 6 inclus, 10 exclu
  };

  useEffect(() => {
    document.body.style.margin = "0";
    document.body.style.minHeight = "100vh";
    document.body.style.display = "flex";
    document.body.style.flexDirection = "column";

    const fetchTickets = async () => {
      try {
        const response = await fetch('/alltickets');
        const data = await response.json();
        setTickets(data);
      } catch (error) {
        console.error("Erreur lors de la récupération des tickets:", error);
      }
    };

    fetchTickets();
  }, []);

  const status = (statut) => ({
    padding: "0.3rem 0.8rem",
    borderRadius: "4px",
    fontSize: "0.85rem",
    backgroundColor: {
      "Ouvert": "#ffebee",
      "En cours": "#e3f2fd",
      "Résolu": "#e8f5e9",
    }[statut],
    color: {
      "Ouvert": "#c62828",
      "En cours": "#1565c0",
      "Résolu": "#2e7d32",
    }[statut],
    fontWeight: "bold",
  });

  const filteredTickets = tickets.filter(ticket =>
    searchId === "" || ticket._id?.toString().includes(searchId)
  );

  const handleModifyClick = () => {
    setModifiedTicket({
      ...selectedTicket,
      sujet: selectedTicket.sujet, // initialiser avec la valeur actuelle
      description: selectedTicket.description, // initialiser avec la valeur actuelle
    });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setModifiedTicket((prev) => ({ ...prev, [name]: value }));
  };

  const handleSaveChanges = async () => {
    try {
      const response = await fetch(`/tickets/${modifiedTicket._id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          sujet: modifiedTicket.sujet,
          description: modifiedTicket.description,
        }),
      });

      if (response.ok) {
        const updatedTicket = await response.json();
        setTickets((prevTickets) =>
          prevTickets.map((ticket) =>
            ticket._id === updatedTicket._id ? updatedTicket : ticket
          )
        );
        setSelectedTicket(updatedTicket);
        setModifiedTicket(null); // Reset modification state
      } else {
        console.error("Erreur lors de la mise à jour du ticket");
      }
    } catch (error) {
      console.error("Erreur lors de la mise à jour du ticket:", error);
    }
  };

  return (
    <>
      <IndexNavbar />
      <div style={container}>
        <div style={contentWrapper}>
          <img
            src="/images/support-banner.jpg"
            alt="Bannière Support"
            style={bannerImage}
          />
          <h1 style={title}>Espace Support Client</h1>
          <p style={subtitle}>Consultez vos tickets et suivez leur avancement</p>

          {/* Champ de recherche */}
          <div style={{ marginBottom: "1rem", textAlign: "center" }}>
            <input
              type="text"
              placeholder="Rechercher par numéro de ticket"
              value={searchId}
              onChange={(e) => setSearchId(e.target.value)}
              style={{
                padding: "0.5rem",
                width: "60%",
                maxWidth: "300px",
                borderRadius: "6px",
                border: "1px solid #ccc",
                fontSize: "1rem"
              }}
            />
          </div>

          <div style={ticketGrid}>
            <div style={ticketList}>
              {filteredTickets.map((ticket) => {
                return (
                  <div
                    key={ticket._id}
                    style={ticketItem}
                    onClick={() => setSelectedTicket(ticket)}
                  >
                    <div style={ticketHeader}>
                      <span style={ticketId}>
                        {ticket._id ? `#${getShortCode(ticket._id)}` : "ID non disponible"}
                      </span>
                      <span style={status(ticket.statut)}>{ticket.statut}</span>
                    </div>
                    <h3 style={ticketTitle}>{ticket.sujet}</h3>
                    <p style={ticketDate}>{ticket.date?.slice(0, 10)}</p>
                  </div>
                );
              })}
              {filteredTickets.length === 0 && (
                <p style={{ textAlign: "center", color: "#999" }}>Aucun ticket trouvé.</p>
              )}
            </div>

            {selectedTicket && (
              <div style={detailsPanel}>
                <div style={detailsHeader}>
                  <h2 style={detailsTitle}>Détails du ticket</h2>
                  <button style={closeButton} onClick={() => setSelectedTicket(null)}>×</button>
                </div>
                <div style={detailsContent}>
                  <div style={detailRow}>
                    <span style={detailLabel}>Numéro :</span>
                    <span>#{getShortCode(selectedTicket._id)}</span>
                  </div>
                  <div style={detailRow}>
                    <span style={detailLabel}>Statut :</span>
                    <span>{selectedTicket.statut}</span>
                  </div>
                  <div style={detailRow}>
                    <span style={detailLabel}>Date :</span>
                    <span>{selectedTicket.date?.slice(0, 10)}</span>
                  </div>

                  {/* Description du ticket */}
                  <div style={detailDescription}>
                    <p style={detailLabel}>Description :</p>
                    <p>{selectedTicket.description}</p>
                  </div>

                  {/* Bouton Modifier */}
                  {!modifiedTicket ? (
                    <button style={modifyButton} onClick={handleModifyClick}>Modifier</button>
                  ) : (
                    <div>
                      <div style={detailRow}>
                        <label style={detailLabel}>Sujet :</label>
                        <input
                          type="text"
                          name="sujet"
                          value={modifiedTicket.sujet}
                          onChange={handleInputChange}
                          style={inputStyle}
                        />
                      </div>
                      <div style={detailRow}>
                        <label style={detailLabel}>Description :</label>
                        <textarea
                          name="description"
                          value={modifiedTicket.description}
                          onChange={handleInputChange}
                          style={inputStyle}
                        />
                      </div>
                      <button style={saveButton} onClick={handleSaveChanges}>Enregistrer</button>
                    </div>
                  )}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
      <Footerr />
    </>
  );
}

