import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faPen } from "@fortawesome/free-solid-svg-icons";
import axios from "axios";

export default function CardTable() {
  const [users, setUsers] = useState([
    {
      nom: "Toumi",
      prenom: "Med Rayen",
      email: "toumirayen130@gmail.com",
      id: "05",
    },
  ]);
  const [showForm, setShowForm] = useState(false);
  const [newUser, setNewUser] = useState({
    nom: "",
    prenom: "",
    email: "",
    id: "",
  });

  const handleInputChange = (e) => {
    setNewUser({ ...newUser, [e.target.name]: e.target.value });
  };

  const handleAddUser = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:3000/api/users", newUser);
      if (res.status === 200 || res.status === 201) {
        setUsers([...users, newUser]);
        setNewUser({ nom: "", prenom: "", email: "", id: "" });
        setShowForm(false);
        alert("✅ Utilisateur ajouté avec succès !");
      }
    } catch (err) {
      console.error(err);
      alert("❌ Une erreur s’est produite");
    }
  };

  return (
    <div className="bg-[#0d3b66] text-white p-6 rounded-lg shadow-md">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-xl font-bold">Liste des utilisateurs</h2>
        <button
          onClick={() => setShowForm(!showForm)}
          className="bg-green-500 hover:bg-green-600 text-white font-semibold px-4 py-2 rounded"
        >
          {showForm ? "Annuler" : "Ajouter"}
        </button>
      </div>

      <table className="w-full text-left mb-6 bg-white text-black rounded overflow-hidden">
        <thead className="bg-[#0b2e4a] text-white">
          <tr>
            <th className="px-4 py-2">Nom</th>
            <th className="px-4 py-2">Prénom</th>
            <th className="px-4 py-2">Email</th>
            <th className="px-4 py-2">ID</th>
            <th className="px-4 py-2 text-right">Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user, i) => (
            <tr key={i} className="border-b">
              <td className="px-4 py-2">{user.nom}</td>
              <td className="px-4 py-2">{user.prenom}</td>
              <td className="px-4 py-2">{user.email}</td>
              <td className="px-4 py-2">{user.id}</td>
              <td className="px-4 py-2 text-right space-x-2">
                <button className="bg-red-500 hover:bg-red-600 text-white p-1 rounded">
                  <FontAwesomeIcon icon={faTrash} />
                </button>
                <button className="bg-blue-500 hover:bg-blue-600 text-white p-1 rounded">
                  <FontAwesomeIcon icon={faPen} />
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {showForm && (
        <form onSubmit={handleAddUser} className="bg-[#0b2e4a] p-4 rounded-lg">
          <h3 className="text-lg font-semibold mb-4">Ajouter un utilisateur</h3>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-4">
            <input
              type="text"
              name="nom"
              placeholder="Nom"
              value={newUser.nom}
              onChange={handleInputChange}
              className="p-2 rounded bg-white text-black placeholder-gray-500"
              required
            />
            <input
              type="text"
              name="prenom"
              placeholder="Prénom"
              value={newUser.prenom}
              onChange={handleInputChange}
              className="p-2 rounded bg-white text-black placeholder-gray-500"
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={newUser.email}
              onChange={handleInputChange}
              className="p-2 rounded bg-white text-black placeholder-gray-500"
              required
            />
            <input
              type="text"
              name="id"
              placeholder="ID"
              value={newUser.id}
              onChange={handleInputChange}
              className="p-2 rounded bg-white text-black placeholder-gray-500"
              required
            />
          </div>
          <button
            type="submit"
            className="bg-emerald-500 hover:bg-emerald-600 text-white font-semibold px-4 py-2 rounded"
          >
            Ajouter
          </button>
        </form>
      )}
    </div>
  );
}
