import React , {useEffect, useState} from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faPen } from '@fortawesome/free-solid-svg-icons';
import { getAllUsers } from "services/ApiUser";
import { useNavigate } from "react-router-dom"; // 🚨 Assure-toi que React Router est installé

export default function CardTable({ color }) {
  const [users, setUsers] = useState([]);
  const navigate = useNavigate(); // 🔁 hook de navigation

  const getUsers = async () => {
    try {
      await getAllUsers().then((res) => {
        setUsers(res.data.userList);
      });
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getUsers();
  }, []);

  return (
    <>
      <div
        className={
          "relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded " +
          (color === "light" ? "bg-white" : "bg-lightBlue-900 text-white")
        }
      >
        <div className="rounded-t mb-0 px-4 py-3 border-0">
          <div className="flex flex-wrap items-center">
            <div className="relative w-full px-4 max-w-full flex-grow flex-1 flex justify-between items-center">
              <h3
                className={
                  "font-semibold text-lg " +
                  (color === "light" ? "text-blueGray-700" : "text-white")
                }
              >
                Users
              </h3>
              <button
                className="bg-emerald-500 text-white active:bg-emerald-600 text-xs font-bold uppercase px-4 py-2 rounded shadow hover:shadow-md outline-none focus:outline-none ease-linear transition-all duration-150"
                type="button"
                onClick={() => navigate("/add-user")} // 🧭 redirection vers la page de création
              >
                Add User
              </button>
            </div>
          </div>
        </div>
        <div className="block w-full overflow-x-auto">
          <table className="items-center w-full bg-transparent border-collapse">
            <thead>
              <tr>
                <th className="px-6 py-3 text-xs font-semibold text-left uppercase border border-solid whitespace-nowrap">
                  Nom
                </th>
                <th className="px-6 py-3 text-xs font-semibold text-left uppercase border border-solid whitespace-nowrap">
                  Prenom
                </th>
                <th className="px-6 py-3 text-xs font-semibold text-left uppercase border border-solid whitespace-nowrap">
                  Email
                </th>
                <th className="px-6 py-3 text-xs font-semibold text-left uppercase border border-solid whitespace-nowrap">
                  ID
                </th>
                <th className="px-6 py-3 text-xs font-semibold text-left uppercase border border-solid whitespace-nowrap"></th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td className="px-6 py-4 text-xs border-t">Toumi</td>
                <td className="px-6 py-4 text-xs border-t">Med Rayen</td>
                <td className="px-6 py-4 text-xs border-t">toumirayen130@gmail.com</td>
                <td className="px-6 py-4 text-xs border-t"><strong>05</strong></td>
                <td className="px-6 py-4 text-xs border-t text-right">
                  <button className="bg-red-500 text-white px-4 py-2 rounded shadow hover:shadow-lg text-xs font-bold uppercase ml-3">
                    <FontAwesomeIcon icon={faTrash} />
                  </button>
                  <button className="bg-lightBlue-500 text-white px-4 py-2 rounded shadow hover:shadow-lg text-xs font-bold uppercase ml-3">
                    <FontAwesomeIcon icon={faPen} />
                  </button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
}

CardTable.defaultProps = {
  color: "light",
};

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
